import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

const groupStyles = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
};
const groupBadgeStyles = {
    backgroundColor: '#EBECF0',
    borderRadius: '2em',
    color: '#172B4D',
    display: 'inline-block',
    fontSize: 12,
    fontWeight: 'normal',
    lineHeight: '1',
    minWidth: 1,
    padding: '0.16666666666667em 0.5em',
    textAlign: 'center',
};

const formatGroupLabel = data => (
    <div style={groupStyles}>
        <span>{data.label}</span>
        <span style={groupBadgeStyles}>{data.options.length}</span>
    </div>
);

class InscripCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listTypeApp: [{ value: 1, label: 'Eléve deja inscrit a une formation' }, { value: 2, label: 'Nouveau éleve' }],
            selectedTypeApp: 2,
            listApprenant: [],
            selectedApprenant: '',
            listConnaissance: [],
            numInscripCycle: '',
            numCarteAdh: '',
            listTypeFormat: [{ value: 1, label: 'Societé' }, { value: 2, label: 'Particulier' }],
            selectedTypeFiche: 2,
            nbrPersonne: 0,
            matFisc: '',
            nbrPersTenu: 0,
            societe: '',
            responsable: '',
            mobileResp: '',
            age: '',
            lieuResid: [],
            seletedLieuResid: '',
            listNivBase: [],
            selectedNivBase: '',
            facebook: '',
            fonction: '',
            selectedTypeFormat: 0,
            motivation: '',
            numIdentite: '',
            prenom: '',
            nom: '',
            disabled: false,
            connaissChoisi: [],
            listCycle: [],
            selectedCycle: [],
            email: '',
            tel: '',
            fax: '',
            photoVerso: null,
            nbrHeure: 0,
            blockReglement: false,
            carteFid: '',
            tenu: '',
            propCycle: '',
            reglInsCycle: [],
            societeCoch: false,
            tva: '',
            numCarteFid: '',
            mntCarte: '',
            mntTenu: '',
            nomCycle: '',
            dateDCycle: '',
            dateFCycle: '',
            horaire: '',
            mntTotalPayer: 0,
            listModPay: [],
            tabModPayFormat: [{ modPay: '', mntModPay: 0, numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            tabModPayCycle: [{ modPay: '', mntModPay: 0, numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }], indexLignASupprimer: '',
            modalFormat: false,
            modalCycle: false,
            datePaiement: new Date(),
            mntCycleSansTva: '',
            selectedPayCycle: 2,
            arrayRegl: [],
            reglEleve: [],
            mntFraisTotalPayer: 0,
            mntIncripTotalPayer: 0,
            selectedBolPayFormat: 2,
            inforEleve: '',
            fraisReglEleve: [],
            modPayform: '',
            idInscriptionCycle: '',
            fraisCycle: [],
            mntFraisCycleTotalPayer: 0,
            elvId: '',
            idInsFormation: '',
            modePaiementFormation: '',
            disabledFraisFor: false,
            disabledRegFor: false,
            disabledFraisCycle: false,
            disabledRegCycle: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumInscripCycle.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        numInscripCycle: code_html
                    })
                }
            });
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumAdheCycle.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        numCarteAdh: code_html
                    })
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectResid.php').then(res => {
                this.setState({
                    lieuResid: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
                console.log(res)
                this.setState({
                    listNivBase: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectConnaissance.php').then(res => {
                console.log(res)
                this.setState({
                    listConnaissance: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    addTypeApp = (selectedTypeApp) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (selectedTypeApp) {
            this.setState({ selectedTypeApp: selectedTypeApp }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            listApprenant: code_html
                        })
                    }
                });
            });
        }
    }

    addApprenant = (selectedApprenant) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedApprenant: selectedApprenant }, () => {
            if (selectedApprenant != null) {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetInforApprenant.php?id=' + selectedApprenant.value,
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            numIdentite: code_html.numIdentite,
                            nom: code_html.nom,
                            prenom: code_html.prenom,
                            age: code_html.age,
                            selectedNivBase: code_html.niveau,
                            email: code_html.email,
                            tel: code_html.tel
                        })
                    }
                })
            };
        })
    }

    addType = (selectedTypeFiche) => {
        this.setState({ selectedTypeFiche: selectedTypeFiche });
    }

    addCarteFid = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                carteFid: this.state.numCarteAdh
            });
        }
        else {
            this.setState({
                carteFid: ''
            });
        }
    }

    addTenu = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                tenu: true
            });
        }
        else {
            this.setState({
                tenu: false
            });
        }
    }

    addNbrPersonne = (event) => {
        this.setState({
            nbrPersonne: event.target.value
        });
    }

    addMatFisc = (event) => {
        this.setState({
            matFisc: event.target.value
        });
    }

    addNbrPersTenu = (event) => {
        this.setState({
            nbrPersTenu: event.target.value
        });
    }

    addSociete = (event) => {
        this.setState({
            societe: event.target.value
        });
    }

    addResponsable = (event) => {
        this.setState({
            responsable: event.target.value
        });
    }

    addMobile = (event) => {
        this.setState({
            mobileResp: event.target.value
        });
    }

    addAge = (event) => {
        this.setState({
            age: event.target.value
        });
    }

    addLieuResid = (seletedLieuResid) => {
        this.setState({ seletedLieuResid: seletedLieuResid });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
    };

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addFax = (event) => {
        this.setState({
            fax: event.target.value
        });
    }

    addFacebook = (event) => {
        this.setState({
            facebook: event.target.value
        });
    }

    addFct = (event) => {
        this.setState({
            fonction: event.target.value
        });
    }

    addPhotoVerso(e) {
        this.setState({ photoVerso: e.target.files })
    }

    addTypeFormat = (selectedTypeFormat) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedTypeFormat: selectedTypeFormat }, () => {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectDetailCycleByTypeForCycle.php',
                type: 'POST',
                data: {
                    typeForCycle: selectedTypeFormat,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        listCycle: code_html
                    })
                }
            });
        });
    }

    addMotivation = (event) => {
        this.setState({
            motivation: event.target.value
        });
    }

    addNumIdentite = (event) => {
        this.setState({
            numIdentite: event.target.value
        });
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addConnaiss = (e, el) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.value
            this.setState({
                connaissChoisi: [...this.state.connaissChoisi, element]
            });
        }
        else {
            let element = el.value
            let indice = this.state.connaissChoisi.indexOf(element)
            this.state.connaissChoisi.splice(indice, 1)
        }
    }

    addCycle = selectedCycle => {
        this.setState({ selectedCycle });
    };

    ajoutInscripCycle = () => {
        if (this.state.seletedLieuResid != '' && this.state.selectedNivBase != '' && this.state.selectedTypeFormat != 0 && this.state.selectedCycle.length > 0 && this.state.connaissChoisi.length > 0) {
            var lieuResid = this.state.seletedLieuResid.value
            var niveauDeBase = this.state.selectedNivBase.value
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            var userId = userPermissions[0].idUser
            // Test si photo vide
            var pathPhotoVerso = '';
            if (this.state.photoVerso != null) {
                pathPhotoVerso = process.env.REACT_APP_API_URL + "images/Photo_eleves_cycles/" + this.state.photoVerso[0].name
            }
            var detailCycles = []
            for (var y = 0; y < this.state.selectedCycle.length; y++) {
                detailCycles.push(this.state.selectedCycle[y].value)
            }
            detailCycles = detailCycles.toString()
            var conaissanceChoix = this.state.connaissChoisi.toString()
            if (this.state.selectedTypeFiche == 1) {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddInscripCycle.php',
                    type: 'POST',
                    data: {
                        selectedApprenant: '',
                        numIdentite: this.state.numIdentite,
                        prenom: this.state.prenom,
                        numInscripCycle: this.state.numInscripCycle,
                        numCarteFid: this.state.carteFid,
                        matFisc: this.state.matFisc,
                        nbrPersonne: this.state.nbrPersonne,
                        nbrPersTenu: this.state.nbrPersTenu,
                        nom: this.state.societe,
                        responsable: this.state.responsable,
                        mobileResp: this.state.mobileResp,
                        age: this.state.age,
                        lieuResidId: lieuResid,
                        niveauBase: niveauDeBase,
                        email: this.state.email,
                        tel: this.state.tel,
                        fax: this.state.fax,
                        facebook: this.state.facebook,
                        fctActu: this.state.fonction,
                        pathPhoto: pathPhotoVerso,
                        typeFormat: this.state.selectedTypeFormat,
                        typeFiche: this.state.selectedTypeFiche,
                        nbrHeure: this.state.nbrHeure,
                        motivation: this.state.motivation,
                        connaissChoisi: conaissanceChoix,
                        cycleChoisi: detailCycles,
                        anneeScolaire: anneeScolaire,
                        userId: userId
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        if (code_html.Result == "OK") {
                            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectModePaiement.php').then(res => {
                                console.log(res)
                                this.setState({
                                    listModPay: res.data
                                })
                            })
                            this.setState({
                                blockReglement: true,
                                fraisCycle: code_html.fraisCycl,
                                reglInsCycle: code_html.regCycl,
                                societeCoch: code_html.regCycl.societeCoch,
                                tva: code_html.regCycl.tva,
                                numCarteFid: code_html.regCycl.numCarteFid,
                                mntCarte: code_html.regCycl.carte,
                                mntTenu: code_html.regCycl.tenu,
                                nomCycle: code_html.regCycl.cycle,
                                dateDCycle: code_html.regCycl.dateD,
                                dateFCycle: code_html.regCycl.dateF,
                                horaire: code_html.regCycl.horaire,
                                idInscriptionCycle: code_html.regCycl.idInscripCycle
                            }, () => {
                                console.log(this.state.modPayform)
                                var mntCycleSansTva = this.state.reglInsCycle.mnt
                                this.setState({
                                    mntCycleSansTva: mntCycleSansTva
                                });
                            });
                        }
                        else {
                            toast.error("⛔ Probléme lors de l'insertion !!", { containerId: 'A' });
                        }
                    }
                });
            }
            else if (this.state.selectedTypeFiche == 2) {
                var persAvecTenu = 0
                if (this.state.tenu == true) {
                    var persAvecTenu = 1
                }
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddInscripCycle.php',
                    type: 'POST',
                    data: {
                        selectedApprenant: this.state.selectedApprenant,
                        responsable: this.state.responsable,
                        mobileResp: this.state.mobileResp,
                        nbrHeure: this.state.nbrHeure,
                        numInscripCycle: this.state.numInscripCycle,
                        numCarteFid: this.state.carteFid,
                        numIdentite: this.state.numIdentite,
                        matFisc: this.state.matFisc,
                        prenom: this.state.prenom,
                        nom: this.state.nom,
                        age: this.state.age,
                        nbrPersonne: 1,
                        nbrPersTenu: persAvecTenu,
                        lieuResidId: lieuResid,
                        niveauBase: niveauDeBase,
                        email: this.state.email,
                        tel: this.state.tel,
                        fax: this.state.fax,
                        facebook: this.state.facebook,
                        fctActu: this.state.fonction,
                        pathPhoto: pathPhotoVerso,
                        typeFormat: this.state.selectedTypeFormat,
                        typeFiche: this.state.selectedTypeFiche,
                        motivation: this.state.motivation,
                        connaissChoisi: conaissanceChoix,
                        cycleChoisi: detailCycles,
                        anneeScolaire: anneeScolaire,
                        userId: userId
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        if (code_html.Result == "OK") {
                            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectModePaiement.php').then(res => {
                                console.log(res)
                                this.setState({
                                    listModPay: res.data
                                })
                            })
                            if (this.state.selectedApprenant != '') {
                                this.setState({
                                    arrayRegl: code_html.regFormat,
                                    inforEleve: code_html.regFormat[0],
                                    fraisReglEleve: code_html.regFormat[1],
                                    reglEleve: code_html.regFormat[2],
                                }, () => {
                                    console.log(this.state.inforEleve)
                                    var idEleve = this.state.inforEleve[0].eleveId
                                    var idInsFormation = this.state.inforEleve[0].idIns
                                    var modePaiementFormation = this.state.inforEleve[0].mode
                                    this.setState({
                                        elvId: idEleve,
                                        idInsFormation: idInsFormation,
                                        modePaiementFormation: modePaiementFormation
                                    });
                                });
                            }
                            this.setState({
                                blockReglement: true,
                                fraisCycle: code_html.fraisCycl,
                                reglInsCycle: code_html.regCycl,
                                societeCoch: code_html.regCycl.societeCoch,
                                tva: code_html.regCycl.tva,
                                numCarteFid: code_html.regCycl.numCarteFid,
                                mntCarte: code_html.regCycl.carte,
                                mntTenu: code_html.regCycl.tenu,
                                nomCycle: code_html.regCycl.cycle,
                                dateDCycle: code_html.regCycl.dateD,
                                dateFCycle: code_html.regCycl.dateF,
                                horaire: code_html.regCycl.horaire,
                                idInscriptionCycle: code_html.regCycl.idInscripCycle
                            }, () => {
                                console.log(this.state.modPayform)
                                var mntCycleSansTva = this.state.reglInsCycle.mnt
                                this.setState({
                                    mntCycleSansTva: mntCycleSansTva
                                });
                            });
                        }
                        else {
                            toast.error("⛔ Probléme lors de l'insertion !!", { containerId: 'A' });
                        }
                    }
                });
            }
        }
        else {
            toast.error("⛔ Veuillez verifier les champs lieu de residence,niveau d'inscription,type formation,cycle et conaissance !!", { containerId: 'A' });
        }
    }

    addMntFraisPayer = (event, indexMntFraisPayer) => {
        var mntFraisPayer = event.target.value
        var mntFrais = parseInt(this.state.fraisCycle[indexMntFraisPayer].mntFraisCycle)
        if (mntFrais >= mntFraisPayer) {
            this.setState({
                fraisCycle: this.state.fraisCycle.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntFraisCyclePayer: mntFraisPayer }) : el))
            }, () => {
                var mntFraisRestant = mntFrais - mntFraisPayer
                this.setState({
                    fraisCycle: this.state.fraisCycle.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntFraisCycleRest: Number.parseFloat(mntFraisRestant).toFixed(3) }) : el))
                });
                var sommeMntFraisPayer = 0;
                for (var i = 0; i < this.state.fraisCycle.length; i++) {
                    if (this.state.fraisCycle[i].mntFraisCyclePayer != "") {
                        sommeMntFraisPayer += parseInt(this.state.fraisCycle[i].mntFraisCyclePayer)
                    }
                    else {
                        sommeMntFraisPayer += 0
                    }
                }
                this.setState({
                    mntFraisCycleTotalPayer: Number.parseFloat(sommeMntFraisPayer).toFixed(3)
                });
            })
        }
        else {
            toast.error('⛔ Le montant saisie est supérieur !!', { containerId: 'A' });
        }
    }

    addPayTotFraisCycle = (e, index) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            var arrayFrais = this.state.fraisCycle
            var mntFraisCyclePayer = arrayFrais[index].mntFraisCycleRest
            var elFrais = arrayFrais[index]
            elFrais: Object.assign(elFrais, { mntFraisCyclePayer: mntFraisCyclePayer })
            this.setState({
                fraisCycle: arrayFrais
            }, () => {
                var mntFraisCycleRest = this.state.fraisCycle[index].mntFraisCycle - this.state.fraisCycle[index].mntFraisCyclePayer
                var arrayFrais = this.state.fraisCycle
                var elFrais = arrayFrais[index]
                elFrais: Object.assign(elFrais, { mntFraisCycleRest: Number.parseFloat(mntFraisCycleRest).toFixed(3) })
                this.setState({
                    fraisCycle: arrayFrais
                }, () => {
                    var sommeMntFraisPayer = 0;
                    for (var i = 0; i < this.state.fraisCycle.length; i++) {
                        if (this.state.fraisCycle[i].mntFraisCyclePayer != "") {
                            sommeMntFraisPayer += parseInt(this.state.fraisCycle[i].mntFraisCyclePayer)
                        }
                        else {
                            sommeMntFraisPayer += 0
                        }
                    }
                    this.setState({
                        mntFraisCycleTotalPayer: Number.parseFloat(sommeMntFraisPayer).toFixed(3)
                    });
                });
            });
        }
        else {
            var arrayFrais = this.state.fraisCycle
            var mntFraisCyclePayer = 0
            var elFrais = arrayFrais[index]
            elFrais: Object.assign(elFrais, { mntFraisCyclePayer: mntFraisCyclePayer })
            this.setState({
                fraisCycle: arrayFrais
            }, () => {
                var mntFraisCycleRest = this.state.fraisCycle[index].mntFraisCycle - this.state.fraisCycle[index].mntFraisCyclePayer
                var arrayFrais = this.state.fraisCycle
                var elFrais = arrayFrais[index]
                elFrais: Object.assign(elFrais, { mntFraisCycleRest: Number.parseFloat(mntFraisCycleRest).toFixed(3) })
                this.setState({
                    fraisCycle: arrayFrais
                }, () => {
                    var sommeMntFraisPayer = 0;
                    for (var i = 0; i < this.state.fraisCycle.length; i++) {
                        if (this.state.fraisCycle[i].mntFraisCyclePayer != "") {
                            sommeMntFraisPayer += parseInt(this.state.fraisCycle[i].mntFraisCyclePayer)
                        }
                        else {
