                                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleLignFormat(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                                        </Fragment>)}
                                                                </tr>
                                                            </tbody>
                                                        )}
                                                    </table>
                                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px', paddingBottom: '10px' }}>
                                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLignFormat} color="secondary"><i className="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                                    </div>
                                                </Fragment>) : (null)}
                                        </Fragment>) : (<Fragment><h3>Paiement reglement formation :</h3><p style={{ paddingLeft: '15px' }}>{"L'eléve " + this.state.nom + ' ' + this.state.prenom + " a payé la totalité de l'inscription"}</p>
                                        </Fragment>)) : (null)}

                                {this.state.blockReglement == true ?
                                    (<Fragment>
                                        {this.state.fraisCycle.length > 0 ?
                                            (<Fragment>
                                                <h3 style={{ marginTop: '10px' }}>Paiement frais cycle :</h3>
                                                <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                    <thead>
                                                        <tr>
                                                            <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                            <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                                            <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                                            <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                                            <th style={{ textAlign: 'center' }} scope="col">Cocher</th>
                                                        </tr>
                                                    </thead>
                                                    {this.state.fraisCycle.map((el, index) =>
                                                        <tbody>
                                                            <tr>
                                                                <td className="col-md-4" style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{el.nomFraisCycle}</p></td>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntFraisCycle).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><input type="number" value={el.mntFraisCyclePayer} onChange={(e) => this.addMntFraisPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntFraisCycleRest).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                <td className="col-md-2">
                                                                    <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                                        <Checkbox
                                                                            onChange={(e) => this.addPayTotFraisCycle(e, index)}
                                                                            disabled={this.state.disabledFrais}
                                                                        />
                                                                    </div>
                                                                </td>
                                                            </tr>
                                                        </tbody>
                                                    )}
                                                </table>
                                                <table className="table table-bordered">
                                                    <tbody>
                                                        <tr style={{ backgroundColor: '#f2f2f2' }}>
                                                            <td className="col-md-4" style={{ textAlign: 'center', fontWeight: 'bold' }}>Montant total frais payer</td>
                                                            <td className="col-md-2"></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center', fontWeight: 'bold' }}>{this.state.mntFraisCycleTotalPayer}</td>
                                                            <td className="col-md-2"></td>
                                                            <td className="col-md-2"></td>
                                                        </tr>
                                                    </tbody>
                                                </table>
                                            </Fragment>) : (null)}
                                        <h3>Paiement cycle:</h3>
                                        <table style={{ marginTop: '10px' }} className="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Infos Cycle</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Montant</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Cocher</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                <tr>
                                                    <td className="col-md-4" style={{ textAlign: 'center' }}><p>{"cycle: " + this.state.nomCycle}</p><p>{"Période: " + this.state.dateDCycle + "---" + this.state.dateFCycle + " " + this.state.horaire}</p></td>
                                                    <td className="col-md-2" style={{ textAlign: 'center' }}><input type="number" className="form-control" disabled value={Number.parseFloat(this.state.reglInsCycle.mnt).toFixed(3)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3', textAlign: 'center' }} /></td>
                                                    <td className="col-md-2" style={{ textAlign: 'center' }}><input type="number" value={this.state.reglInsCycle.mntCyclePay} onChange={(e) => this.addMntCyclePay(e)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3', textAlign: 'center' }} /></td>
                                                    <td className="col-md-2" style={{ textAlign: 'center' }}><input type="number" value={Number.parseFloat(this.state.reglInsCycle.mntCycleRestant).toFixed(3)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3', textAlign: 'center' }} /></td>
                                                    <td className="col-md-2">
                                                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                            <Checkbox
                                                                onChange={(e) => this.addPayTotRegCycle(e)}
                                                                disabled={this.state.disabledFrais}
                                                            />
                                                        </div>
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                        {this.state.societeCoch == true ?
                                            (<label style={{ paddingRight: '5px', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginRight: '10px' }}>
                                                Tva
                                                <Checkbox
                                                    onChange={(e) => this.addTva(e)}
                                                    disabled={this.state.disabled}
                                                />
                                                {this.state.tva}%
                                            </label>
                                            ) : (null)}
                                        <h3 style={{ paddingLeft: '15px' }}>Mode de paiement :</h3>
                                        <table style={{ marginTop: '10px' }} className="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Mode paiement</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Montant Paiement</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Numéro cheque</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Echéance cheque</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Date encaissement</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Titulaire cheque</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Banque</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                                </tr>
                                            </thead>
                                            {this.state.tabModPayCycle.map((el, index) =>
                                                <tbody>
                                                    <tr>
                                                        <td className="col-md-2"><Select
                                                            value={el.modPay}
                                                            isClearable={true}
                                                            isSearchable={true}
                                                            onChange={(e) => this.addModPayCycle(e, index)}
                                                            options={this.state.listModPay}
                                                        />  </td>
                                                        <td className="col-md-1" style={{ textAlign: 'center' }}><input type="number" value={el.mntModPay} onChange={(e) => this.addMntModPayCycle(e, index)} style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                        {this.state.tabModPayCycle[index].modPay.value == 2 ?
                                                            (<Fragment>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><input type="number" value={el.numCheque} onChange={(e) => this.addNumChecCycle(e, index)} style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} dateFormat="dd/MM/yyy" selected={el.echeanceChec} onChange={(e) => this.addEcheanceChecCycle(e, index)} /></td>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} dateFormat="dd/MM/yyy" selected={el.dateEncaiss} onChange={(e) => this.addDateEncaissCycle(e, index)} /></td>
                                                                <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" value={el.titulaireChec} onChange={(e) => this.addTitulaireChecCycle(e, index)} style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" value={el.banque} onChange={(e) => this.addBanqueCycle(e, index)} style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-1" style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleLignCycle(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                            </Fragment>) :
                                                            (<Fragment>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><input type="number" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                                <td className="col-md-1" style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleLignCycle(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                            </Fragment>)}
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                        <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                            <Button style={{ borderRadius: '5px' }} onClick={this.additionLignCycle} color="secondary"><i className="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                        </div>
                                    </Fragment>) : (null)}

                                {this.state.blockReglement == false ?
                                    (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutInscripCycle}>Confirmer</button>
                                    </div>) : (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutRegInscripCycle}>Confirmer</button>
                                    </div>)}
                            </div>
                        </div>
                    </div>
                    <Modal isOpen={this.state.modalFormat} toggle={this.nonDeleteLignFormat} className={this.props.className} fade={false}>
                        <ModalHeader toggle={this.nonDeleteLignFormat}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                        <ModalBody>
                            <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                <div style={{ display: 'flex', justifyContent: 'center' }}>
                                    <p>Êtes-Vous sûr de vouloir supprimer ce mode de paiement ?</p>
                                </div>
                                <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                    <div><button onClick={() => this.deleteLignFormat()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                    <div><button onClick={() => this.nonDeleteLignFormat()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                </div>
                            </div>
                        </ModalBody>
                    </Modal>
                    <Modal isOpen={this.state.modalCycle} toggle={this.nonDeleteLignCycle} className={this.props.className} fade={false}>
                        <ModalHeader toggle={this.nonDeleteLignCycle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                        <ModalBody>
                            <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                <div style={{ display: 'flex', justifyContent: 'center' }}>
                                    <p>Êtes-Vous sûr de vouloir supprimer ce mode de paiement ?</p>
                                </div>
                                <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                    <div><button onClick={() => this.deleteLignCycle()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                    <div><button onClick={() => this.nonDeleteLignCycle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                </div>
                            </div>
                        </ModalBody>
                    </Modal>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}
export default withRouter(InscripCycle);
