import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class Apprenants extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'CIN',
                    selector: 'cin',
                    sortable: false,
                    center: true
                },
                {
                    name: 'PrÃ©nom-Nom',
                    selector: 'prenomNom',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Mobile',
                    selector: 'mobile',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Modifier',
                    cell: row => this.state.permissionApprenants.modifier == 1 ? (<Link to={{
                        pathname: '/ModifApprenant',
                        state: { idApprenant: row.idApprenant }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} class="fas fa-pencil-alt"></i></button></Link>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
                {
                    name: 'Desactiver',
                    cell: row => this.state.permissionApprenants.supprimer == 1 ? (<button className='buttonSupprimer' onClick={() => this.toggletwo(row.idApprenant)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idApprenant: '',
            pending: true,
            // recherche
            nom: '',
            prenom: '',
            cin: '',
            villeResid: '',
            dateNaiss: '',
            listNivBase: [],
            selectedNivBase: '',
            ageMin: '',
            ageMax: '',
            //
            permissionApprenants: '',
            permissionInscription: ''
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var permissionApprenants = userPermissions[14]
            var permissionInscription = userPermissions[18]
            var anneeScolaire = userPermissions[0].anneeScolaire
            this.setState({
                permissionApprenants: permissionApprenants,
                permissionInscription: permissionInscription
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
                console.log(res)
                this.setState({
                    listNivBase: res.data
                })
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        }
    }

    newElement(page, totalRows) {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.prenom == "" && this.state.nom == "" && this.state.cin == "" && this.state.villeResid == "" && this.state.dateNaiss == "" && this.state.selectedNivBase == "" && this.state.ageMin == "" && this.state.ageMax == "") {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
        else {
            if (this.state.selectedNivBase == "") {
                var idNiveau = ""
            }
            else {
                var idNiveau = this.state.selectedNivBase.value
            }
            // convert date format date to string
            if (this.state.dateNaiss != "") {
                let dateNaiss = this.state.dateNaiss
                let month = '' + (dateNaiss.getMonth() + 1);
                let day = '' + dateNaiss.getDate();
                let year = dateNaiss.getFullYear();
                if (month.length < 2)
                    month = '0' + month;
                if (day.length < 2)
                    day = '0' + day;
                var convertDateNaiss = [year, month, day].join('-');
            }
            else {
                var convertDateNaiss = ""
            }
            //
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ApprenantSearch.php',
                        type: 'POST',
                        data: {
                            prenom: this.state.prenom,
                            nom: this.state.nom,
                            cin: this.state.cin,
                            villeResid: this.state.villeResid,
                            dateNaiss: convertDateNaiss,
                            idNiveau: idNiveau,
                            ageMin: this.state.ageMin,
                            ageMax: this.state.ageMax,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    perPage(currentRowsPerPage, currentPage) {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.prenom == "" && this.state.nom == "" && this.state.cin == "" && this.state.villeResid == "" && this.state.dateNaiss == "" && this.state.selectedNivBase == "" && this.state.ageMin == "" && this.state.ageMax == "") {
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: currentRowsPerPage,
                            page: currentPage,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
        else {
            if (this.state.selectedNivBase == "") {
                var idNiveau = ""
            }
            else {
                var idNiveau = this.state.selectedNivBase.value
            }
            // convert date format date to string
            if (this.state.dateNaiss != "") {
                let dateNaiss = this.state.dateNaiss
                let month = '' + (dateNaiss.getMonth() + 1);
                let day = '' + dateNaiss.getDate();
                let year = dateNaiss.getFullYear();
                if (month.length < 2)
                    month = '0' + month;
                if (day.length < 2)
                    day = '0' + day;
                var convertDateNaiss = [year, month, day].join('-');
            }
            else {
                var convertDateNaiss = ""
            }
            //
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ApprenantSearch.php',
                        type: 'POST',
                        data: {
                            prenom: this.state.prenom,
                            nom: this.state.nom,
                            cin: this.state.cin,
                            villeResid: this.state.villeResid,
                            dateNaiss: convertDateNaiss,
                            idNiveau: idNiveau,
                            ageMin: this.state.ageMin,
                            ageMax: this.state.ageMax,
                            limit: currentRowsPerPage,
                            page: currentPage,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    filterSearch(event) {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    toggletwo = (idApprenant) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idApprenant: idApprenant
        }));
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    deleteSearch = () => {
        this.state.search.splice(0, 1)
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addCin = (event) => {
        this.setState({
            cin: event.target.value
        });
    }

    addVilleResid = (event) => {
        this.setState({
            villeResid: event.target.value
        });
    }

    addDateNaiss = d => {
        this.setState({
            dateNaiss: d
        });
    };

    addNivBase = (selectedNivBase) => {
        if (selectedNivBase != null) {
            this.setState({ selectedNivBase });
        }
        else {
            this.setState({ selectedNivBase: '' });
        }
    }

    addAgeMin = (event) => {
        this.setState({
            ageMin: event.target.value
        });
    }

    addAgeMax = (event) => {
        this.setState({
            ageMax: event.target.value
        });
    }

    recherche = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                if (this.state.selectedNivBase == "") {
                    var idNiveau = ""
                }
                else {
                    var idNiveau = this.state.selectedNivBase.value
                }
                // convert date format date to string
                if (this.state.dateNaiss != "") {
                    let dateNaiss = this.state.dateNaiss
                    let month = '' + (dateNaiss.getMonth() + 1);
                    let day = '' + dateNaiss.getDate();
                    let year = dateNaiss.getFullYear();
                    if (month.length < 2)
                        month = '0' + month;
                    if (day.length < 2)
                        day = '0' + day;
                    var convertDateNaiss = [year, month, day].join('-');
                }
                else {
                    var convertDateNaiss = ""
                }
                //
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ApprenantSearch.php',
                    type: 'POST',
                    data: {
                        prenom: this.state.prenom,
                        nom: this.state.nom,
                        cin: this.state.cin,
                        villeResid: this.state.villeResid,
                        dateNaiss: convertDateNaiss,
                        idNiveau: idNiveau,
                        ageMin: this.state.ageMin,
                        ageMax: this.state.ageMax,
                        limit: this.state.nbrEltperPage,
                        page: 1,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                }
                )
            }
        )
    }

    afficherTout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({
            prenom: '',
            nom: '',
            cin: '',
            villeResid: '',
            dateNaiss: '',
            selectedNivBase: '',
            ageMin: '',
            ageMax: ''
        });
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html.data,
                    pending: false
                });
            }
        });
    }

    deleteApprenant() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + "Back_mediatraining/DeleteApprenant.php?id=" + this.state.idApprenant + "&userId=" + userId,
            type: 'POST',
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    modal: false
                }, () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                        type: 'POST',
                        data: {
                            query: '',
                            limit: this.state.nbrEltperPage,
                            page: 1,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data
                            });
                        }
                    });
                })
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Apprenants</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    {this.state.permissionApprenants.ajouter == 1 ?
                                        (<div style={{ marginRight: '10px' }}><Link to="/AjoutApprenant" > <button style={{ background: '#3A3F51', border: '1px solid #3A3F51', borderRadius: '5px' }} type="button" className="btn btn-info"> <i class="fas fa-plus"></i>Apprenant</button></Link></div>
                                        ) : (null)}
                                    {this.state.permissionInscription.ajouter == 1 ?
                                        (<div><Link to="/Inscription" ><button style={{ background: '#00C0EF', border: '1px solid #00C0EF', borderRadius: '5px' }} type="button" className="btn btn-info"> <i class="fas fa-plus"></i>Inscription</button></Link></div>) : (null)}
                                </div>
                                <div class="row">
                                    <h4 style={{ paddingLeft: '10px' }}>CritÃ¨res de recherche</h4>
                                    <Fragment>
                                        <div class="col-md-6">
                                        <label>PrÃ©nom</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.addPrenom(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                        <label>Nom</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.addNom(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                        <label>CIN</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.addCin(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                        <label>RÃ©sidence</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.addVilleResid(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Date naissance</label>
                                                <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                    selected={this.state.dateNaissance}
                                                    onChange={this.addDateNaiss}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div style={{ display: 'flex', alignItems: 'center' }} class="form-group">
                                                <label style={{ marginRight: '62px' }}>Niveau</label>
                                                <div style={{ width: '351px' }}>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addNivBase(e)}
                                                        options={this.state.listNivBase}
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                        <label>Age min</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.addAgeMin(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                        <label>Age max</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.addAgeMax(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                    </Fragment>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                    <ModalBody>
                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                <p>Ãtes-Vous sÃ»r de vouloir supprimer cet apprenant ?</p>
                            </div>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.deleteApprenant()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>
                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
            </div>
        );
    }
}

export default Apprenants;
