import React, { Component } from "react";
import jQuery from "jquery";
import Table from "reactstrap/lib/Table";

class ModifNoteNonBTS extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listNomNatureNote: [],
      noteEleve: [],
      nomEleve: "",
    };
  }

  componentDidMount() {
    if (JSON.parse(localStorage.getItem("userPermissions")) != null) {
      var idNote = this.props.location.state.idNote;
      var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
      var anneeScolaire = userPermissions[0].anneeScolaire;
      jQuery.ajax({
        url:
          process.env.REACT_APP_API_URL +
          "Back_mediatraining/GetElvNonBtsById.php",
        type: "POST",
        data: {
          idNote: idNote,
          anneeScolaire: anneeScolaire,
        },
        dataType: "json",
        success: (code_html, statut) => {
          console.log(code_html);
          this.setState({
            listNomNatureNote: code_html[0],
            noteEleve: code_html[1],
            nomEleve: code_html[1][0].nomEleve,
          });
        },
      });
    }
  }

  render() {
    return (
      <div id="page-wrapper" style={{ minHeight: "263px", marginLeft: "0px" }}>
        <div className="container-fluid">
          <div className="row bg-title">
            <div className="col-lg-12">
              <h4 className="page-title">Ajout utilisateur</h4>
            </div>
            {/* /.col-lg-12 */}
          </div>
          {/* /.row */}
          <div className="row">
            <div className="col-sm-12">
              <div className="white-box">
                <div className="row">
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>Eleve</th>
                        {this.state.listNomNatureNote.map((elem) => (
                          <th>{elem}</th>
                        ))}
                      </tr>
                    </thead>
                    <tbody>
                      <td>{this.state.nomEleve}</td>
                      {this.state.noteEleve.map((el, indexOfLigne) => (
                        <div>
                          {el.infoNote.map((element, indexOfColumn) => (
                            // <tr></tr>
                              <td>
                                <input
                                  type="text"
                                  onChange={(e) =>
                                    this.changeNote(
                                      indexOfLigne,
                                      indexOfColumn,
                                      e.target.value
                                    )
                                  }
                                  value={element.note}
                                  style={{ textAlign: "center" }}
                                ></input>
                              </td>
                          ))}
                        </div>
                      ))}
                    </tbody>
                  </Table>
                  ;
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ModifNoteNonBTS;
