import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Switch from "react-switch";
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Fragment } from 'react';

class Inscription extends Component {
    constructor(props) {
        super(props);
        this.state = {
            checked: false,
            prenom: '',
            nom: '',
            type: '',
            numSelonType: '',
            nationalite: '',
            dateDelivSelonType: '',
            villeDelivreSelonType: '',
            dateNaissance: '',
            lieuNaissance: '',
            email: '',
            selectedGender: 'Masculin',
            mobile: '',
            tel: '',
            adresseOfficiel: '',
            adresseTun: '',
            listNivBase: [],
            selectedNivBase: '',
            imageVerso: null,
            numInscrip: '',
            listApprenants: [],
            selectedApprenant: '',
            disabled: false,
            listPapiers: [],
            listTypePay: [],
            selectedTypePay: 'Annuel',
            listFilieres: [],
            selectedFiliere: '',
            listSpecialites: [],
            selectedSpecialite: '',
            listFormations: [],
            selectedFormation: '',
            listNiveaux: [],
            selectedNiveau: '',
            listGroupes: [],
            selectedGroup: '',
            papiersChoisi: []
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listFilieres: code_html
                });
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectApprenants.php').then(res => {
            console.log(res)
            this.setState({
                listApprenants: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/PapiersDemandee.php').then(res => {
            console.log(res)
            this.setState({
                listPapiers: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/TypesPaiementEleve.php').then(res => {
            console.log(res)
            this.setState({
                listTypePay: res.data
            })
        })
    }

    handleChange = (checked) => {
        this.setState({ checked });
        console.log(this.state.checked)
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    addNumSelonType = (event) => {
        this.setState({
            numSelonType: event.target.value
        });
    }

    addDateLivraison = d => {
        this.setState({
            dateDelivSelonType: d
        });
    };

    addVilleDelivreSelonType = (event) => {
        this.setState({
            villeDelivreSelonType: event.target.value
        });
    }

    addNationalite = (event) => {
        this.setState({
            nationalite: event.target.value
        });
    }

    addDateNaissance = d => {
        this.setState({
            dateNaissance: d
        });
    };

    addLieuNaissance = (event) => {
        this.setState({
            lieuNaissance: event.target.value
        });
    }

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addGender = (selectedGender) => {
        this.setState({ selectedGender: selectedGender });
    }

    addMobile = (event) => {
        this.setState({
            mobile: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addAdresseOfficiel = (event) => {
        this.setState({
            adresseOfficiel: event.target.value
        });
    }

    addAdresseTun = (event) => {
        this.setState({
            adresseTun: event.target.value
        });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
        console.log(`nivBase selected:`, selectedNivBase);
    };

    addImageVerso(e) {
        this.setState({ imageVerso: e.target.files })
    }

    addNumInscrip = (event) => {
        this.setState({
            numInscrip: event.target.value
        });
    }

    addApprenant = selectedApprenant => {
        if (selectedApprenant != null) {
            this.setState({ selectedApprenant }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetInfoEleveById.php?id=' + selectedApprenant.value).then(res => {
                    console.log(res)
                    this.setState({
                        type: res.data.type,
                        numSelonType: res.data.numero,
                        nationalite: res.data.nationalite,
                        dateDelivSelonType: res.data.dateNum,
                        villeDelivreSelonType: res.data.villeNum,
                        dateNaissance: res.data.naissance,
                        lieuNaissance: res.data.villeNaiss,
                        mobile: res.data.mobile,
                        tel: res.data.tel,
                        adresseTun: res.data.adresse,
                        selectedNivBase: res.data.nivBase,
                        imageVerso: res.data.photo
                    })
                })
            })
        }
        else {
            this.setState({
                type: '',
                numSelonType: '',
                nationalite: '',
                dateDelivSelonType: '',
                villeDelivreSelonType: '',
                dateNaissance: '',
                lieuNaissance: '',
                mobile: '',
                tel: '',
                adresseTun: '',
                selectedNivBase: '',
                imageVerso: ''
            });
        }
    };

    addFiliere = selectedFiliere => {
        if (selectedFiliere != null) {
            this.setState({ selectedFiliere }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectSpecByFiliere.php?id=' + selectedFiliere.value).then(res => {
                    console.log(res)
                    this.setState({
                        listSpecialites: res.data
                    })
                })
            })
        }
        else {
            this.setState({
                listSpecialites: []
            })
        }
    };

    addSpecialite = selectedSpecialite => {
        if (selectedSpecialite != null) {
            this.setState({ selectedSpecialite }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFormBySpecialite.php?id=' + selectedSpecialite.value).then(res => {
                    console.log(res)
                    this.setState({
                        listFormations: res.data
                    })
                })
            })
        }
        else {
            this.setState({
                listFormations: []
            })
        }
    };

    addFormation = selectedFormation => {
        if (selectedFormation != null) {
            this.setState({ selectedFormation }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivByFormation.php?id=' + selectedFormation.value).then(res => {
                    console.log(res)
                    this.setState({
                        listNiveaux: res.data
                    })
                })
            })
        }
        else {
            this.setState({
                listNiveaux: []
            })
        }
    };

    addNiveau = selectedNiveau => {
        if (selectedNiveau != null) {
            this.setState({ selectedNiveau }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupByNiv.php?id=' + selectedNiveau.value).then(res => {
                    console.log(res)
                    this.setState({
                        listGroupes: res.data
                    })
                })
            })
        }
        else {
            this.setState({
                listGroupes: []
            })
        }
    };

    addGroup = selectedGroup => {
        this.setState({ selectedGroup })
    };

    addPapiers = (e, index) => {
        console.log('Checkbox checked:', (e.target.checked));
        console.log(e.nativeEvent.path[2].innerText)
        if (e.target.checked === true) {
            let element = e.nativeEvent.path[2].innerText
            this.setState({
                papiersChoisi: [...this.state.papiersChoisi, element]
            });
        }
        else {
            this.state.papiersChoisi.splice(index, 1)
        }
    }

    addTypePay = (selectedTypePay) => {
        this.setState({ selectedTypePay: selectedTypePay });
    }

    ajout = () => {
        console.log(this.state.papiersChoisi)
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Inscription</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <label style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '10px' }}>
                                        <span style={{ marginLeft: '15px', marginRight: '10px' }}>Apprenant introuvable dans la liste!!!! Ajouter un Apprenant</span>
                                        <Switch offColor='#FF0000' onChange={this.handleChange} checked={this.state.checked} />
                                    </label>
                                </div>
                                {this.state.checked === false ?
                                    (<Fragment>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Num d'inscription</label>
                                                    <input type="text" class="form-control" placeholder="Numro inscription" onChange={this.addNumInscrip} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Apprenant</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addApprenant}
                                                        options={this.state.listApprenants}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div style={{ display: 'grid' }} class="form-group">
                                                    <label>Photo</label>
                                                    <img style={{ width: '20%' }} src={this.state.imageVerso} alt="img" />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Date naissance</label>
                                                <input type="text" disabled class="form-control" value={this.state.dateNaissance} style={{ borderRadius: '5px' }} />
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Lieu de naissance</label>
                                                    <input type="text" disabled class="form-control" value={this.state.lieuNaissance} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nationalit</label>
                                                    <input type="text" disabled class="form-control" value={this.state.nationalite} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Type</label>
                                                    <input type="text" disabled class="form-control" value={this.state.type} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Numero</label>
                                                    <input type="text" disabled class="form-control" value={this.state.numSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Delivr le</label>
                                                <input type="text" disabled class="form-control" value={this.state.dateDelivSelonType} style={{ borderRadius: '5px' }} />
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Delivr ville de</label>
                                                    <input type="text" disabled class="form-control" value={this.state.villeDelivreSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Mobile</label>
                                                    <input type="text" disabled class="form-control" value={this.state.mobile} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Tel</label>
                                                    <input type="text" disabled class="form-control" value={this.state.tel} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse en tunisie</label>
                                                    <input type="text" disabled class="form-control" value={this.state.adresseTun} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau de base</label>
                                                    <input type="text" disabled class="form-control" value={this.state.selectedNivBase.label} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                        </div>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nom complet du pre</label>
                                                    <input type="text" class="form-control" placeholder="Nom pre" onChange={this.addNomPere} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse des parents</label>
                                                    <input type="text" class="form-control" placeholder="Adresse parents" onChange={this.addAdresseParents} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Profession</label>
                                                    <input type="text" class="form-control" placeholder="Profession" onChange={this.addProfession} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Tl.Bur</label>
                                                    <input type="text" class="form-control" placeholder="Tel bureau" onChange={this.addTelBureau} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Filire</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFiliere}
                                                        options={this.state.listFilieres}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Specialit</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addSpecialite}
                                                        options={this.state.listSpecialites}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Formation</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFormation}
                                                        options={this.state.listFormations}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addNiveau}
                                                        options={this.state.listNiveaux}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Groupe</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addGroupe}
                                                        options={this.state.listGroupes}
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                        <div style={{ marginBottom: '20px' }} class="row">
                                            <div class="col-md-6">
                                                <label>Papiers Apportes</label>
                                                <span style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', border: '1px solid', padding: '10px', borderRadius: '5px' }}>
                                                    {this.state.listPapiers.map((el, index) =>
                                                        <label>
                                                            <Checkbox
                                                                onChange={(e) => this.addPapiers(e, index)}
                                                                disabled={this.state.disabled}
                                                            />
                                                            {el.papier}
                                                        </label>
                                                    )}
                                                </span>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Paiement</label>
                                                <RadioGroup
                                                    name="typePay"
                                                    selectedValue={this.state.selectedTypePay}
                                                    onChange={this.addTypePay}>
                                                    <div style={{ border: '1px solid', borderRadius: '5px', height: '72px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px' }}>
                                                        {this.state.listTypePay.map(el =>
                                                            <div>
                                                                <label>
                                                                    <Radio value={el.type} />{el.type}
                                                                </label>
                                                            </div>
                                                        )}
                                                    </div>
                                                </RadioGroup>
                                            </div>
                                        </div>
                                    </Fragment>) : (null)}
                                {this.state.checked === true ?
                                    (<Fragment>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Prnom</label>
                                                    <input type="text" class="form-control" placeholder="Prnom" onChange={this.addPrenom} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nom</label>
                                                    <input type="text" class="form-control" placeholder="Nom" onChange={this.addNom} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Sexe</label>
                                                <RadioGroup
                                                    name="sexe"
                                                    selectedValue={this.state.selectedGender}
                                                    onChange={this.addGender}>
                                                    <label>
                                                        <Radio value="Masculin" />Masculin
                                                </label>
                                                    <label>
                                                        <Radio value="Feminin" />Feminin
                                                </label>
                                                </RadioGroup>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Type</label>
                                                <RadioGroup
                                                    name="type"
                                                    selectedValue={this.state.selectedType}
                                                    onChange={this.addType}>
                                                    <label>
                                                        <Radio value="CIN" />CIN
                                                    </label>
                                                    <label>
                                                        <Radio value="Passeport" />Passeport
                                                    </label>
                                                </RadioGroup>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Numero</label>
                                                    <input type="text" class="form-control" placeholder="Numero selon le type" onChange={this.addNumSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nationalit</label>
                                                    <input type="text" class="form-control" placeholder="Nationalit" onChange={this.addNationalite} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Delivr le</label>
                                                <div class="form-group">
                                                    <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                        selected={this.state.dateDelivSelonType}
                                                        onChange={this.addDateLivraison}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Delivr ville de</label>
                                                    <input type="text" class="form-control" placeholder="Delivr dans ville de" onChange={this.addVilleDelivreSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Date naissance</label>
                                                <div class="form-group">
                                                    <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                        selected={this.state.dateNaissance}
                                                        onChange={this.addDateNaissance}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Lieu de naissance</label>
                                                    <input type="text" class="form-control" placeholder="Lieu de naissance" onChange={this.addLieuNaissance} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse officiel</label>
                                                    <input type="text" class="form-control" placeholder="Adresse officiel" onChange={this.addAdresseOfficiel} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse en tunisie</label>
                                                    <input type="text" class="form-control" placeholder="Adresse en tunisie" onChange={this.addAdresseTun} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Email</label>
                                                    <input type="text" class="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Mobile</label>
                                                    <input type="text" class="form-control" placeholder="Mobile" onChange={this.addMobile} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Tel</label>
                                                    <input type="text" class="form-control" placeholder="Tel" onChange={this.addTel} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau de base</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addNivBase}
                                                        options={this.state.listNivBase}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Photo verso </label>
                                                    <input type="file" class="form-control" name="file" onChange={(e) => this.addImageVerso(e)} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                        </div>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nom complet du pre</label>
                                                    <input type="text" class="form-control" placeholder="Nom pre" onChange={this.addNomPere} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse des parents</label>
                                                    <input type="text" class="form-control" placeholder="Adresse parents" onChange={this.addAdresseParents} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Profession</label>
                                                    <input type="text" class="form-control" placeholder="Profession" onChange={this.addProfession} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Tl.Bur</label>
                                                    <input type="text" class="form-control" placeholder="Tel bureau" onChange={this.addTelBureau} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Filire</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFiliere}
                                                        options={this.state.listFilieres}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Specialit</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addSpecialite}
                                                        options={this.state.listSpecialites}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Formation</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFormation}
                                                        options={this.state.listFormations}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addNiveau}
                                                        options={this.state.listNiveaux}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Groupe</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addGroupe}
                                                        options={this.state.listGroupes}
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                        <div style={{ marginBottom: '20px' }} class="row">
                                            <div class="col-md-6">
                                                <label>Papiers Apportes</label>
                                                <span style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', border: '1px solid', padding: '10px', borderRadius: '5px' }}>
                                                    {this.state.listPapiers.map(el =>
                                                        <label>
                                                            <Checkbox
                                                                checked
                                                                onChange={this.addPapiers}
                                                                disabled={this.state.disabled}
                                                            />
                                                            {el.papier}
                                                        </label>
                                                    )}
                                                </span>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Paiement</label>
                                                <RadioGroup
                                                    name="typePay"
                                                    selectedValue={this.state.selectedTypePay}
                                                    onChange={this.addTypePay}>
                                                    <div style={{ border: '1px solid', borderRadius: '5px', height: '72px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px' }}>
                                                        {this.state.listTypePay.map(el =>
                                                            <div>
                                                                <label>
                                                                    <Radio value={el.type} />{el.type}
                                                                </label>
                                                            </div>
                                                        )}
                                                    </div>
                                                </RadioGroup>
                                            </div>
                                        </div>
                                    </Fragment>) : (null)}

                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(Inscription);
