import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class AjoutMatiere extends Component {
    constructor(props) {
        super(props);
        this.state = {
            matieres: [{ code: '', nomMatiere: '', abrev: '', typeEns: '', nbrSemance: '', quantite: '' }],
            indexLignMatiereASupprimer: '',
            modal: false,
            ListTypeEns: []
        }
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectTypeEns.php').then(res => {
            console.log(res)
            this.setState({
                ListTypeEns: res.data
            })
        })
    }

    additionLignMatiere = () => {
        let element = { code: '', nomMatiere: '', abrev: '', typeEns: '', nbrSemance: '' }
        this.setState({
            matieres: [...this.state.matieres, element]
        })
    }

    addCode = (event, indexCode) => {
        let code = event.target.value;
        this.setState({
            matieres: this.state.matieres.map((el, id) => (id === indexCode ? Object.assign({}, { code: code }) : el))
        });
    }

    addNomMatiere = (event, indexNomMatiere) => {
        let nomMatiere = event.target.value;
        this.setState({
            matieres: this.state.matieres.map((el, id) => (id === indexNomMatiere ? Object.assign({}, { nomMatiere: nomMatiere }) : el))
        });
    }

    addAbrev = (event, indexAbrev) => {
        let abrev = event.target.value;
        this.setState({
            matieres: this.state.matieres.map((el, id) => (id === indexAbrev ? Object.assign({}, { abrev: abrev }) : el))
        });
    }

    addTypeEns = (selectedTypeEns, indexTypeEns) => {
        let typeEns = selectedTypeEns.value;
        this.setState({
            matieres: this.state.matieres.map((el, id) => (id === indexTypeEns ? Object.assign({}, { typeEns: typeEns }) : el))
        });
    }

    addNbrSeance = (event, indexNbrSeance) => {
        let nbrSeance = event.target.value;
        this.setState({
            matieres: this.state.matieres.map((el, id) => (id === indexNbrSeance ? Object.assign({}, { nbrSeance: nbrSeance }) : el))
        });
    }

    toggleLignMatiere = (indexLignMatiere) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignMatiereASupprimer: indexLignMatiere
        }));
    }

    deleteLignMatiere = () => {
        this.state.matieres.splice(this.state.indexLignMatiereASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLignMatiere = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
        console.log(this.state.matieres)
        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddMatiere.php',
        //     type: 'POST',
        //     data: {
        //         matieres: this.state.matieres,
        //     },
        //     dataType: 'json',
        //     success: (code_html, statut) => {
        //         if (code_html.Result == "OK") {
        //             this.props.history.push('/Matieres');
        //         }
        //         else if (code_html.Result == "KO") {
        //             alert("erreur,l'un de vos champs est vide!!");
        //         }
        //     }
        // });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout matiére</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Code</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Abrev</th>
                                                <th style={{ textAlign: 'center' }} scope="col">TypeEns</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Nbr seance</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Qte</th>
                                            </tr>
                                        </thead>
                                        {this.state.matieres.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td><input type="text" value={el.code} placeholder="code" onChange={(e) => this.addCode(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td><input type="text" value={el.nomMatiere} placeholder="nom matiére" onChange={(e) => this.addNomMatiere(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td><input type="text" value={el.abrev} placeholder="Abréviation" onChange={(e) => this.addAbrev(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td style={{ width: '20%' }}><Select
                                                        value={el.typeEns}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addTypeEns(e, index)}
                                                        options={this.state.ListTypeEns}
                                                    />  </td>
                                                    <td style={{ textAlign: 'center' }}><input type="number" value={el.nbrSeance} placeholder="Nombre de séance" onChange={(e) => this.addNbrSeance(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '33%' }} className='buttonSupprimer' onClick={() => this.toggleLignMatiere(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLignMatiere} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteLignMatiere} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteLignMatiere}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer cette ligne de matiére ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteLignMatiere()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteLignMatiere()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutMatiere);
