import Select from 'react-select';
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class Magasins extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Code',
                    selector: 'code',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Magasin',
                    selector: 'nom',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Explorer',
                    cell: row => <Link to={{
                        pathname: '/ExplMagasin',
                        state: { idMagasin: row.idMagasin }
                    }}><button className='buttonExplorer'><i style={{ color: 'white' }} className="far fa-eye"></i></button></Link>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
                {
                    name: 'Modifier',
                    cell: row => <Link to={{
                        pathname: '/ModifMagasin',
                        state: { idMagasin: row.idMagasin }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
                {
                    name: 'Desactiver',
                    cell: row => <button className='buttonSupprimer' onClick={() => this.toggletwo(row.idMagasin)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idMagasin: '',
            pending: true,
            gouvernorats: [],
            selectedGouv: '',
            enseignes: [],
            selectedEnseigne: ''

        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/Magasins.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectGouver.php').then(res => {
                console.log(res)
                this.setState({
                    gouvernorats: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectEnseignes.php').then(res => {
                console.log(res)
                this.setState({
                    enseignes: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    newElement(page, totalRows) {
        if (this.state.selectedGouv == '' && this.state.selectedEnseigne == '') {
            this.setState({
                pending: true
            },
                function () {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_magasin/Magasins.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: page
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }.bind(this)
            )
        }
        else {
            if (this.state.selectedGouv != '') {
                var gouvId = this.state.selectedGouv.value
            }
            else {
                var gouvId = ''
            }
            if (this.state.selectedEnseigne != '') {
                var enseigneId = this.state.selectedEnseigne.value
            }
            else {
                var enseigneId = ''
            }
            this.setState({
                pending: true
            },
                function () {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_magasin/SearchMagasins.php',
                        type: 'POST',
                        data: {
                            gouvId: gouvId,
                            enseigneId: enseigneId,
                            limit: this.state.nbrEltperPage,
                            page: page
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }.bind(this)
            )
        }
    }

    perPage(currentRowsPerPage, currentPage) {
        if (this.state.selectedGouv == '' && this.state.selectedEnseigne == '') {
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , function () {
                    console.log(currentRowsPerPage)
                    console.log(currentPage)
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_magasin/Magasins.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: currentRowsPerPage,
                            page: currentPage
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
        else {
            if (this.state.selectedGouv != '') {
                var gouvId = this.state.selectedGouv.value
            }
            else {
                var gouvId = ''
            }
            if (this.state.selectedEnseigne != '') {
                var enseigneId = this.state.selectedEnseigne.value
            }
            else {
                var enseigneId = ''
            }
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , function () {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_magasin/SearchMagasins.php',
                        type: 'POST',
                        data: {
                            gouvId: gouvId,
                            enseigneId: enseigneId,
                            limit: currentRowsPerPage,
                            page: currentPage
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
    }

    filterSearch(event) {
        if (this.state.selectedGouv == '' && this.state.selectedEnseigne == '') {
            this.setState({
                filterElement: event.target.value,
                resetFirstPage: !this.state.resetFirstPage,
                pending: true
            },
                function () {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_magasin/Magasins.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: 1
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    })
                }
            );
        }
    }

    toggletwo = (idMagasin) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idMagasin: idMagasin
        }));
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    deleteMagasin() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + "Back_magasin/DeleteMagasin.php?id=" + this.state.idMagasin,
            type: 'POST',
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    modal: false
                }, () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_magasin/Magasins.php',
                        type: 'POST',
                        data: {
                            query: '',
                            limit: this.state.nbrEltperPage,
                            page: 1
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data
                            });
                        }
                    });
                })
            }
        });
    }

    searchGouv = selectedGouv => {
        if (selectedGouv != null) {
            this.setState({ selectedGouv: selectedGouv });
        }
        else {
            this.setState({ selectedGouv: '' });
        }
    }

    searchEnseigne = selectedEnseigne => {
        if (selectedEnseigne != null) {
            this.setState({ selectedEnseigne: selectedEnseigne });
        }
        else {
            this.setState({ selectedEnseigne: '' });
        }
    }

    recherche = () => {
        if (this.state.selectedGouv != '') {
            var gouvId = this.state.selectedGouv.value
        }
        else {
            var gouvId = ''
        }
        if (this.state.selectedEnseigne != '') {
            var enseigneId = this.state.selectedEnseigne.value
        }
        else {
            var enseigneId = ''
        }
        this.setState({
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_magasin/SearchMagasins.php',
                    type: 'POST',
                    data: {
                        gouvId: gouvId,
                        enseigneId: enseigneId,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    afficherTout = () => {
        this.setState({
            selectedEnseigne: '',
            selectedGouv: ''
        }, () => {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/Magasins.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Magasins</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/AjoutMagasin" > <button style={{ background: '#3A3F51', border: '1px solid #3A3F51', borderRadius: '5px' }} type="button" className="btn btn-info"> <i className="fas fa-plus"></i>Ajout magasin</button></Link>
                                </div>
                                <div className="row">
                                    <h4 style={{ paddingLeft: '10px' }}>Critres de recherche</h4>
                                    <Fragment>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Gouvernorat</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={(e) => this.searchGouv(e)}
                                                    options={this.state.gouvernorats}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Enseigne</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={(e) => this.searchEnseigne(e)}
                                                    options={this.state.enseignes}
                                                />
                                            </div>
                                        </div>
                                    </Fragment>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                    <ModalBody>
                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                <p>tes-Vous sr de vouloir supprimer ce magasin ?</p>
                            </div>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.deleteMagasin()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}

export default Magasins;
