import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';

class InscripCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listConnaissance: [],
            numInscripCycle: '',
            listTypeFormat: [{ value: 1, label: 'SocietÃ©' }, { value: 2, label: 'Particulier' }],
            selectedType: 2,
            nbrPersonne: 0,
            matFisc: '',
            nbrPersTenu: 0,
            societe: '',
            responsable: '',
            mobileResp: '',
            age: '',
            lieuResid: [],
            seletedLieuResid: '',
            listNivBase: [],
            selectedNivBase: '',
            facebook: '',
            fonction: '',
            selectedTypeFormat: 2,
            motivation: '',
            numIdentite: '',
            prenom: '',
            nom: '',
            disabled: false,
            connaissChoisi: [],
            listCycle: [],
            selectedCycle: [],
            email: '',
            tel: '',
            fctActu: '',
            imageVerso: null,

        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumInscripCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numInscripCycle: "00" + (parseInt(code_html) + 1).toString()
                })
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectResid.php').then(res => {
            this.setState({
                lieuResid: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                listNivBase: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectConnaissance.php').then(res => {
            console.log(res)
            this.setState({
                listConnaissance: res.data
            })
        })
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listCycle: code_html
                })
            }
        });
    }

    addType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    addNbrPersonne = (event) => {
        this.setState({
            nbrPersonne: event.target.value
        });
    }

    addMatFisc = (event) => {
        this.setState({
            matFisc: event.target.value
        });
    }

    addNbrPersTenu = (event) => {
        this.setState({
            nbrPersTenu: event.target.value
        });
    }

    addSociete = (event) => {
        this.setState({
            societe: event.target.value
        });
    }

    addResponsable = (event) => {
        this.setState({
            responsable: event.target.value
        });
    }

    addMobile = (event) => {
        this.setState({
            mobileResp: event.target.value
        });
    }

    addAge = (event) => {
        this.setState({
            age: event.target.value
        });
    }

    addLieuResid = (seletedLieuResid) => {
        this.setState({ seletedLieuResid: seletedLieuResid });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
    };

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addFacebook = (event) => {
        this.setState({
            facebook: event.target.value
        });
    }

    addFct = (event) => {
        this.setState({
            fonction: event.target.value
        });
    }

    addImageVerso(e) {
        this.setState({ imageVerso: e.target.files })
    }

    addTypeFormat = (event) => {
        this.setState({
            fonction: event.target.value
        });
    }

    addTypeFormat = (selectedTypeFormat) => {
        this.setState({ selectedTypeFormat: selectedTypeFormat });
    }

    addMotivation = (event) => {
        this.setState({
            motivation: event.target.value
        });
    }

    addNumIdentite = (event) => {
        this.setState({
            numIdentite: event.target.value
        });
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addConnaiss = (e, el) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.value
            this.setState({
                connaissChoisi: [...this.state.connaissChoisi, element]
            });
        }
        else {
            let element = el.value
            let indice = this.state.connaissChoisi.indexOf(element)
            this.state.connaissChoisi.splice(indice, 1)
        }
    }

    ajout = () => {
        if (this.state.selectedType == 1) {
            console.log(this.state.matFisc)
            console.log(this.state.nbrPersonne)
            console.log(this.state.nbrPersTenu)
            console.log(this.state.societe)
            console.log(this.state.responsable)
            console.log(this.state.mobileResp)
            console.log(this.state.age)
            console.log(this.state.seletedLieuResid)
            console.log(this.state.selectedNivBase)
            console.log(this.state.email)
            console.log(this.state.tel)
            console.log(this.state.facebook)
            console.log(this.state.fctActu)
            console.log(this.state.photoVerso)
            console.log(this.state.motivation)
            console.log(this.state.connaissChoisi)
            console.log(this.state.selectedCycle)
        }
        else if (this.state.selectedType == 2) {
            console.log(this.state.numInscripCycle)
            console.log(this.state.prenom)
            console.log(this.state.nom)
            console.log(this.state.age)
            console.log(this.state.seletedLieuResid)
            console.log(this.state.selectedNivBase)
            console.log(this.state.email)
            console.log(this.state.tel)
            console.log(this.state.facebook)
            console.log(this.state.fctActu)
            console.log(this.state.photoVerso)
            console.log(this.state.motivation)
            console.log(this.state.connaissChoisi)
            console.log(this.state.selectedCycle)
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Inscription cycle</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
