import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import axios from 'axios'
import jQuery from 'jquery'
import Select from 'react-select';

class ExplDispoMag extends Component {
    constructor(props) {
        super(props);
        this.state = {
            arrayRapport: [],
            date: '',
            magasin: '',
            marchandiseur: '',
            articleRapp: [],
            selectedArticleRapp: '',
            articlesDispo: [{ label: 'Disponible', value: '1' }, { label: 'Indisponible', value: '0' }],
            selectedDispoArticle: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var idRappDispo = this.props.location.state.idRappDispo;
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetRapportDispoById.php?id=' + idRappDispo).then(res => {
                this.setState({
                    idRappDispo: idRappDispo,
                    arrayRapport: res.data,
                    date: res.data[0].date,
                    magasin: res.data[0].magasin,
                    marchandiseur: res.data[0].marchandiseur
                }, () => {
                })
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/SelectArticlesByRappId.php',
                type: 'POST',
                data: {
                    idRappDispo: idRappDispo
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        articleRapp: code_html
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    searchDispoArticle = selectedDispoArticle => {
        if (selectedDispoArticle !== null) {
            this.setState({ selectedDispoArticle: selectedDispoArticle });
        }
        else {
            this.setState({
                selectedDispoArticle: ''
            });
        }
    };

    searchArticle = selectedArticleRapp => {
        if (selectedArticleRapp !== null) {
            this.setState({ selectedArticleRapp: selectedArticleRapp });
        }
        else {
            this.setState({
                selectedArticleRapp: ''
            });
        }
    };

    recherche = () => {
        if (this.state.selectedDispoArticle != '') {
            var dispoArticle = this.state.selectedDispoArticle.value
        }
        else {
            var dispoArticle = ''
        }
        if (this.state.selectedArticleRapp != '') {
            var articleRapp = this.state.selectedArticleRapp.value
        }
        else {
            var articleRapp = ''
        }
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_magasin/GetRapportDispoByIdSearch.php',
            type: 'POST',
            data: {
                idRappDispo: this.state.idRappDispo,
                articleDispoId: dispoArticle,
                articleId: articleRapp
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    arrayRapport: code_html
                });
            }
        });
    }

    afficherTout = () => {
        this.setState({
            selectedDispoArticle: '',
            selectedArticleRapp: ''
        }, () => {
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetRapportDispoById.php?id=' + this.state.idRappDispo).then(res => {
                this.setState({
                    arrayRapport: res.data
                })
            })
        });
    }

    impr = () => {
        if (this.state.selectedDispoArticle != '') {
            var dispoArticle = this.state.selectedDispoArticle.value
        }
        else {
            var dispoArticle = ''
        }
        if (this.state.selectedArticleRapp != '') {
            var articleRapp = this.state.selectedArticleRapp.value
        }
        else {
            var articleRapp = ''
        }
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_magasin/ImprRapportArticleDispo.php',
            type: 'POST',
            data: {
                idRappDispo: this.state.idRappDispo,
                articleDispoId: dispoArticle,
                articleId: articleRapp
            },
            dataType: 'json',
            success: function (code_html, statut) {
                var server_path = code_html.server_path;
                var file_path = code_html.file_path
                var path = server_path + file_path;
                window.open(path, "_blank")
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Rapport disponibilité</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/RapportDispo"> <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label>Date</label>
                                            <input type="text" className="form-control" disabled value={this.state.date} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label>Magasin</label>
                                            <input type="text" className="form-control" disabled value={this.state.magasin} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label>Marchandiseur</label>
                                            <input type="text" className="form-control" disabled value={this.state.marchandiseur} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <h4 style={{ paddingLeft: '10px' }}>Critères de recherche</h4>
                                    <Fragment>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Disponibilité articles</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchDispoArticle}
                                                    options={this.state.articlesDispo}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Articles</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchArticle}
                                                    options={this.state.articleRapp}
                                                />
                                            </div>
                                        </div>
                                    </Fragment>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.impr}><i style={{ color: 'white', paddingRight: '5px' }} class="fas fa-print"></i>Impression</button>
                                    </div>
                                </div>
                                {this.state.arrayRapport.length > 0 ? (<div style={{ marginTop: '10px' }} className="row">
                                    <div className="table-responsive" style={{ textAlign: 'center' }}>
                                        <table className="table align-items-center table-flush">
                                            <thead className="thead-light">
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col"><i class="fas fa-shopping-basket"></i>Articles</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Dispo <i style={{ color: 'green' }} className="fas fa-check-circle"></i></th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Indispo <i style={{ color: 'red' }} className="far fa-times-circle"></i></th>
                                                </tr>
                                            </thead>
                                            {this.state.arrayRapport.map((el, index) =>
                                                <tbody>
                                                    <tr>
                                                        <td>
                                                            {el.article}
                                                        </td>
                                                        {el.dispo == 1 ? (<td>
                                                            <input style={{ fontWeight: 'bold' }} type="checkbox" checked={true} />
                                                        </td>) : (<td>
                                                            <input style={{ fontWeight: 'bold' }} type="checkbox" checked={false} />
                                                        </td>)}
                                                        {el.dispo == 0 ? (<td>
                                                            <input style={{ fontWeight: 'bold' }} type="checkbox" checked={true} />
                                                        </td>) : (<td>
                                                            <input style={{ fontWeight: 'bold' }} type="checkbox" checked={false} />
                                                        </td>)}
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                    </div>
                                </div>) : (null)}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ExplDispoMag);
