import React, { Component } from 'react';
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import jQuery from 'jquery'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import './Test.css'

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user_name: '',
            user_passe: '',
        }
        this.changeusername = this.changeusername.bind(this)
        this.changeuserpasse = this.changeuserpasse.bind(this)
        this.connect = this.connect.bind(this)
    }

    changeusername(event) {
        this.setState({
            user_name: event.target.value
        });
    }

    changeuserpasse(event) {
        this.setState({
            user_passe: event.target.value
        });
    }

    connect() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_snaii/ConnectUtilisateur.php',
            type: 'POST',
            data: {
                user_nom: this.state.user_name,
                mot_de_passe: this.state.user_passe
            },
            dataType: 'json',
            success: function (code_html, statut) {
                if (code_html.connection === "OK") {
                    toast.success('✅ Bienvenue dans SNAII !', { containerId: 'A' });
                    document.location = "/Clients"
                }
                else if (code_html.connection === "KO") {
                    toast.error('⛔ Veuillez vérifier votre login et mot de passe !', { containerId: 'A' });
                }
            }
        });
    }

    render() {
        return (
            <section id="wrapper" className="login-register">
                <div className="main">
                    <div className="container">
                        <center>
                            <div className="middle">
                                <div id="login">
                                    <form action="javascript:void(0);" method="get">
                                        <fieldset className="clearfix">
                                            <p><span className="fa fa-user" /><input type="text" placeholder="Username" required /></p> {/* JS because of IE support; better: placeholder="Username" */}
                                            <p><span className="fa fa-lock" /><input type="password" placeholder="Password" required /></p> {/* JS because of IE support; better: placeholder="Password" */}
                                            <div>
                                                <span style={{ width: '48%', textAlign: 'left', display: 'inline-block' }}></span>
                                                <span style={{ width: '50%', textAlign: 'right', display: 'inline-block' }}><input type="submit" defaultValue="Sign In" onClick={this.connect.bind(this)} /></span>
                                            </div>
                                        </fieldset>
                                        <div className="clearfix" />
                                    </form>
                                    <div className="clearfix" />
                                </div> {/* end login */}
                                <div className="logo">LOGO
                <div className="clearfix" />
                                </div>
                            </div>
                        </center>
                    </div>
                </div>
                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
            </section>
        );
    }
}

export default Login;
