import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Menu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            barreNavigation: [{ nomElement: 'Scolarite', arraySousElement: [{ nomSousElement: 'Matieres par formation', link: '/MatieresFormat', permission: 0, iconOption: 'fas fa-graduation-cap' }, { nomSousElement: 'Papiers Apportées', link: '/Papiers', permission: 0, iconOption: 'fas fa-paste' }, { nomSousElement: 'Frais formation', link: '/FraisFormation', permission: 0, iconOption: 'fas fa-money-check-alt' }, { nomSousElement: 'Niveaux', link: '/Niveaux', permission: 0, iconOption: 'fas fa-list-ol' }, { nomSousElement: 'Formations', link: '/Formations', permission: 0, iconOption: 'fas fa-scroll' }, { nomSousElement: 'Specialités', link: '/Specialites', permission: 0, iconOption: 'fas fa-chalkboard' }, { nomSousElement: 'Matieres', link: '/Matieres', permission: 0, iconOption: 'fas fa-book-open' }, { nomSousElement: 'Filiéres', link: '/Filieres', permission: 0, iconOption: 'fas fa-bookmark' }, { nomSousElement: 'Niveau de base', link: '/NivBase', permission: 0, iconOption: 'fas fa-book' }, { nomSousElement: 'Type enseignement', link: '/TypeEnseign', permission: 0, iconOption: 'fas fa-edit' }], permiss: 0, icon: 'fas fa-clipboard' }]
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Menu</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    {this.state.barreNavigation.map((el, index) =>
                                        <div className="col-md-4">
                                            <div className="white-box">
                                                <h3><i style={{ paddingRight: '5px' }} className={el.icon}></i>{el.nomElement}</h3>
                                                <div className="myadmin-dd dd" id="nestable">
                                                    <ol className="dd-list">
                                                        <li className="dd-item" data-id={index}>
                                                            {el.arraySousElement.map(ele =>
                                                                <Link to={ele.link}><div style={{border: '1px solid rgba(120,130,140,.13)',padding: '8px 16px',height: 'auto',fontWeight: '600'}}><i style={{ paddingRight: '5px' }} className={ele.iconOption}></i>{ele.nomSousElement}</div></Link>)}
                                                        </li>
                                                    </ol>
                                                </div>
                                            </div>
                                        </div>)}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Menu;
