import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { RadioGroup, Radio } from 'react-radio-group'

class AjoutRemboursement extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dateRembours: new Date(),
            selectedScol: '',
            listApprenant: [],
            selectedApprenant: '',
            listRecu: [],
            selectedNumRecu: '',
            description: '',
            mntRebours: 0,
            numPiece: '',
            dateEcheance: new Date(),
            tabModPay: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            modal: false,
            indexLignASupprimer: ''
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    listApprenant: code_html
                });
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectModePaiement.php').then(res => {
            console.log(res)
            this.setState({
                listModPay: res.data
            })
        })
    }

    addDateRembours = d => {
        this.setState({
            datePay: d
        });
    }

    addType = (selectedScol) => {
        if (selectedScol != null) {
            this.setState({ selectedScol: selectedScol }, () => {
                var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
                var anneeScolaire = userPermissions[0].anneeScolaire
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleveByScol.php',
                    type: 'POST',
                    data: {
                        selectedScol: selectedScol,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            listApprenant: code_html
                        });
                    }
                });
            });
        }
    }

    addApprenant = selectedApprenant => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedApprenant }, () => {
            if (selectedApprenant != null) {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetRecuByEleve.php',
                    type: 'POST',
                    data: {
                        selectedScol: this.state.selectedScol,
                        idApprenant: selectedApprenant.value,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            listRecu: code_html
                        });
                    }
                });
            }
        });
    };

    addNumRecu = selectedNumRecu => {
        this.setState({ selectedNumRecu }, () => {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetMntRecu.php',
                type: 'POST',
                data: {
                    selectedScol: this.state.selectedScol,
                    idRecu: selectedScol
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        mntRebours: code_html
                    });
                }
            })
        });
    };

    addDescript = (event) => {
        this.setState({
            description: event.target.value
        });
    }

    addMntRebours = (event) => {
        this.setState({
            mntRebours: event.target.value
        });
    }

    addNumPiece = (event) => {
        this.setState({
            numPiece: event.target.value
        });
    }

    addDateEcheance = d => {
        this.setState({
            dateEcheance: d
        });
    }

    additionLign = () => {
        let element = { modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }
        this.setState({
            tabModPay: [...this.state.tabModPay, element]
        })
    }

    addModPay = (selectedModPay, indexModPay) => {
        if (selectedModPay != null) {
            let modPay = selectedModPay;
            this.setState({
                tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { modPay: modPay }) : el))
            });
        }
    }

    addMntModPay = (event, indexModPay) => {
        let mntModPay = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { mntModPay: Number.parseFloat(mntModPay).toFixed(3) }) : el))
        });
    }

    addNumChec = (event, indexModPay) => {
        let numCheque = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { numCheque: numCheque }) : el))
        });
    }

    addEcheanceChec = (d, indexModPay) => {
        let dateChec = d;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { echeanceChec: dateChec }) : el))
        });
    }

    addDateEncaiss = (d, indexModPay) => {
        let dateEncaiss = d;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { dateEncaiss: dateEncaiss }) : el))
        });
    }

    addTitulaireChec = (event, indexModPay) => {
        let titulaireChec = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { titulaireChec: titulaireChec }) : el))
        });
    }

    addBanque = (event, indexModPay) => {
        let banque = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { banque: banque }) : el))
        });
    }

    toggleLign = (index) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignASupprimer: index
        }));
    }

    deleteLign = () => {
        this.state.tabModPay.splice(this.state.indexLignASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLign = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        var userId = userPermissions[0].idUser
        // convert date seance
        let datePay = this.state.datePay
        let month = '' + (datePay.getMonth() + 1);
        let day = '' + datePay.getDate();
        let year = datePay.getFullYear();
        if (month.length < 2)
            month = '0' + month;
        if (day.length < 2)
            day = '0' + day;
        var convertDatePay = [year, month, day].join('-');
        // 
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddRemboursement.php',
            type: 'POST',
            data: {
                modPay: this.state.tabModPay,
                anneeScolaire: anneeScolaire,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    // this.props.history.push('/Remboursement');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout remboursement</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date remboursement</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateRembours}
                                                onChange={this.addDateRembours}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div class="col-md-6">
                                        <RadioGroup
                                            name="typeScol"
                                            selectedValue={this.state.selectedScol}
                                            onChange={this.addType}>
                                            <label style={{ paddingRight: '5px' }}>
                                                <Radio value="1" />Formation
                                                    </label>
                                            <label style={{ paddingRight: '5px' }}>
                                                <Radio value="2" />Cycle
                                                    </label>
                                        </RadioGroup>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>eleve</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addApprenant}
                                                options={this.state.listApprenant}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Numro recu</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addNumRecu}
                                                options={this.state.listRecu}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Description</label>
                                            <input type="text" className="form-control" placeholder='Description' onChange={this.addDescript} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Montant  rembourser</label>
                                            <input type="number" className="form-control" value={Number.parseFloat(this.state.mntRebours).toFixed(3)} onChange={this.addMntRebours} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Numro pice</label>
                                            <input type="number" className="form-control" onChange={this.addNumPiece} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Echance</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateEcheance}
                                                onChange={this.addDateEcheance}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className='row'>
                                    <h3 style={{ paddingLeft: '15px' }}>Mode de paiement :</h3>
                                    <table style={{ marginTop: '10px' }} className="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Mode paiement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Montant Paiement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Numro cheque</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Echance cheque</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Date encaissement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Titulaire cheque</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Banque</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.tabModPay.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td className="col-md-3"><Select
                                                        value={el.modPay}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addModPay(e, index)}
                                                        options={this.state.listModPay}
                                                    />  </td>
                                                    <td className="col-md-1" style={{ textAlign: 'center' }}><input type="number" value={el.mntModPay} onChange={(e) => this.addMntModPay(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                    {this.state.tabModPay[index].modPay.value == 2 ?
                                                        (<Fragment>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="number" value={el.numCheque} onChange={(e) => this.addNumChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.echeanceChec} onChange={(e) => this.addEcheanceChec(e, index)} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.dateEncaiss} onChange={(e) => this.addDateEncaiss(e, index)} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" value={el.titulaireChec} onChange={(e) => this.addTitulaireChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" value={el.banque} onChange={(e) => this.addBanque(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }} onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                        </Fragment>) :
                                                        (<Fragment>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="number" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }} onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                        </Fragment>)}
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                    <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i className="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                            <Modal isOpen={this.state.modal} toggle={this.nonDeleteLign} className={this.props.className} fade={false}>
                                <ModalHeader toggle={this.nonDeleteLign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                <ModalBody>
                                    <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                                            <p>tes-Vous sr de vouloir supprimer ce mode de paiement ?</p>
                                        </div>
                                        <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                            <div><button onClick={() => this.deleteLign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                            <div><button onClick={() => this.nonDeleteLign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                        </div>
                                    </div>
                                </ModalBody>
                            </Modal>
                            <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

