        super(props);
        this.state = {
            barreNavigation: [{ titre: 'Documentation', element: [{ nomElement: "Inscription", iconElement: '', linkElement: '', sousPermission: 0, arraySousElement: [{ nomSousElement: 'Formation initiale', linkSousElement: '/Inscription', sousSousPermission: 0, iconSousElement: 'fas fa-box-tissue', arraySousSousElement: [] }, { nomSousElement: 'Formation accéléré', linkSousElement: '/InscripCycle', sousSousPermission: 0, iconSousElement: 'fas fa-box-tissue', arraySousSousElement: [] }] }, { nomElement: "Reglements", iconElement: '', linkElement: '', sousPermission: 0, arraySousElement: [{ nomSousElement: 'Reglement formation initiale', linkSousElement: '', sousSousPermission: 0, iconSousElement: '', arraySousSousElement: [{ nomSousSousElement: 'Consulter reglement', linkSousSousElement: '/ReglApprenant', sousSousSousPermission: 0, iconSousSousElement: 'fas fa-eye' }, { nomSousSousElement: 'Mise a jour reglement', linkSousSousElement: '/ModifReglApp', sousSousSousPermission: 0, iconSousSousElement: 'fas fa-edit' }] }, { nomSousElement: 'Reglement formation accéléré', linkSousElement: '', sousSousPermission: 0, iconSousElement: '', arraySousSousElement: [{ nomSousSousElement: 'Consulter reglement', linkSousSousElement: '/ReglApprenantCycle', sousSousSousPermission: 0, iconSousSousElement: 'fas fa-eye' }, { nomSousSousElement: 'Mise a jour reglement', linkSousSousElement: '/ModifReglCycleApp', sousSousSousPermission: 0, iconSousSousElement: 'fas fa-edit' }] }, { nomSousElement: "Autre Frais", iconSousElement: 'fas fa-money-check', linkSousElement: '/AutreFrais', sousSousPermission: 0, arraySousSousElement: [] }, { nomSousElement: "Reboursement", iconSousElement: 'fas fa-coins', linkSousElement: '/Remboursement', sousSousPermission: 0, arraySousSousElement: [] },] }, { nomElement: "Asseduité", iconElement: '', linkElement: '', sousPermission: 0, arraySousElement: [{ nomSousElement: "Absence éleve", iconSousElement: 'fas fa-calendar-alt', linkSousElement: '/Absence', sousSousPermission: 0, arraySousSousElement: [] }] }, { nomElement: "Gestion des enseignants", iconElement: '', linkElement: '', sousPermission: 0, arraySousElement: [{ nomSousElement: 'Reglement des enseignants', linkSousElement: '', sousSousPermission: 0, iconSousElement: '', arraySousSousElement: [{ nomSousSousElement: 'Consulter reglement', linkSousSousElement: '/ReglementEnseignant', sousSousSousPermission: 0, iconSousSousElement: 'fas fa-eye' }, { nomSousSousElement: 'Mise a jour reglement', linkSousSousElement: '/PayReglementEnseignant', sousSousSousPermission: 0, iconSousSousElement: 'fas fa-edit' }, { nomSousSousElement: 'Chéques enseignants', linkSousSousElement: '/ChequeEns', sousSousSousPermission: 0, iconSousSousElement: 'far fa-credit-card' }] }, { nomSousElement: "Présence enseignant", iconSousElement: 'fas fa-user-clock', linkSousElement: '/PresenceEnseignant', sousSousPermission: 0, arraySousSousElement: [] }, { nomSousElement: "Conseils et assistance", iconSousElement: 'fas fa-pencil-ruler', linkSousElement: '/ConseilAssistance', sousSousPermission: 0, arraySousSousElement: [] },] }], iconTitre: 'fas fa-list-alt', permission: 0 }]
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        if (userPermissions != null) {
        }
        else {
            document.location = '/'
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Menu</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box" >
                                <div className="row">
                                    {this.state.barreNavigation.map((el, index) =>
                                        <div className="col-md-4" style={{ color: "rgb(43, 43, 43)" }}>
                                            <div className="white-box" style={{ border: 'solid 1px', borderRadius: '5px' }}>
                                                <h3 style={{ border: 'solid 1px', backgroundColor: 'black', color: 'white' }}><i style={{ paddingRight: '5px', paddingLeft: '5px' }} className={el.iconTitre}></i>{el.titre}</h3>
                                                <div className="myadmin-dd dd" id="nestable">
                                                    <ol className="dd-list">
                                                        {el.element.map((ele, indice) =>
                                                            <div>
                                                                {
                                                                    (ele.arraySousElement.length > 0) ?
                                                                        // Noeud Parent Ayant des Fils Feuilles
                                                                        (<Fragment>
                                                                            <li className="dd-item" data-id={index}>
                                                                                <div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600', color: '#2b2b2b' }}><i style={{ paddingRight: '5px' }} className={ele.iconElement}></i>{ele.nomElement}</div>
                                                                                <ol className="dd-list">
                                                                                    {ele.arraySousElement.map(elm =>
                                                                                        <li className="dd-item" data-id={index + 1}>
                                                                                            {elm.linkSousElement != '' ? (<Link style={{ color: '#2b2b2b' }} to={elm.linkSousElement}><div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}><i style={{ paddingRight: '5px' }} className={elm.iconSousElement}></i>{elm.nomSousElement}</div></Link>) : (<div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}><i style={{ paddingRight: '5px' }} className={elm.iconSousElement}></i>{elm.nomSousElement}</div>)}
                                                                                            {elm.arraySousSousElement.length > 0 ? (
                                                                                                <ol className="dd-list">
                                                                                                    {elm.arraySousSousElement.map(ellm =>
                                                                                                        <li className="dd-item" data-id={index + 1}>
                                                                                                            <Link style={{ color: '#2b2b2b' }} to={ellm.linkSousSousElement}><div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}><i style={{ paddingRight: '5px' }} className={ellm.iconSousSousElement}></i>{ellm.nomSousSousElement}</div></Link>
                                                                                                        </li>)}
                                                                                                </ol>) : (null)}
                                                                                        </li>)}
                                                                                </ol>
                                                                            </li> </Fragment>) :
                                                                        // Noeud Feuille (Terminaison)
                                                                        (<Link style={{ color: '#2b2b2b' }} to={ele.linkElement}><div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}><i style={{ paddingRight: '5px' }} className={ele.iconElement}></i>{ele.nomElement}</div></Link>)
                                                                }
                                                            </div>
                                                        )}
                                                    </ol>
                                                </div>
                                            </div>
                                        </div>)}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default MenuDocumentation;
