import React, { Component } from 'react';
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import './paginate.css'
import { Fragment } from 'react';

class StatiquePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicSession: true,
            clicScolarite: false,
            listpermissions: [],
            permissionGestionRoles: [],
            permissionGestionUtilisateurs: [],
            permissionTypeEnseign: [],
            permissionNivBase: [],
            permissionFilieres: [],
            permissionMatieres: [],
            permissionSpecialites: [],
            permissionNivFor: [],
            permissionMatFor: [],
            permissionEnseignants: []
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        if (userPermissions != null) {
            this.setState({ listpermissions: userPermissions })
            var gestionRoles = userPermissions[0]
            this.setState({ permissionGestionRoles: gestionRoles })
            var gestionUtilisateurs = userPermissions[1]
            this.setState({ permissionGestionUtilisateurs: gestionUtilisateurs })
            var typeEnseign = userPermissions[2]
            this.setState({ permissionTypeEnseign: typeEnseign })
            var nivBase = userPermissions[3]
            this.setState({ permissionNivBase: nivBase })
            var filieres = userPermissions[4]
            this.setState({ permissionFilieres: filieres })
            var matieres = userPermissions[5]
            this.setState({ permissionMatieres: matieres })
            var specialites = userPermissions[6]
            this.setState({ permissionSpecialites: specialites })
            var nivFor = userPermissions[7]
            this.setState({ permissionNivFor: nivFor })
            var matFor = userPermissions[8]
            this.setState({ permissionMatFor: matFor })
            var enseignants = userPermissions[9]
            this.setState({ permissionEnseignants: enseignants })
            if (userPermissions[0].idRoleUser != 1) {
                this.setState({
                    clicScolarite: true
                });
            }
        }
        else {
            this.props.history.push('/');
        }
    }

    clicSession = () => {
        this.setState({
            clicSession: true,
            clicScolarite: false
        });
        this.props.history.push('/Users');
    }

    clicScolarite = () => {
        this.setState({
            clicScolarite: true,
            clicSession: false
        });
        this.props.history.push('/TypeEnseign');
    }

    clearStorage = () => {
        localStorage.clear();
        this.props.history.push('/');
    }

    render() {
        return (
            <div id="wrapper">
                {/* Navigation */}
                <nav className="navbar navbar-default navbar-static-top" style={{ marginBottom: 0 }}>
                    <div className="navbar-header"> <a className="navbar-toggle hidden-sm hidden-md hidden-lg " href="javascript:void(0)" data-toggle="collapse" data-target=".navbar-collapse"><i className="ti-menu" /></a>
                        <div className="top-left-part"><a className="logo"><img style={{ backgroundColor: 'white' }} className="img-circle" width='30px' height='30px' src="images/training.jpg" alt="Global Media Training" />&nbsp;<span style={{ fontSize: '13px' }} className="hidden-xs">Global Media Training</span></a></div>
                        <ul className="nav navbar-top-links navbar-left hidden-xs">
                            <li><a href="javascript:void(0)" className="open-close hidden-xs waves-effect waves-light"><i className="ti-menu icon-arrow-left-circle" /></a></li>
                            {(this.state.permissionGestionRoles.ajouter == 1 || this.state.permissionGestionRoles.explorer == 1 || this.state.permissionGestionRoles.modifier == 1 || this.state.permissionGestionRoles.supprimer == 1) ?
                                (<li onClick={this.clicSession} className='hvr-back-pulse'><a><i style={{ marginRight: '5px' }} class="fas fa-shoe-prints"></i>Habilitation</a></li>) : (null)}
                            <li onClick={this.clicScolarite} className='hvr-back-pulse'><a><i style={{ marginRight: '5px' }} class="fas fa-clipboard"></i>Scolarité</a></li>
                        </ul>
                        <ul className="nav navbar-top-links navbar-right pull-right">
                            <li>
                                <div onClick={this.clearStorage} className='hvr-shrink'><span style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="profile-pic"> <img src="images/users/logout.png" alt="user-img" width={25} className="img-circle" /><b className="hidden-xs">Déconnexion</b> </span></div>
                            </li>
                        </ul>
                    </div>
                    {/* /.navbar-header */}
                    {/* /.navbar-top-links */}
                    {/* /.navbar-static-side */}
                </nav>
                <div className="navbar-default sidebar nicescroll" role="navigation">
                    <div className="sidebar-nav navbar-collapse">
                        <ul className="nav in" id="side-menu">
                            <li className="sidebar-search hidden-sm hidden-md hidden-lg">
                                <div className="input-group custom-search-form">
                                    <input type="text" className="form-control" placeholder="Search..." />
                                    <span className="input-group-btn">
                                        <button className="btn btn-default" type="button"><i className="ti-search" /> </button>
                                    </span>
                                </div>
                            </li>
                            {this.state.clicScolarite == true ?
                                (
                                    <Fragment>
                                        {(this.state.permissionEnseignants.ajouter == 1 || this.state.permissionEnseignants.explorer == 1 || this.state.permissionEnseignants.modifier == 1 || this.state.permissionEnseignants.supprimer == 1) ?
                                            (<li>
                                                <Link to="/Enseignants" className="waves-effect" ><i class="fas fa-chalkboard-teacher"></i> Enseignants</Link>
                                            </li>) : (null)}
                                        <li>
                                            <Link to="/ModPaiEnseignants" className="waves-effect" ><i class="fas fa-chalkboard-teacher"></i> Paiement enseignants</Link>
                                        </li>
                                        {(this.state.permissionMatFor.ajouter == 1 || this.state.permissionMatFor.explorer == 1 || this.state.permissionMatFor.modifier == 1 || this.state.permissionMatFor.supprimer == 1) ?
                                            (<li>
                                                <Link to="/MatieresFormat" className="waves-effect" ><i class="fas fa-graduation-cap"></i> Matieres par formation</Link>
                                            </li>) : (null)}
                                        {(this.state.permissionNivFor.ajouter == 1 || this.state.permissionNivFor.explorer == 1 || this.state.permissionNivFor.modifier == 1 || this.state.permissionNivFor.supprimer == 1) ?
                                            (<li>
                                                <Link to="/FormationNiv" className="waves-effect" ><i class="fas fa-list-ol"></i> Formations</Link>
                                            </li>) : (null)}
                                        {(this.state.permissionSpecialites.ajouter == 1 || this.state.permissionSpecialites.explorer == 1 || this.state.permissionSpecialites.modifier == 1 || this.state.permissionSpecialites.supprimer == 1) ?
                                            (<li>
                                                <Link to="/Specialites" className="waves-effect" ><i class="fas fa-chalkboard"></i> Specialités</Link>
                                            </li>) : (null)}
                                        {(this.state.permissionMatieres.ajouter == 1 || this.state.permissionMatieres.explorer == 1 || this.state.permissionMatieres.modifier == 1 || this.state.permissionMatieres.supprimer == 1) ?
                                            (<li>
                                                <Link to="/Matieres" className="waves-effect" ><i class="fas fa-book-open"></i> Matieres</Link>
                                            </li>) : (null)}
                                        {(this.state.permissionFilieres.ajouter == 1 || this.state.permissionFilieres.explorer == 1 || this.state.permissionFilieres.modifier == 1 || this.state.permissionFilieres.supprimer == 1) ?
                                            (<li>
                                                <Link to="/Filieres" className="waves-effect" ><i class="fas fa-globe-africa"></i> Filiéres</Link>
                                            </li>) : (null)}
                                        {(this.state.permissionNivBase.ajouter == 1 || this.state.permissionNivBase.explorer == 1 || this.state.permissionNivBase.modifier == 1 || this.state.permissionNivBase.supprimer == 1) ?
                                            (<li>
                                                <Link to="/NivBase" className="waves-effect" ><i class="fas fa-book"></i> Niveau de base</Link>
                                            </li>) : (null)}
                                        {(this.state.permissionTypeEnseign.ajouter == 1 || this.state.permissionTypeEnseign.explorer == 1 || this.state.permissionTypeEnseign.modifier == 1 || this.state.permissionTypeEnseign.supprimer == 1) ?
                                            (<li>
                                                <Link to="/TypeEnseign" className="waves-effect" ><i class="fas fa-edit"></i> Type enseignement</Link>
                                            </li>) : (null)}
                                    </Fragment>) : (null)}
                            {this.state.clicSession == true ?
                                (
                                    <Fragment>
                                        {(this.state.permissionGestionRoles.ajouter == 1 || this.state.permissionGestionRoles.explorer == 1 || this.state.permissionGestionRoles.modifier == 1 || this.state.permissionGestionRoles.supprimer == 1) ?
                                            (<li>
                                                <Link to="/Roles" className="waves-effect" ><i class="fas fa-user-edit"></i> Gestion de role</Link>
                                            </li>) : (null)}
                                        {(this.state.permissionGestionUtilisateurs.ajouter == 1 || this.state.permissionGestionUtilisateurs.explorer == 1 || this.state.permissionGestionUtilisateurs.modifier == 1 || this.state.permissionGestionUtilisateurs.supprimer == 1) ?
                                            (<li>
                                                <Link to="/Users" className="waves-effect" ><i class="fas fa-users"></i> Gestion d'utilisateur</Link>
                                            </li>) : (null)}
                                    </Fragment>) : (null)}
                        </ul>
                    </div>
                    {/* /.sidebar-collapse */}
                </div>
                {this.props.children}
            </div>
        );
    }
}

export default withRouter(StatiquePage);
