import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import axios from 'axios'
import jQuery from 'jquery'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class ExplFeedBackClient extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idTravailleur: '',
            nomTravailleur: '',
            adresse: '',
            telephone: '',
            email: '',
            nomService: '',
            listEtat: [{ value: 0, label: 'Invalide' }, { value: 1, label: 'Valide' }],
            etat: ''
        }
        this.modif = this.modif.bind(this);
    }

    componentDidMount() {
        let id_travailleur = this.props.location.state.id_travailleur;
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/GetTravailleurById.php?id=' + id_travailleur).then(res => {
            console.log(res)
            this.setState({
                idTravailleur: res.data[0][0].id_travailleur,
                nomTravailleur: res.data[0][0].nomTravailleur,
                adresse: res.data[0][0].adresse,
                telephone: res.data[0][0].telephone,
                email: res.data[0][0].email,
                nomService: res.data[0][0].nomService,
                etat: res.data[1]
            })
            console.log(this.state.etat)
        })
    }

    modifEtatTravailleur = etat => {
        this.setState({ etat });
        console.log(`Etat selected:`, etat);
    };

    modif() {
        console.log(this.state.etat.value)

        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_snaii/UpdEtatTravailleur.php?id=' + this.state.idTravailleur,
            type: 'POST',
            data: {
                etatTravailleur: this.state.etat.value
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    toast.success('✅ Félicitation la modification de l\'état du travailleur a été effectuée avec succès !', { containerId: 'A' });
                }
                else if (code_html.Result == "KO") {
                    toast.error('⛔ Erreur,l\'un de vos champs est vide!', { containerId: 'A' });
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div style={{backgroundColor:'white'}} className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification etat travailleur</h4>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Etat travailleur</label>
                                <Select
                                    value={this.state.etat}
                                    isClearable={true}
                                    isSearchable={true}
                                    onChange={this.modifEtatTravailleur}
                                    options={this.state.listEtat}
                                />
                            </div>
                        </div>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif.bind(this)}>Confirmer</button>
                    </div>

                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Informations travailleur</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Nom travailleur</label>
                                <input type="text" disabled Value={this.state.nomTravailleur} class="form-control" style={{backgroundColor:'white',borderRadius:'5px'}}/>
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Adresse</label>
                                <input type="text" disabled Value={this.state.adresse} class="form-control" style={{backgroundColor:'white',borderRadius:'5px'}}/>
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Telephone</label>
                                <input type="text" disabled Value={this.state.telephone} class="form-control" style={{backgroundColor:'white',borderRadius:'5px'}}/>
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Email</label>
                                <input type="text" disabled Value={this.state.email} class="form-control" style={{backgroundColor:'white',borderRadius:'5px'}}/>
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Nom service</label>
                                <input type="text" disabled Value={this.state.nomService} class="form-control" style={{backgroundColor:'white',borderRadius:'5px'}}/>
                            </div>
                        </div>
                    </div>
                    <Link to="/Travailleurs">
                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '20px' }}>
                            <button style={{ width: '15%', backgroundColor: '#0083FF', borderColor: '#0083FF', borderRadius: '5px' }} type="button" className="btn btn-success">Retour</button>
                        </div>
                    </Link>
                </div>
                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={4000} />
            </div>
        );
    }
}

export default ExplFeedBackClient;
