                    email: this.state.email,
                    tel: this.state.tel,
                    facebook: this.state.facebook,
                    fctActu: this.state.fonction,
                    pathPhoto: pathPhotoVerso,
                    typeFormat: this.state.selectedTypeFormat,
                    typeFiche: this.state.selectedTypeFiche,
                    motivation: this.state.motivation,
                    connaissChoisi: this.state.connaissChoisi,
                    cycleChoisi: detailCycles,
                    anneeScolaire: anneeScolaire,
                    userId: userId
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.setState({
                            ajoutInscripCycle: true
                        });
                    }
                    else {
                        alert("prbl lors de l'insertion")
                    }
                }
            });
        }
        // Test si photo vide
        if (this.state.photoVerso != null) {
            let files = this.state.photoVerso;
            console.warn('data files', files)
            let reader = new FileReader();
            reader.readAsDataURL(files[0])
            reader.onload = (e) => {
                console.log(e)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UploadphotoInscripCycle.php',
                    type: 'POST',
                    data: {
                        file: e.target.result,
                        nomfile: this.state.photoVerso[0].name
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                    }
                }
                )
            }
        }
