import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable, { createTheme } from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Collapsible from 'react-collapsible';
import Select from 'react-select';

class ReglDiplInt extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Nom et prÃ©nom',
                    selector: 'nomComplet',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Reglement',
                    cell: row => <div onClick={() => this.reglElv(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px' }}><p>Reglement</p></div></div>
                    ,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true
                }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            pending: true,
            regElv: [],
            // search
            eleve: [],
            selectedElv: '',
            groupe: [],
            selectedGroupe: '',
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsDiplInt.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectElevesDipl.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        eleve: code_html
                    });
                }
            });
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupeElvDiplInt.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        groupe: code_html
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    reglElv = (row) => {
        this.setState({
            regElv: row.reglInscrip
        }, () => {
            console.log(this.state.regElv.detailRegl)
            this.setState(prevState => ({
                modal: !prevState.modal,
            }));
        });
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal,
        }));
    }

    newElement = (page, totalRows) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedElv != '') {
            var idElv = this.state.selectedElv.value
        }
        else {
            var idElv = ''
        }
        if (this.state.selectedGroupe != '') {
            var idGroupe = this.state.idGroupe.value
        }
        else {
            var idGroupe = ''
        }
        if ((idElv == '') && (idGroupe == '')) {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsDiplInt.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsDiplIntSearch.php',
                        type: 'POST',
                        data: {
                            eleveId: idElv,
                            groupeId: idGroupe,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    perPage = (currentRowsPerPage, currentPage) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedElv != '') {
            var idElv = this.state.selectedElv.value
        }
        else {
            var idElv = ''
        }
        if (this.state.selectedGroupe != '') {
            var idGroupe = this.state.idGroupe.value
        }
        else {
            var idGroupe = ''
        }
        if ((idElv == '') && (idGroupe == '')) {
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsDiplInt.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: currentRowsPerPage,
                            page: currentPage,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsDiplIntSearch.php',
                        type: 'POST',
                        data: {
                            eleveId: idElv,
                            groupeId: idGroupe,
                            limit: currentRowsPerPage,
                            page: currentPage,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    filterSearch = (event) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsDiplInt.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    searchElv = (selectedElv) => {
        if (selectedElv != null) {
            this.setState({ selectedElv: selectedElv });
        }
        else {
            this.setState({ selectedElv: '' });
        }
    }

    searchGroupe = selectedGroupe => {
        if (selectedGroupe != null) {
            this.setState({ selectedGroupe: selectedGroupe });
        }
        else {
            this.setState({ selectedGroupe: '' });
        }
    };

    recherche = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedElv != '') {
            var idElv = this.state.selectedElv.value
        }
        else {
            var idElv = ''
        }
        if (this.state.selectedGroupe != '') {
            var idGroupe = this.state.idGroupe.value
        }
        else {
            var idGroupe = ''
        }
        this.setState({
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsDiplIntSearch.php',
                    type: 'POST',
                    data: {
                        eleveId: idElv,
                        groupeId: idGroupe,
                        anneeScolaire: anneeScolaire,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    afficherTout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsDiplInt.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html.data,
                    pending: false
                });
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Reglement diplome internationale</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <h4 style={{ paddingLeft: '10px' }}>CritÃ¨res de recherche</h4>
                                    <Fragment>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Etudiant</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchElv}
                                                    options={this.state.eleve}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Groupe</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchGroupe}
                                                    options={this.state.groupe}
                                                />
                                            </div>
                                        </div>
                                    </Fragment>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Reglement etudiant</p></div></div></ModalHeader>
                    <ModalBody>
                        <div>
                            <table className="table">
                                <thead >
                                    <tr>
                                        <th style={{ textAlign: 'center' }} scope="col">Etat</th>
                                    </tr>
                                </thead>
                                {this.state.regElv.map((el, index) =>
                                    <tbody>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}><div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}><p>{el.etat}</p><p style={{ paddingLeft: '5px' }}>{el.mntNonRegle}</p></div>
                                                <Collapsible trigger={<button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }}><i style={{ color: 'white' }} className="fas fa-info"></i></button>}>
                                                    <Fragment>
                                                        <table className="table table-bordered">
                                                            <thead >
                                                                <tr>
                                                                    <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Date</th>
                                                                    <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Montant</th>
                                                                    <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Reglement</th>
                                                                </tr>
                                                            </thead>
                                                            {el.detailRegl.map(ele =>
                                                                <tbody>
                                                                    <tr>
                                                                        <td style={{ textAlign: 'center', fontSize: '10px' }}><p>{ele.date}</p></td>
                                                                        <td style={{ textAlign: 'center', fontSize: '10px' }}><p>{ele.totalPay}</p></td>
                                                                        <td style={{ textAlign: 'center', fontSize: '10px' }}>
                                                                            {ele.infoPay.map(elem =>
                                                                                elem.typePay == "ChÃ©que" ?
                                                                                    (<Fragment><p>{elem.typePay}</p><p style={{ fontSize: '9px' }}>{'EchÃ©ance : ' + elem.echeance}</p></Fragment>) : (<p>{elem.typePay}</p>)
                                                                            )}
                                                                        </td>
                                                                    </tr>
                                                                </tbody>
                                                            )}
                                                        </table>
                                                    </Fragment>
                                                </Collapsible>
                                            </td>
                                        </tr>
                                    </tbody>
                                )}
                            </table>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white', width: '40px ' }}><i style={{ color: 'white' }} className="fas fa-sign-out-alt"></i></button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}
export default ReglDiplInt;
