import React, { Component } from 'react'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'

class AjoutUnite extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomUnite: ''
        }
        this.addNomUnite = this.addNomUnite.bind(this);
        this.ajout = this.ajout.bind(this)
    }

    addNomUnite(event) {
        this.setState({
            nomUnite: event.target.value
        });
    }

    ajout() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/AddUnite.php',
            type: 'POST',
            data: {
                nomUnite: this.state.nomUnite,
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Unites');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout unité</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Unité</label>
                                            <input type="text" class="form-control" placeholder="Unité" onChange={this.addNomUnite.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutUnite);
