import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class AjoutTypePayEleve extends Component {
    constructor(props) {
        super(props);
        this.state = {
            typePay: '',
            sousTypePay: [],
            choix: [],
            choixChecked: [],
            modal: false,
            indexLignASupprimer: ''
        }
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetChoixPay.php').then(res => {
            console.log(res)
            this.setState({
                choix: res.data
            })
        })
    }

    addTypePay = (event) => {
        this.setState({
            typePay: event.target.value
        });
    }

    additionLign = () => {
        let element = { sousType: '' }
        this.setState({
            sousTypePay: [...this.state.sousTypePay, element]
        })
    }

    addSousTypePay = (event, indexSousTypePay) => {
        let sousType = event.target.value;
        this.setState({
            sousTypePay: this.state.sousTypePay.map((el, id) => (id === indexSousTypePay ? Object.assign(el, { sousType: sousType }) : el))
        });
    }

    toggleLign = (indexLign) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignASupprimer: indexLign
        }));
    }

    deleteLign = () => {
        this.state.sousTypePay.splice(this.state.indexLignASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLign = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    addChoix = (e, el, index) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.id
            this.setState({
                choixChecked: [...this.state.choixChecked, element]
            });
        }
        else {
            this.state.choixChecked.splice(index, 1)
        }
    }

    ajout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddTypePayEleve.php',
            type: 'POST',
            data: {
                typePay: this.state.typePay,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/TypesPaiementEleve');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout type paiement</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Type paiement</label>
                                            <input type="text" class="form-control" placeholder="Type paiement" onChange={this.addTypePay} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <table style={{ marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Sous type paiement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.sousTypePay.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.sousType} onChange={(e) => this.addSousTypePay(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '150px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '10%' }} className='buttonSupprimer' onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px', marginBottom: '10px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Choix</label>
                                        <span style={{ width: '161px', display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', border: '1px solid', padding: '10px', borderRadius: '5px' }}>
                                            {this.state.choix.map((el, index) =>
                                                <label>
                                                    <Checkbox style={{ paddingRight: '5px' }}
                                                        onChange={(e) => this.addChoix(e, el, index)}
                                                        disabled={this.state.disabled}
                                                    />
                                                    {el.choixPay}
                                                </label>
                                            )}
                                        </span>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteLign} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteLign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer cette ligne ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteLign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteLign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutTypePayEleve);
