import React, { Component } from "react";
import jQuery from "jquery";
import Select from "react-select";
import "react-toastify/dist/ReactToastify.css";
import DataTable from "react-data-table-component";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class ListNote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      columns: [
        {
          name: 'Modifier',
          cell: row => <Link to={{
            pathname: '/ModifNoteNonBts',
            state: { idNote: row.idNote }
          }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
          ignoreRowClick: true,
          allowOverflow: true,
          button: true
        },
      ],
      listNiveau: [],
      listGroupes: [],
      listMatieres: [],
      listSousMat: [],
      listNatureNote: [],
      listTrimestre: [
        { value: 1, label: "Trimestre 1" },
        { value: 2, label: "Trimestre 2" },
        { value: 3, label: "Trimestre 3" },
      ],
      listEleves: [],
      selectedGroupe: "",
      selectedNiveau: "",
      selectedMatiere: "",
      selectedSousMat: "",
      selectedTrimestre: "",
      blockSousMatiere: false,
      dateAjoutNote: new Date(),
      nombreTotalElement: 0,
      filterElement: "",
      mycurrentPage: 1,
      resetFirstPage: false,
      nbrEltperPage: 5,
      modal: false,
      pending: true,
      blockDataTable: false
    };
    this.newElement = this.newElement.bind(this);
    // this.filterSearch = this.filterSearch.bind(this);
  }

  componentDidMount() {
    if (JSON.parse(localStorage.getItem("userPermissions")) != null) {
      var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
      var anneeScolaire = userPermissions[0].anneeScolaire;
      jQuery.ajax({
        url:
          process.env.REACT_APP_API_URL +
          "Back_mediatraining/SelectNiveauNonBts.php",
        type: "POST",
        data: {
          anneeScolaire: anneeScolaire,
        },
        dataType: "json",
        success: (code_html, statut) => {
          this.setState({
            listNiveau: code_html,
          });
        },
      });
    } else {
      document.location = "/";
    }
  }

  addNiveau = (selectedNiveau) => {
    var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
    var anneeScolaire = userPermissions[0].anneeScolaire;
    if (selectedNiveau != null) {
      this.setState({ selectedNiveau }, () => {
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/SelectedGroupe.php",
          type: "POST",
          data: {
            niveauId: selectedNiveau.value,
            anneeScolaire: anneeScolaire,
          },
          dataType: "json",
          success: (code_html, statut) => {
            this.setState({
              listGroupes: code_html,
            });
          },
        });
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/SelectedMatieres.php",
          type: "POST",
          data: {
            niveauId: selectedNiveau.value,
            anneeScolaire: anneeScolaire,
          },
          dataType: "json",
          success: (code_html, statut) => {
            this.setState({
              listMatieres: code_html,
            });
          },
        });
      });
    } else {
      this.setState({
        listGroupes: "",
        listMatieres: ""
      });
    }
  };

  addGroup = (selectedGroupe) => {
    if (selectedGroupe != null) {
      this.setState({ selectedGroupe: selectedGroupe }, () => { });
    } else {
      this.setState({
        selectedGroupe: "",
      });
    }
  };

  addMatiere = (selectedMatiere) => {
    var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
    var anneeScolaire = userPermissions[0].anneeScolaire;
    if (selectedMatiere != null) {
      this.setState({ selectedMatiere: selectedMatiere }, () => {
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/SelectedSousMat.php",
          type: "POST",
          data: {
            matiereId: selectedMatiere.value,
            anneeScolaire: anneeScolaire,
          },
          dataType: "json",
          success: (code_html, statut) => {
            this.setState({
              listSousMat: code_html,
            });
          },
        });
      });
    } else {
      this.setState({
        listSousMat: [],
        selectedMatiere: "",
      });
    }
  };

  addTrimestre = (selectedTrimestre) => {
    if (selectedTrimestre != null) {
      this.setState({ selectedTrimestre: selectedTrimestre });
    }
    else {
      this.setState({
        selectedTrimestre: ''
      });
    }
  };

  addSousMat = (selectedSousMat) => {
    if (selectedSousMat != '') {
      this.setState({ selectedSousMat: selectedSousMat });
    }
    else {
      this.setState({
        selectedSousMat: ''
      });
    }
  };

  rechercher = () => {
    if (this.state.selectedNiveau != '' && this.state.selectedGroupe != '' && this.state.selectedMatiere != '' && this.state.selectedTrimestre != '') {
      if (this.state.listSousMat.length == 0) {
        if (this.state.selectedSousMat != '') {
          var idSousMat = this.state.selectedSousMat.value
        }
        else {
          var idSousMat = -1
        }
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/ListNoteNonBts.php",
          type: "POST",
          data: {
            matiereId: this.state.selectedMatiere.value,
            sousMatId: idSousMat,
            groupeId: this.state.selectedGroupe.value,
            trimId: this.state.selectedTrimestre.value,
            query: "",
            limit: this.state.nbrEltperPage,
            page: 1,
          },
          dataType: "json",
          success: (code_html, statut) => {
            console.log(code_html);
            var arrayAncColumns = [{
              name: 'Modifier',
              cell: row => <Link to={{
                pathname: '/ModifNoteNonBts',
                state: { idNote: row.idNote }
              }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
              ignoreRowClick: true,
              allowOverflow: true,
              button: true
            }]
            var arrayRecColumns = code_html.columns
            var arrayColumns = []
            var arrayColumns = arrayRecColumns.concat(arrayAncColumns);
            this.setState({
              nombreTotalElement: code_html.total,
              columns: arrayColumns,
              data: code_html.data,
              pending: false,
              blockDataTable: true
            });
          },
        });
      }
      else {
        if (this.state.selectedSousMat != '') {
          jQuery.ajax({
            url:
              process.env.REACT_APP_API_URL +
              "Back_mediatraining/ListNoteNonBts.php",
            type: "POST",
            data: {
              matiereId: this.state.selectedMatiere.value,
              sousMatId: idSousMat,
              groupeId: this.state.selectedGroupe.value,
              trimId: this.state.selectedTrimestre.value,
              query: "",
              limit: this.state.nbrEltperPage,
              page: 1,
            },
            dataType: "json",
            success: (code_html, statut) => {
              console.log(code_html);
              var arrayAncColumns = [{
                name: 'Modifier',
                cell: row => <Link to={{
                  pathname: '/ModifNoteNonBts',
                  state: { idNote: row.idNote }
                }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                ignoreRowClick: true,
                allowOverflow: true,
                button: true
              }]
              var arrayRecColumns = code_html.columns
              var arrayColumns = []
              var arrayColumns = arrayRecColumns.concat(arrayAncColumns);
              this.setState({
                nombreTotalElement: code_html.total,
                columns: arrayColumns,
                data: code_html.data,
                pending: false,
                blockDataTable: true
              });
            },
          });
        }
        else {
          toast.error('⛔ Veuillez verifier les critéres de recherche !!', { containerId: 'A' });
        }
      }
    }
    else {
      toast.error('⛔ Veuillez verifier les critéres de recherche !!', { containerId: 'A' });
    }
  }

  newElement(page, totalRows) {
    if (this.state.selectedNiveau != '' && this.state.selectedGroupe != '' && this.state.selectedMatiere != '' && this.state.selectedTrimestre != '') {
      if (this.state.listSousMat.length == 0) {
        if (this.state.selectedSousMat != '') {
          var idSousMat = this.state.selectedSousMat.value
        }
        else {
          var idSousMat = -1
        }
        this.setState(
          {
            pending: true,
          },
          () => {
            jQuery.ajax({
              url:
                process.env.REACT_APP_API_URL +
                "Back_mediatraining/ListNoteNonBts.php",
              type: "POST",
              data: {
                matiereId: this.state.selectedMatiere.value,
                sousMatId: idSousMat,
                groupeId: this.state.selectedGroupe.value,
                trimId: this.state.selectedTrimestre.value,
                query: this.state.filterElement,
                limit: this.state.nbrEltperPage,
                page: page,
              },
              dataType: "json",
              success: (code_html, statut) => {
                console.log(code_html);
                var arrayAncColumns = [{
                  name: 'Modifier',
                  cell: row => <Link to={{
                    pathname: '/ModifNoteNonBts',
                    state: { idNote: row.idNote }
                  }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                  ignoreRowClick: true,
                  allowOverflow: true,
                  button: true
                }]
                var arrayRecColumns = code_html.columns
                var arrayColumns = []
                var arrayColumns = arrayRecColumns.concat(arrayAncColumns);
                this.setState({
                  nombreTotalElement: code_html.total,
                  columns: arrayColumns,
                  data: code_html.data,
                  pending: false,
                  blockDataTable: true
                });
              },
            });
          }
        );
      }
      else {
        if (this.state.selectedSousMat != '') {
          jQuery.ajax({
            url:
              process.env.REACT_APP_API_URL +
              "Back_mediatraining/ListNoteNonBts.php",
            type: "POST",
            data: {
              matiereId: this.state.selectedMatiere.value,
              sousMatId: idSousMat,
              groupeId: this.state.selectedGroupe.value,
              trimId: this.state.selectedTrimestre.value,
              query: "",
              limit: this.state.nbrEltperPage,
              page: 1,
            },
            dataType: "json",
            success: (code_html, statut) => {
              console.log(code_html);
              var arrayAncColumns = [{
                name: 'Modifier',
                cell: row => <Link to={{
                  pathname: '/ModifNoteNonBts',
                  state: { idNote: row.idNote }
                }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                ignoreRowClick: true,
                allowOverflow: true,
                button: true
              }]
              var arrayRecColumns = code_html.columns
              var arrayColumns = []
              var arrayColumns = arrayRecColumns.concat(arrayAncColumns);
              this.setState({
                nombreTotalElement: code_html.total,
                columns: arrayColumns,
                data: code_html.data,
                pending: false,
                blockDataTable: true
              });
            },
          });
        }
        else {
          toast.error('⛔ Veuillez verifier les critéres de recherche !!', { containerId: 'A' });
        }
      }
    }
    else {
      toast.error('⛔ Veuillez verifier les critéres de recherche !!', { containerId: 'A' });
    }
  }

  perPage(currentRowsPerPage, currentPage) {
    if (this.state.selectedNiveau != '' && this.state.selectedGroupe != '' && this.state.selectedMatiere != '' && this.state.selectedTrimestre != '') {
      if (this.state.listSousMat.length == 0) {
        if (this.state.selectedSousMat != '') {
          var idSousMat = this.state.selectedSousMat.value
        }
        else {
          var idSousMat = -1
        }
        this.setState(
          {
            nbrEltperPage: currentRowsPerPage,
            pending: true,
          },
          () => {
            jQuery.ajax({
              url:
                process.env.REACT_APP_API_URL +
                "Back_mediatraining/ListNoteNonBts.php",
              type: "POST",
              data: {
                matiereId: this.state.selectedMatiere.value,
                sousMatId: idSousMat,
                groupeId: this.state.selectedGroupe.value,
                trimId: this.state.selectedTrimestre.value,
                query: this.state.filterElement,
                limit: currentRowsPerPage,
                page: currentPage,
              },
              dataType: "json",
              success: (code_html, statut) => {
                console.log(code_html);
                var arrayAncColumns = [{
                  name: 'Modifier',
                  cell: row => <Link to={{
                    pathname: '/ModifNoteNonBts',
                    state: { idNote: row.idNote }
                  }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                  ignoreRowClick: true,
                  allowOverflow: true,
                  button: true
                }]
                var arrayRecColumns = code_html.columns
                var arrayColumns = []
                var arrayColumns = arrayRecColumns.concat(arrayAncColumns);
                this.setState({
                  nombreTotalElement: code_html.total,
                  columns: arrayColumns,
                  data: code_html.data,
                  pending: false,
                  blockDataTable: true
                });
              },
            });
          }
        );
      }
      else {
        if (this.state.selectedSousMat != '') {
          jQuery.ajax({
            url:
              process.env.REACT_APP_API_URL +
              "Back_mediatraining/ListNoteNonBts.php",
            type: "POST",
            data: {
              matiereId: this.state.selectedMatiere.value,
              sousMatId: idSousMat,
              groupeId: this.state.selectedGroupe.value,
              trimId: this.state.selectedTrimestre.value,
              query: "",
              limit: this.state.nbrEltperPage,
              page: 1,
            },
            dataType: "json",
            success: (code_html, statut) => {
              console.log(code_html);
              var arrayAncColumns = [{
                name: 'Modifier',
                cell: row => <Link to={{
                  pathname: '/ModifNoteNonBts',
                  state: { idNote: row.idNote }
                }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                ignoreRowClick: true,
                allowOverflow: true,
                button: true
              }]
              var arrayRecColumns = code_html.columns
              var arrayColumns = []
              var arrayColumns = arrayRecColumns.concat(arrayAncColumns);
              this.setState({
                nombreTotalElement: code_html.total,
                columns: arrayColumns,
                data: code_html.data,
                pending: false,
                blockDataTable: true
              });
            },
          });
        }
        else {
          toast.error('⛔ Veuillez verifier les critéres de recherche !!', { containerId: 'A' });
        }
      }
    }
    else {
      toast.error('⛔ Veuillez verifier les critéres de recherche !!', { containerId: 'A' });
    }
  }

  // filterSearch(event) {
  //   this.setState(
