import React, { Component } from "react";
import jQuery from "jquery";
import { withRouter } from "react-router-dom";
import "./paginate.css";

class ImprimeDevis extends Component {
  constructor(props) {
    super(props);
    
    this.state = {
        data: [],
        email:"",
        nomClient:"",
        idDevis:"",
        nombreTotalElement: 0,
      filterElement: "",
      mycurrentPage: 1,
      resetFirstPage: false,
      nbrEltperPage: 5,
      modal: false,
      idEnseignant: "",
      pending: true,
      detailEnseign: [],
      nomEnseign: "",
      mois: "",
      nbrPratique: "",
      nbrTheorique: "",
      nbrCycle: "",
      modalModif: false,
      ligneModif: [],
      groupeEnseign: "",
      departSeance: "",
      finSeance: "",
      dateSeance: "",
      idEnteteAbs: [],
      nbrHeureParMois:0,
      tab: [],
      tabs: [],
      detaildevis:[],
     /*declaration titre*/
      x:1,

    };
  };


  componentDidMount() {
    const searchParams = new URLSearchParams(window.location.search);
        var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
      var url = window.location.href;
      var array = url.split("=");
      var params = array[1];
      var arrayInfo = params.split("-");
      var id= arrayInfo[0];
     
  
     
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_hooda/GetDevis.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html.data,
                    nomClient:code_html.data.nomClient,
                    pending: false
                });
            }
        });
        
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL + "Back_hooda/getdetaildevisbyid.php",
          type: "POST",
          data: {
            id: id,
          
          },
          dataType: "json",
          success: (code_html, statut) => {
            console.log(code_html);
            this.setState(
              {
                detaildevis: code_html,
                nomclient: code_html[0].nomClient,
                email:code_html[0].email,
                idDevis:code_html[0].idDevis
              },
             
            );
          },
        });
  }      
  impr = () => {
    window.print();
  };


  render() {
    
    const Prix = this.state.detaildevis.reduce((acc, ele) => acc + ele.Montant_article*1, 0);
    return (
      <div className="white-box">
        <div
          style={{
            textAlign: "center",
            display: "flex",
            justifyContent: "center",marginTop: "2%"
          }}
          className="row"
        >
          <div className="form-group">
            <img src="images/hooda.jpg" alt="" width={"150px"} />
          </div>
          
        </div>
      
        <p style={{ display: "flex", paddingLeft: "80px" }}>
           <strong>
              <p >

                <strong> 
                    <p style={{paddingLeft:"90px"}}>Abidjan le, {new Date().toLocaleString("fr-FR", { day : '2-digit'})} {new Date().toLocaleString("fr-FR",{ month: "long" })} {new Date().getFullYear()}</p>
                    {this.state.nomEnseign.replace(/%20/g, " ")} 
                </strong>
              </p>
            <p style={{ paddingLeft: "90px" }}>Devis N° : {this.state.idDevis}</p>
            <p style={{ paddingLeft: "90px" }}>Contact :  {this.state.email}</p> 
            {this.state.nomEnseign.replace(/%20/g, " ")} </strong>
        </p>
        <p style={{ display: "flex", justifyContent: "center", marginTop: "1%"}}>
                       
        </p>
        <p style={{ textAlign: "center"}}><strong> Hooda Graphics Numérique vous remercie de votre demande et vous prie de bien vouloir trouver notre meilleure proposition pour:</strong></p>
        <div style={{ display: "center", justifyContent: "flex-end", marginTop: "1%",marginLeft:'15%'}}>
          
                
                  {this.state.detaildevis.map((ele, id) => (
                      
                      <table style={{ border: "solid", width: "80%", color: "black",marginTop:'30px' }}>
                        <thead>
                          <tr style={{  border: "solid", borderColor: "black" }}>
                            <th style={{ textAlign: "center", border: "solid", borderBottom: "none", color: "black", borderColor: "black" }}>
                              <strong style={{color:'blue',fontSize:'25px',fontFamily:'Open Sans, Arial, sans-serif'}}>{ele.nomarticle} - {ele.quantite} exemplaire</strong>
                            </th>
                          </tr>
                          </thead>
                        <tbody>
                          <tr >
                            {ele.format!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}} ><p style={{paddingRight:'20px'}}>Format:</p> 
                            
                            <p style={{paddingLeft:'20px'}}>{ele.format}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                          {ele.poids!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}}><p style={{paddingRight:'20px'}}>Poids:</p> 
                              <p style={{paddingLeft:'20px'}}>{ele.poids}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                          {ele.papier!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}}><p style={{paddingRight:'20px'}}>Papier:</p> 
                              <p style={{paddingLeft:'20px'}}>{ele.papier}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                          {ele.prepresse!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}}><p style={{paddingRight:'20px'}}>Prépresse:</p> 
                              <p style={{paddingLeft:'20px'}}>{ele.prepresse}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                          {ele.impression!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}}><p style={{paddingRight:'20px'}}>Impression:</p> 
                              <p style={{paddingLeft:'20px'}}>{ele.impression}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                          {ele.finition!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}}><p style={{paddingRight:'20px'}}>Finition:</p> 
                              <p style={{paddingLeft:'20px'}}>{ele.finition}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                          {ele.faconnage!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}}><p style={{paddingRight:'20px'}}>Façonnage:</p> 
                              <p style={{paddingLeft:'20px'}}>{ele.faconnage}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                          {ele.paquetage!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}}><p style={{paddingRight:'20px'}}>Paquetage:</p> 
                              <p style={{paddingLeft:'20px'}}>{ele.paquetage}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                          {ele.livraison!=null?( 
                              <td style={{display:'flex',justifyContent:'flex-start'}}><p style={{paddingRight:'20px'}}>Livraison:</p> 
                              <p style={{paddingLeft:'20px'}}>{ele.livraison}</p>
                            </td>
                          ):null}
                          </tr>
                          <tr >
                             <td style={{ display: "flex", justifyContent: "flex-end",paddingRight:'200px', border: "solid",borderBottom:'none',borderColor:'black',borderLeft:'none',borderRight:'none'}}>
                             <strong style={{paddingRight:'20px',color:'blue',fontSize:'20px',fontFamily:'Open Sans, Arial, sans-serif'}}>Pour  {ele.quantite} exemplaire H.T</strong>
                             </td>
                          </tr>
                          <tr >
                             <td style={{ display: "flex", justifyContent: "flex-end",paddingRight:'105px'}}>
                             <p style={{color:'blue',fontSize:'15px',fontFamily:'Open Sans, Arial, sans-serif'}}>Prix unitaire:{ele.Montant_article}H.T</p>
                             </td>
                          </tr>
                        </tbody>
                      </table>   
                  ))}
                  
                  
                
          </div> 
          
                
          
   
       
        
       
        
        <div style={{ display: "flex", justifyContent: "center"  ,padding:"20px"}}>
          <i
            id="print_me_not"
            style={{ fontSize: "30px", cursor: "pointer" }}
            onClick={this.impr}
            class="fas fa-print"
          ></i>
        </div>
        
      </div>
    );
  }
}

export default withRouter (ImprimeDevis);
