import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import Collapsible from 'react-collapsible';

class CaisseUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: [],
            selectedUser: '',
            dateDebut: '',
            dateFin: '',
            arrayDet: [],
            blockDet: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectUser.php').then(res => {
                console.log(res)
                this.setState({
                    user: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    selectUser = (selectedUser) => {
        if (selectedUser != null) {
            this.setState({
                selectedUser: selectedUser
            }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/CaisseUser.php',
                    type: 'POST',
                    data: {
                        userId: selectedUser.value,
                        dateDebut: this.state.dateDebut,
                        dateFin: this.state.dateFin
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            arrayDet: code_html,
                            blockDet: true
                        });
                    }
                });
            });
        }
        else {
            this.setState({
                arrayDet: [],
                blockDet: false,
                selectedUser: ''
            });
        }
    }

    addDateDebut = d => {
        this.setState({
            dateDebut: d
        });
    };

    addDateFin = d => {
        this.setState({
            dateFin: d
        });
    };

    recherche = () => {
        if (this.state.selectedUser != '') {
            // convert date debut
            if (this.state.dateDebut != "") {
                let dateDebut = this.state.dateDebut
                let month = '' + (dateDebut.getMonth() + 1);
                let day = '' + dateDebut.getDate();
                let year = dateDebut.getFullYear();
                if (month.length < 2)
                    month = '0' + month;
                if (day.length < 2)
                    day = '0' + day;
                var convertdateDebut = [year, month, day].join('-');
            }
            else {
                var convertdateDebut = ""
            }
            //
            if (this.state.dateFin != "") {
                let dateFin = this.state.dateFin
                let month = '' + (dateFin.getMonth() + 1);
                let day = '' + dateFin.getDate();
                let year = dateFin.getFullYear();
                if (month.length < 2)
                    month = '0' + month;
                if (day.length < 2)
                    day = '0' + day;
                var convertdateFin = [year, month, day].join('-');
            }
            else {
                var convertdateFin = ""
            }
            //
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/CaisseUser.php',
                type: 'POST',
                data: {
                    userId: this.state.selectedUser.value,
                    dateDebut: convertdateDebut,
                    dateFin: convertdateFin
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        arrayDet: code_html
                    });
                }
            }
            )
        }
        else {

        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Caisse utilisateur</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Menu" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label>Utilisateur</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.selectUser(e)}
                                                options={this.state.user}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateDebut}
                                                onChange={this.addDateDebut}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label>Date fin</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateFin}
                                                onChange={this.addDateFin}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                    <div style={{ textAlign: 'center' }}>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    {this.state.blockDet == true ? (
                                        <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                            <Collapsible style={{width:'50%'}} trigger={<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}><button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }}><i style={{ color: 'white' }} className="fas fa-info"></i></button><h2>Encaissement</h2></div>}>
                                                <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                    <thead>
                                                        <tr>
                                                            <th className="col-md-6" scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Date</th>
                                                            <th className="col-md-4" scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Montant</th>
                                                            <th className="col-md-2" scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Mode</th>
                                                        </tr>
                                                    </thead>
                                                    {this.state.arrayDet.map(el =>
                                                        <tbody>
                                                            <tr>
                                                                <td className="col-md-6" style={{ textAlign: 'center' }}>{el.date}</td>
                                                                <td className="col-md-4" style={{ textAlign: 'center' }}>{el.montant}</td>
                                                                <td className="col-md-2" style={{ textAlign: 'center' }}>{el.mode}</td>
                                                            </tr>
                                                        </tbody>
                                                    )}
                                                </table>
                                                <table className="table table-bordered">
                                                    <thead>
                                                        <tr style={{ backgroundColor: '#f2f2f2' }}>
                                                            <th className="col-md-6" style={{ textAlign: 'center', fontWeight: 'bold' }}>Total</th>
                                                            <th className="col-md-4"></th>
                                                            <th className="col-md-2"></th>
                                                        </tr>
                                                    </thead>
                                                </table>
                                            </Collapsible>
                                            <Collapsible style={{width:'50%'}} trigger={<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}><button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }}><i style={{ color: 'white' }} className="fas fa-info"></i></button><h2>Decaissement</h2></div>}>
                                                <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                    <thead>
                                                        <tr>
                                                            <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Montant</th>
                                                            <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Date</th>
                                                            <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Mode</th>
                                                        </tr>
                                                    </thead>
                                                    {this.state.arrayDet.map(el =>
                                                        <tbody>
                                                            <tr>
                                                                <td style={{ textAlign: 'center' }}>{el.montant}</td>
                                                                <td style={{ textAlign: 'center' }}>{el.date}</td>
                                                                <td style={{ textAlign: 'center' }}>{el.mode}</td>
                                                            </tr>
                                                        </tbody>
                                                    )}
                                                </table>
                                            </Collapsible>
                                        </div>
                                    ) : (
                                            null
                                        )}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div >
        );
    }
}
export default withRouter(CaisseUser);
