import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class AjoutMagasin extends Component {
    constructor(props) {
        super(props);
        this.state = {
            enseignes: [],
            selectedEnseigne: '',
            magasin: '',
            code: '',
            tel: '',
            adresse: '',
            articles: [],
            selectedArticle: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectEnseignes.php').then(res => {
                console.log(res)
                this.setState({
                    enseignes: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectArticles.php').then(res => {
                console.log(res)
                this.setState({
                    articles: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    addEnseigne = selectedEnseigne => {
        this.setState({ selectedEnseigne: selectedEnseigne });
    };

    addMagasin = (event) => {
        this.setState({
            magasin: event.target.value
        });
    }

    addCode = (event) => {
        this.setState({
            code: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            password: event.target.value
        });
    }

    // addAdresse = (event) => {
    // }

    addArticle = selectedArticle => {
        this.setState({ selectedArticle });
    };

    ajout = () => {
        if (this.state.selectedEnseigne != '' && this.state.magasin != '' && this.state.selectedArticle != '') {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/AddMagasin.php',
                type: 'POST',
                data: {
                    enseigneId: this.state.selectedEnseigne.value,
                    magasin: this.state.magasin,
                    code: this.state.code,
                    tel: this.state.tel,
                    // adresse: this.state.adresse,
                    arrayArticles: this.state.selectedArticle
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.props.history.push('/Magasins');
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
        else {
            toast.error('⛔ Veuillez verifier les champs enseigne,magasin et articles !!', { containerId: 'A' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout magasin</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Magasins" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Enseigne</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addEnseigne}
                                                options={this.state.enseignes}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Magasin</label>
                                            <input type="text" className="form-control" placeholder="Nom" onChange={this.addMagasin} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Code</label>
                                            <input type="text" className="form-control" placeholder="Nom" onChange={this.addCode} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Tel</label>
                                            <input type="text" className="form-control" placeholder="Nom" onChange={this.addTel} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Articles</label>
                                            <Select
                                                isMulti={true}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addArticle}
                                                options={this.state.articles}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutMagasin);
