import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class AjoutNiveau extends Component {
    constructor(props) {
        super(props);
        this.state = {
            formations: [],
            selectedFormation: '',
            nomNiveau: '',
            mntInitial: 0,
            niveaux: [{ duree: '', typePayEleve: '', mensuel: [], reduction: [], tranches: [] }],
            indexLignFormationASupprimer: '',
            modal: false,
            typesPayEleve: [],
            selectedTypePayEleve: '',
            reducFormat: 0,
            checked: false,
            tabChoix: [],
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFormations.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    formations: code_html
                });
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectTypesPayEleve.php').then(res => {
            console.log(res)
            this.setState({
                typesPayEleve: res.data
            })
        })
    }

    addFormation = selectedFormation => {
        this.setState({ selectedFormation });
        console.log(`Formation selected:`, selectedFormation);
    };

    addNomNiveau = (event) => {
        this.setState({
            nomNiveau: event.target.value
        });
    }

    addMntInitial = (event) => {
        this.setState({
            mntInitial: event.target.value
        });
    }

    additionLignFormation = () => {
        let element = { duree: '', typePayEleve: '', mensuel: [], reduction: [], tranches: [] }
        this.setState({
            niveaux: [...this.state.niveaux, element]
        })
    }

    addDuree = (event, indexNiveau) => {
        let duree = event.target.value;
        this.setState({
            niveaux: this.state.niveaux.map((el, id) => (id === indexNiveau ? Object.assign(el, { duree: duree }) : el))
        });
    }

    addTypePayEleve = (selectedTypePayEleve, indexTypePayEleve) => {
        if (selectedTypePayEleve != null) {
            let typePayEleve = selectedTypePayEleve;
            this.setState({
                selectedTypePayEleve: selectedTypePayEleve,
                niveaux: this.state.niveaux.map((el, id) => (id === indexTypePayEleve ? Object.assign(el, { typePayEleve: typePayEleve }) : el))
            }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetChoix.php?id=' + selectedTypePayEleve.value).then(res => {
                    console.log(res)
                    if (res.data.length === 1) {
                        var stateCopy = this.state.tabChoix;
                        stateCopy[indexTypePayEleve] = res.data;
                        this.setState({
                            tabChoix: stateCopy
                        }, () => {
                            console.log(this.state.tabChoix)
                            const posAnnee = document.getElementsByClassName('posAnnee');
                            console.log(posAnnee.length - 1)
                            let indexAnnuel = posAnnee.length - 1
                            let mntInitial = this.state.mntInitial
                            var stateCopy = this.state.tabChoix;
                            stateCopy[indexAnnuel][0].caze[0] = mntInitial;
                            this.setState({
                                tabChoix: stateCopy
                            });
                        })
                    }
                    else if (res.data.length > 0) {
                        var stateCopy = this.state.tabChoix;
                        stateCopy[indexTypePayEleve] = res.data;
                        this.setState({
                            tabChoix: stateCopy
                        }, () => {
                            console.log(this.state.tabChoix)
                        })
                    }
                    else {
                        var stateCopy = this.state.tabChoix;
                        stateCopy[indexTypePayEleve] = null;
                        this.setState({
                            tabChoix: stateCopy
                        }, () => {
                            console.log(this.state.tabChoix)
                        })
                    }
                })
            });
        }
    }

    add = (event, indexLignTab, indice, nomChoix) => {
        let include = this.state.tabChoix[indexLignTab][indice].idSousTypePayEleve
        console.log(include)
        if (include != undefined) {
            let mntTranche = event.target.value;
            var stateCopy = this.state.tabChoix;
            stateCopy[indexLignTab][indice].caze[indice] = mntTranche;
            this.setState({
                tabChoix: stateCopy
            });
        }
        else {
            if (nomChoix == "Nombre mois") {
                let nbrMois = event.target.value;
                var stateCopy = this.state.tabChoix;
                stateCopy[indexLignTab][indice].caze[indice] = nbrMois;
                this.setState({
                    tabChoix: stateCopy
                }, () => {
                    let montantInitiale = parseInt(this.state.mntInitial)
                    let nombreMois = parseInt(this.state.tabChoix[indexLignTab][indice].caze[indice])
                    if (montantInitiale > 0 && nombreMois > 0) {
                        let mntInitiale = parseInt(this.state.mntInitial);
                        let nbrMois = parseInt(this.state.tabChoix[indexLignTab][indice].caze[indice])
                        let mntCalcul = mntInitiale / nbrMois
                        var stateCopy = this.state.tabChoix;
                        stateCopy[indexLignTab][indice + 1].caze[indice + 1] = mntCalcul;
                        this.setState({
                            tabChoix: stateCopy
                        });
                    }
                });
            }
            else if (nomChoix == "Réduction %") {
                let pourcentageRed = event.target.value;
                var stateCopy = this.state.tabChoix;
                stateCopy[indexLignTab][indice].caze[indice] = pourcentageRed;
                this.setState({
                    tabChoix: stateCopy
                }, () => {
                    let montantInitiale = parseInt(this.state.mntInitial)
                    let pourcentageReduction = parseInt(this.state.tabChoix[indexLignTab][indice].caze[indice])
                    if (montantInitiale > 0 && pourcentageReduction > 0) {
                        let mntInitiale = parseInt(this.state.mntInitial);
                        let pourcentageRed = parseInt(this.state.tabChoix[indexLignTab][indice].caze[indice])
                        let mntCalcul = mntInitiale - (mntInitiale / pourcentageRed)
                        var stateCopy = this.state.tabChoix;
                        stateCopy[indexLignTab][indice + 1].caze[indice + 1] = mntCalcul;
                        this.setState({
                            tabChoix: stateCopy
                        });
                    }
                });
            }
        }
    }

    toggleLignFormation = (indexLignFormation) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignFormationASupprimer: indexLignFormation
        }));
    }

    deleteLignFormation = () => {
        this.state.niveaux.splice(this.state.indexLignFormationASupprimer, 1)
        this.state.tabChoix.splice(this.state.indexLignFormationASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLignFormation = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        let year = new Date().getFullYear();
        // merge two table 
        for (var i = 0; i < this.state.niveaux.length; i++) {
            this.setState({
                niveaux: this.state.niveaux.map((el, id) => (id === i ? Object.assign(el, { tranches: this.state.tabChoix[i] }) : el))
            }, () => {
                console.log(this.state.niveaux)
            });
        }
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddNiveau.php',
            type: 'POST',
            data: {
                niveaux: this.state.niveaux,
                year: year,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Niveaux');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    // voirTranche = () => {
    //     const node = document.getElementsByClassName('x');
    //     console.log(node)
    // }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout niveau</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Formation</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addFormation}
                                                options={this.state.formations}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Niveau</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px' }} type="text" class="form-control" placeholder='Niveau' onChange={this.addNomNiveau} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Montant initial</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px' }} type="number" class="form-control" value={this.state.mntInitial} onChange={this.addMntInitial} />
                                        </div>
                                    </div>
                                </div>
                                <div class="row">
                                    <table style={{ marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Durée</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Type paiment elève</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Montant</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.niveaux.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Durée' value={el.duree} onChange={(e) => this.addDuree(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '120px' }} /></td>
                                                    <td style={{ width: '30%' }}><Select
                                                        className='posAnnee'
                                                        value={el.typePaiementEleve}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addTypePayEleve(e, index)}
                                                        options={this.state.typesPayEleve}
                                                    />  </td>
                                                    <td style={{ textAlign: 'center' }}>{this.state.tabChoix[index] == null ? (null) : (<div style={{ display: 'flex', justifyContent: 'space-evenly', marginTop: '-15px' }}>{this.state.tabChoix[index].map((el, indice) => <div><p>{el.choixPay}</p><input className="tranche" type="number" value={el.caze[indice]} onChange={(e) => this.add(e, index, indice, el.choixPay)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '70px' }} /></div>)}</div>)}</td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '20%' }} className='buttonSupprimer' onClick={() => this.toggleLignFormation(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLignFormation} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteLignFormation} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteLignFormation}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer ce niveau ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteLignFormation()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteLignFormation()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                                {/* <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.voirTranche}>voir tranche</button>
                                </div> */}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutNiveau);
