import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class ModifSousFamille extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idFamilleArt: '',
            familles: [],
            selectedFamille: '',
            arraySousArt: [],
            ligneSousFamilleSupp: '',
            modal: false,
            idSousFamilleSupp: '',
            arrayIdSousFamilleSupp: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var idFamilleArt = this.props.location.state.idFamilleArt;
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetSousFamilleById.php?id=' + idFamilleArt).then(res => {
                this.setState({
                    idFamilleArt: idFamilleArt,
                    selectedFamille: res.data[0].famille,
                    arraySousArt: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectFamilles.php').then(res => {
                console.log(res)
                this.setState({
                    familles: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    updFamille = selectedFamille => {
        this.setState({ selectedFamille: selectedFamille });
    };

    updSousFamille = (event, index) => {
        var sousFamille = event.target.value;
        this.setState({
            arraySousArt: this.state.arraySousArt.map((el, id) => (id === index ? Object.assign(el, { sousFamille: sousFamille }) : el))
        });
    }

    updCode = (event, index) => {
        var code = event.target.value;
        this.setState({
            arraySousArt: this.state.arraySousArt.map((el, id) => (id === index ? Object.assign(el, { code: code }) : el))
        });
    }

    addLigneSousFamille = () => {
        var element = { idSousFamilleArt: -1, sousFamille: '', code: '' }
        this.setState({
            arraySousArt: [...this.state.arraySousArt, element]
        })
    }

    toggleSousFamille = (index, idSousFamilleArtSup) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            ligneSousFamilleSupp: index,
            idSousFamilleSupp: idSousFamilleArtSup
        }));
    }

    deleteSousFamille = () => {
        var idSousFamilleSupp = this.state.idSousFamilleSupp
        this.setState({
            arrayIdSousFamilleSupp: [...this.state.arrayIdSousFamilleSupp, idSousFamilleSupp]
        },()=>{
            console.log(this.state.arrayIdSousFamilleSupp)
        });
        this.state.arraySousArt.splice(this.state.ligneSousFamilleSupp, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteSousFamille = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    modif = () => {
        if (this.state.selectedFamille != '') {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/UpdSousFamille.php',
                type: 'POST',
                data: {
                    familleId: this.state.selectedFamille.value,
                    arraySousArt: this.state.arraySousArt,
                    arrayIdSousFamilleSupp: this.state.arrayIdSousFamilleSupp
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.props.history.push('/SousFamilleArt');
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
        else {
            toast.error('⛔ veuillez verifier le champs famille !!', { containerId: 'A' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification sous-famille</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/SousFamilleArt" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Famille</label>
                                            <Select
                                                value={this.state.selectedFamille}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updFamille}
                                                options={this.state.familles}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Sous-famille:</label>
                                        </div>
                                    </div>
                                    <table style={{ width: '100%', marginTop: '10px' }} className="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Sous-famille</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Code</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Supp</th>
                                            </tr>
                                        </thead>
                                        {this.state.arraySousArt.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }} className="col-md-5"><input style={{ width: '100%' }} type="text" value={el.sousFamille} onChange={(e) => this.updSousFamille(e, index)} /></td>
                                                    <td style={{ textAlign: 'center' }} className="col-md-5"><input style={{ width: '100%' }} type="text" value={el.code} onChange={(e) => this.updCode(e, index)} /></td>
                                                    <td className="col-md-2" style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleSousFamille(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.addLigneSousFamille} color="secondary"><i className="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                        <Modal isOpen={this.state.modal} toggle={this.nonDeleteSousFamille} className={this.props.className} fade={false}>
                            <ModalHeader toggle={this.nonDeleteSousFamille}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                            <ModalBody>
                                <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                    <div style={{ display: 'flex', justifyContent: 'center' }}>
                                        <p>Êtes-Vous sûr de vouloir supprimer cette ligne ?</p>
                                    </div>
                                    <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                        <div><button onClick={() => this.deleteSousFamille()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                        <div><button onClick={() => this.nonDeleteSousFamille()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                    </div>
                                </div>
                            </ModalBody>
                        </Modal>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(ModifSousFamille);
