                                                        <li className="dd-item" data-id={index}><button data-action="collapse" type="button">Collapse</button><button data-action="expand" type="button" style={{ display: 'none' }}>Expand</button>
                                                            {el.element.map(ele =>
                                                                <Fragment>
                                                                    <div className="dd-handle">{ele.nomElement} </div>
                                                                    <ol className="dd-list">
                                                                        {ele.arraySousElement.map(elm =>
                                                                            <li className="dd-item" data-id={index}>
                                                                                <div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}>{elm.nomSousElement}</div>
                                                                            </li>)}
                                                                    </ol>
                                                                </Fragment>)}
                                                        </li>
                                                    </ol>
                                                </div>
                                            </div>
                                        </div>)}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

