import React, { Component } from "react";
import jQuery from "jquery";
import Select from "react-select";
import { withRouter } from "react-router-dom";

class ModifNivNatureNote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrayData: [],
      listNiveau: [],
      selectedNiveau: '',
      listMat: []
    };
  }

  componentDidMount() {
    if (JSON.parse(localStorage.getItem("userPermissions")) != null) {
      var niveauId = this.props.location.state.niveauId;
      var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
      var anneeScolaire = userPermissions[0].anneeScolaire;
      jQuery.ajax({
        url:
          process.env.REACT_APP_API_URL +
          "Back_mediatraining/GetNivNatNoteById.php",
        type: "POST",
        data: {
          niveauId: niveauId,
          anneeScolaire: anneeScolaire,
        },
        dataType: "json",
        success: (code_html, statut) => {
          console.log(code_html);
          this.setState({
            selectedNiveau: code_html[0].niveau,
            arrayData: code_html
          });
        },
      });
      jQuery.ajax({
        url:
          process.env.REACT_APP_API_URL + "Back_mediatraining/SelectNiveau.php",
        type: "POST",
        data: {
          anneeScolaire: anneeScolaire,
        },
        dataType: "json",
        success: (code_html, statut) => {
          this.setState({
            listNiveau: code_html,
          });
        },
      });
    }
  }

  // addNiveau = (selectedNiveau) => {
  //   var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
  //   var anneeScolaire = userPermissions[0].anneeScolaire;
  //   if (selectedNiveau != null) {
  //     this.setState({ selectedNiveau }, () => {
  //       jQuery.ajax({
  //         url:
  //           process.env.REACT_APP_API_URL +
  //           "Back_mediatraining/SelectMatByNiv.php",
  //         type: "POST",
  //         data: {
  //           niveauId: selectedNiveau.value,
  //           anneeScolaire: anneeScolaire
  //         },
  //         dataType: "json",
  //         success: (code_html, statut) => {
  //           this.setState({
  //             listMatieres: code_html
  //           });
  //         },
  //       });
  //     });
  //   }
  //   else {
  //     this.setState({
  //       selectedNiveau: ''
  //     });
  //   }
  // };

  modifMat = (selectedMat, index) => {

    var arrayData = this.state.arrayData
    arrayData[index] = selectedMat
    this.setState({
      arrayData: arrayData
    });
  }

  render() {
    return (
      <div id="page-wrapper" style={{ minHeight: "263px", marginLeft: "0px" }}>
        <div className="container-fluid">
          <div className="row bg-title">
            <div className="col-lg-12">
              <h4 className="page-title">Les Notes</h4>
            </div>
          </div>
          {/* /.row */}
          <div className="row">
            <div className="col-sm-12">
              <div className="white-box">
                <h3>Mati√©re par niveau</h3>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Niveau</label>
                      <Select
                        isClearable={true}
                        isSearchable={true}
                        value={this.state.selectedNiveau}
                        onChange={this.addNiveau}
                        options={this.state.listNiveau}
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <table className="col-md-12">
                    <thead>
                      <th style={{ textAlign: 'center', padding: '5px' }}>Matiere</th>
                      <th style={{ textAlign: 'center', padding: '5px' }}>Nature matiere</th>
                    </thead>
                    <tbody>
                      {this.state.arrayData.map((el, index) =>
                        <tr>
                          <td className="col-md-6">
                            <div className="form-group">
                              <Select
                                value={el.matiere}
                                isClearable={true}
                                isSearchable={true}
                                onClick={(e) => this.modifMat(e, index)}
                                options={this.state.listMat}
                              />
                            </div>
                          </td>
                          <td className="col-md-6">
                            <div className="form-group">
                              <Select
                                value={el.note}
                                isMulti={true}
                                isClearable={true}
                                isSearchable={true}
                                onChange={this.modifMat}
                                options={this.state.listMat}
                              />
                            </div>
                          </td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    marginBottom: "10px",
                  }}
                >
                  <button
                    style={{ borderRadius: "5px" }}
                    type="button"
                    className="btn btn-success"
                    onClick={this.modif}
                  >
                    Confirmer
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default withRouter(ModifNivNatureNote);
