import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';

class InscripCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listConnaissance: [],
            numInscripCycle: '',
            selectedType: 2,
            addNbrPersonne: 0,
            matFisc: '',
            nbrPersTenu: 0,
            societe: '',
            responsable: '',
            mobileResp: '',
            age: '',
            seletedLieuResid: '',
            selectedNivBase: '',
            facebook: '',
            fonction: '',
            selectedTypeFormat: 2,
            motivation: '',
            numIdentite: '',
            prenom: '',
            nom: '',
            disabled: false
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumInscripCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numInscripCycle: code_html
                });
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectConnaissance.php').then(res => {
            console.log(res)
            this.setState({
                listConnaissance: res.data
            })
        })
    }

    addType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    addNbrPersonne = (event) => {
        this.setState({
            addNbrPersonne: event.target.value
        });
    }

    addMatFisc = (event) => {
        this.setState({
            matFisc: event.target.value
        });
    }

    addNbrPersTenu = (event) => {
        this.setState({
            nbrPersTenu: event.target.value
        });
    }

    addSociete = (event) => {
        this.setState({
            societe: event.target.value
        });
    }

    addResponsable = (event) => {
        this.setState({
            responsable: event.target.value
        });
    }

    addMobile = (event) => {
        this.setState({
            mobileResp: event.target.value
        });
    }

    addAge = (event) => {
        this.setState({
            age: event.target.value
        });
    }

    addLieuResid = (seletedLieuResid) => {
        this.setState({ seletedLieuResid: seletedLieuResid });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
    };

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addFacebook = (event) => {
        this.setState({
            facebook: event.target.value
        });
    }

    addFct = (event) => {
        this.setState({
            fonction: event.target.value
        });
    }

    addImageVerso(e) {
        this.setState({ imageVerso: e.target.files })
    }

    addTypeFormat = (event) => {
        this.setState({
            fonction: event.target.value
        });
    }

    addTypeFormat = (selectedTypeFormat) => {
        this.setState({ selectedTypeFormat: selectedTypeFormat });
    }

    addMotivation = (event) => {
        this.setState({
            motivation: event.target.value
        });
    }

    addNumIdentite = (event) => {
        this.setState({
            numIdentite: event.target.value
        });
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addConnaiss = (e, el, index) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.id
            this.setState({
                connaissChoisi: [...this.state.connaissChoisi, element]
            });
        }
        else {
            let element = el.id
            let indice = this.state.connaissChoisi.indexOf(element)
            this.state.connaissChoisi.splice(indice, 1)
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Inscription cycle</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Inscription N</label>
                                            <input type="text" class="form-control" disabled value style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                        </div>
                                    </div>
                                </div>
                                <div class="row">
                                    <div class="col-md-6">
                                        <RadioGroup
                                            name="typeFormat"
                                            selectedValue={this.state.selectedType}
                                            onChange={this.addType}>
                                            <div>
                                                <label>
                                                    <Radio value={1} />Societ
                                                </label>
                                                <label>
                                                    <Radio value={2} />Particulier
                                                </label>
                                            </div>
                                        </RadioGroup>
                                    </div>
                                </div>
                                {this.state.selectedType == 1 ?
                                    (<div class="row">
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Nombre de personne</label>
                                                <input type="number" class="form-control" onChange={this.addNbrPersonne} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Matricule fiscale</label>
                                                <input type="number" class="form-control" onChange={this.addMatFisc} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Nombre de personne avec tenu</label>
                                                <input type="number" class="form-control" onChange={this.addNbrPersTenu} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Socit</label>
                                                <input type="number" class="form-control" onChange={this.addSociete} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Responsable</label>
                                                <input type="number" class="form-control" onChange={this.addResponsable} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Mobile</label>
                                                <input type="number" class="form-control" onChange={this.addMobile} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Age</label>
                                                <input type="number" class="form-control" onChange={this.addAge} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Lieu rsidence</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addLieuResid}
                                                    options={this.state.lieuResidence}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Niveau inscription</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addNivBase}
                                                    options={this.state.listNivBase}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Email</label>
                                                <input type="text" class="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Tel</label>
                                                <input type="number" class="form-control" onChange={this.addTel} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Facebook</label>
                                                <input type="number" class="form-control" onChange={this.addFacebook} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Fonction actuelle</label>
                                                <input type="number" class="form-control" onChange={this.addFct} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Photo verso </label>
                                                <input type="file" class="form-control" name="file" onChange={(e) => this.addImageVerso(e)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <label>Type Formation</label>
                                            <RadioGroup
                                                name="typeFormat"
                                                selectedValue={this.state.selectedTypeFormat}
                                                onChange={this.addTypeFormat}>
                                                <div>
                                                    <label>
                                                        <Radio value={1} />Indviduelle
                                                </label>
                                                    <label>
                                                        <Radio value={2} />Cycle
                                                </label>
                                                </div>
                                            </RadioGroup>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Motivation</label>
                                                <textarea cols="30" rows="10" onChange={this.addMotivation}></textarea>                                        </div>
                                        </div>
                                    </div>) :
                                    (<div class="row">
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Numro Identit</label>
                                                <input type="number" class="form-control" onChange={this.addNumIdentite} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Prnom</label>
                                                <input type="number" class="form-control" onChange={this.addPrenom} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Nom</label>
                                                <input type="number" class="form-control" onChange={this.addNom} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Age</label>
                                                <input type="number" class="form-control" onChange={this.addAge} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Lieu rsidence</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addLieuResid}
                                                    options={this.state.lieuResidence}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Niveau inscription</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addNivBase}
                                                    options={this.state.listNivBase}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Email</label>
                                                <input type="text" class="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Tel</label>
                                                <input type="number" class="form-control" onChange={this.addTel} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Facebook</label>
                                                <input type="number" class="form-control" onChange={this.addFacebook} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Fonction actuelle</label>
                                                <input type="number" class="form-control" onChange={this.addFct} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Photo verso </label>
                                                <input type="file" class="form-control" name="file" onChange={(e) => this.addImageVerso(e)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <label>Type Formation</label>
                                            <RadioGroup
                                                name="typeFormat"
                                                selectedValue={this.state.selectedTypeFormat}
                                                onChange={this.addTypeFormat}>
                                                <div>
                                                    <label>
                                                        <Radio value={1} />Indviduelle
                                                </label>
                                                    <label>
                                                        <Radio value={2} />Cycle
                                                </label>
                                                </div>
                                            </RadioGroup>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Motivation</label>
                                                <textarea cols="30" rows="10" onChange={this.addMotivation}></textarea>                                        </div>
                                        </div>
                                    </div>
                                    )}
                                <div class="row">
                                    <div class="col-md-6">
                                        <label>Comment avez-vous connu notre centre de formation ?</label>
                                        <span style={{ display: 'flex', flexWrap: 'wrap', padding: '10px', borderRadius: '5px', border: 'solid 1px #B3B3B3' }}>
                                            {this.state.listConnaissance.map((el, index) =>
                                                <label>
                                                    <Checkbox
                                                        onChange={(e) => this.addConnaiss(e, el, index)}
                                                        disabled={this.state.disabled}
                                                    />
                                                    {el.label}
                                                </label>
                                            )}
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(InscripCycle);
