import React, { Component } from 'react'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Collapsible from 'react-collapsible';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class Planning extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dateDebut: '',
            dateFin: '',
            arrayPlanif: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
        }
        else {
            document.location = '/'
        }
    }

    addDateDebut = d => {
        this.setState({
            dateDebut: d
        });
    };

    addDateFin = d => {
        this.setState({
            dateFin: d
        });
    };

    planif = () => {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_magasin/Planning.php',
            type: 'POST',
            data: {
                dateDebut: this.state.dateDebut,
                dateFin: this.state.dateFin
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    arrayPlanif: code_html
                });
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Planning</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateDebut}
                                                onChange={this.addDateDebut}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date fin</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateFin}
                                                onChange={this.addDateFin}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.planif}>Planifier</button>
                                    </div>
                                </div>
                                {this.state.arrayPlanif.length > 0}
                                <div className="row">
                                    <table className="table table-bordered">
                                        <thead >
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Jour</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <td>

                                            </td>
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(Planning);
