import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Switch from "react-switch";

class ModifNiveau extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idFormation: '',
            formations: [],
            ListFilieres: [],
            selectedFiliere: '',
            ListSpecialites: [],
            selectedSpecialite: '',
            nomFormation: '',
            ListNivBase: [],
            selectedNivBase: '',
            indexLignFormationASupprimer: '',
            idNivFomationASupprimer: '',
            tabIdNivFormationDelete: [],
            idFraisNivFormatASupprimer: '',
            tabIdFraisNivFormatDelete: [],
            modal: false,
            reducFormat: 0,
            checked: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            let idFormation = this.props.location.state.idFormation;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetFormationById.php?id=' + idFormation).then(res => {
                console.log(res)
                this.setState({
                    idFormation: res.data[0].idFormation,
                    selectedFiliere: res.data[0].filiere,
                    selectedSpecialite: res.data[0].specialite,
                    nomFormation: res.data[0].nomFormation,
                    selectedNivBase: res.data[0].nivBase,
                    reducFormat: res.data[0].pourcentageRed,
                    formations: res.data[1]
                })
                if (parseInt(this.state.formations[0].pourcentageRed) !== 0) {
                    this.setState({
                        checked: true
                    });
                }
            })
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        ListFilieres: code_html
                    })
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
                console.log(res)
                this.setState({
                    ListNivBase: res.data
                })
            })
        }
    }

    additionLignFormation = () => {
        let element = { idNiveau: -1, idFraisNiv: -1, niveau: '', duree: '', nbrMois: 0, prixAnnuel: 0, prixAnnuelRed: 0, prixMensuel: 0, preTranche: 0, deuxTranche: 0, troisTranche: 0 }
        this.setState({
            formations: [...this.state.formations, element]
        })
    }

    updFiliere = selectedFiliere => {
        this.setState({ selectedFiliere }, () => {
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectSpecialites.php?id=' + selectedFiliere.value).then(res => {
                console.log(res)
                this.setState({
                    ListSpecialites: res.data
                })
            })
        });
    };

    updSpecialite = selectedSpecialite => {
        this.setState({ selectedSpecialite });
        console.log(`Specialite selected:`, selectedSpecialite);
    };

    updNomFormation = (event) => {
        this.setState({
            nomFormation: event.target.value
        });
    }

    updNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
        console.log(`NivBase selected:`, selectedNivBase);
    };

    handleChange = (checked) => {
        this.setState({ checked });
        console.log(this.state.checked)
    }

    updReducFormat = (event) => {
        this.setState({
            reducFormat: event.target.value
        });
    }

    updNiveau = (event, indexNiveau) => {
        let niveau = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNiveau ? Object.assign(el, { niveau: niveau }) : el))
        });
    }

    updDuree = (event, indexNiveau) => {
        let duree = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNiveau ? Object.assign(el, { duree: duree }) : el))
        });
    }

    updPrixAnnuel = (event, indexPrixAnnuel) => {
        let prixAnnuel = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexPrixAnnuel ? Object.assign(el, { prixAnnuel: prixAnnuel }) : el))
        });
        if (this.state.checked === true && this.state.reducFormat !== 0 && this.state.formations[indexPrixAnnuel].prixAnnuel !== 0) {
            let prixAnnuel = document.getElementsByClassName('prixAnn')[indexPrixAnnuel].value;
            let prixAnnuelRed = prixAnnuel - (prixAnnuel / (this.state.reducFormat))
            this.setState({
                formations: this.state.formations.map((el, id) => (id === indexPrixAnnuel ? Object.assign(el, { prixAnnuelRed: prixAnnuelRed }) : el))
            });
        }
        if (this.state.formations[indexPrixAnnuel].prixAnnuel !== 0 && this.state.formations[indexPrixAnnuel].nbrMois !== 0) {
            let nbrMois = document.getElementsByClassName('nbrMois')[indexPrixAnnuel].value;
            let prixAnnuel = document.getElementsByClassName('prixAnn')[indexPrixAnnuel].value;
            let prixMensuel = prixAnnuel / nbrMois;
            this.setState({
                formations: this.state.formations.map((el, id) => (id === indexPrixAnnuel ? Object.assign(el, { prixMensuel: prixMensuel }) : el))
            })
        }
    }

    updNbrMois = (event, indexNbrMois) => {
        let nbrMois = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNbrMois ? Object.assign(el, { nbrMois: nbrMois }) : el))
        });
        if (this.state.formations[indexNbrMois].prixAnnuel !== 0 && this.state.formations[indexNbrMois].nbrMois !== 0) {
            let nbrMois = document.getElementsByClassName('nbrMois')[indexNbrMois].value;
            let prixAnnuel = document.getElementsByClassName('prixAnn')[indexNbrMois].value;
            let prixMensuel = prixAnnuel / nbrMois;
            this.setState({
                formations: this.state.formations.map((el, id) => (id === indexNbrMois ? Object.assign(el, { prixMensuel: prixMensuel }) : el))
            })
        }
    }

    updPreTranche = (event, indexPreTranche) => {
        let preTranche = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexPreTranche ? Object.assign(el, { preTranche: preTranche }) : el))
        });
    }

    updDeuxTranche = (event, indexDeuxTranche) => {
        let deuxTranche = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexDeuxTranche ? Object.assign(el, { deuxTranche: deuxTranche }) : el))
        });
    }

    updTroisTranche = (event, indexTroisTranche) => {
        let troisTranche = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexTroisTranche ? Object.assign(el, { troisTranche: troisTranche }) : el))
        });
    }

    toggleLignFormation = (el, indexLignFormation) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idNivFomationASupprimer: el.idNiveau,
            idFraisNivFormatASupprimer: el.idFraisNiv,
            indexLignFormationASupprimer: indexLignFormation
        }));
    }

    deleteLignFormation = () => {
        let idNivFomationASupprimer = this.state.idNivFomationASupprimer
        let idFraisNivFormatASupprimer = this.state.idFraisNivFormatASupprimer
        this.setState({
            tabIdNivFormationDelete: [...this.state.tabIdNivFormationDelete, idNivFomationASupprimer],
            tabIdFraisNivFormatDelete: [...this.state.tabIdFraisNivFormatDelete, idFraisNivFormatASupprimer]
        });
        this.state.formations.splice(this.state.indexLignFormationASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLignFormation = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    modif = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        let year = new Date().getFullYear();
        console.log(this.state.selectedFiliere)
        console.log(this.state.selectedSpecialite)
        console.log(this.state.nomFormation)
        console.log(this.state.selectedNivBase)
        console.log(this.state.formations)
        console.log(this.state.tabIdNivFormationDelete)
        console.log(this.state.tabIdFraisNivFormatDelete)
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdFormationNiv.php?id=' + this.state.idFormation,
            type: 'POST',
            data: {
                filiereId: this.state.selectedFiliere.value,
                specialiteId: this.state.selectedSpecialite.value,
                nomFormation: this.state.nomFormation,
                nivBaseId: this.state.selectedNivBase.value,
                pourcentageRed: this.state.reducFormat,
                formations: this.state.formations,
                year: year,
                idNiveauxDelete: this.state.tabIdNivFormationDelete,
                IdFraisNiveau: this.state.tabIdFraisNivFormatDelete,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/FormationNiv');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification formation</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Filire</label>
                                            <Select
                                                value={this.state.selectedFiliere}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updFiliere}
                                                options={this.state.ListFilieres}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Spcialit</label>
                                            <Select
                                                value={this.state.selectedSpecialite}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updSpecialite}
                                                options={this.state.ListSpecialites}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom formation</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px' }} type="text" class="form-control" value={this.state.nomFormation} onChange={this.updNomFormation} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Niveau de Base</label>
                                            <Select
                                                value={this.state.selectedNivBase}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updNivBase}
                                                options={this.state.ListNivBase}
                                            />
                                        </div>
                                    </div>
                                    <label>
                                        <span style={{ marginLeft: '15px', marginRight: '10px' }}>Rduction</span>
                                        <Switch offColor='#FF0000' onChange={this.handleChange} checked={this.state.checked} />
                                    </label>
                                    {this.state.checked == true ?
                                        (<div class="col-md-6">
                                            <div class="form-group">
                                                <label>Rduction par formation %</label>
                                                <input style={{ backgroundColor: 'white', borderRadius: '5px' }} type="number" class="form-control" value={this.state.reducFormat} onChange={this.addReducFormat} />
                                            </div>
                                        </div>) : (null)}
                                </div>
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Niveau</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Dure</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix Annuel</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix Annuel rd</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Nbr de mois</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix Mensuel</th>
                                                <th colSpan="3" style={{ textAlign: 'center' }} scope="col">Paiement en tranche</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.formations.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.niveau} onChange={(e) => this.updNiveau(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '70px', marginTop: '24px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.duree} onChange={(e) => this.updDuree(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '70px', marginTop: '24px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="number" className='prixAnn' placeholder='Prix Annuel' value={el.prixAnnuel} onChange={(e) => this.updPrixAnnuel(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '84px', marginTop: '24px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="number" value={el.prixAnnuelRed} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '102px', marginTop: '24px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="number" className='nbrMois' value={el.nbrMois} onChange={(e) => this.updNbrMois(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '85px', marginTop: '24px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="number" value={el.prixMensuel} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '88px', marginTop: '24px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><label style={{ textAlign: 'center', width: '70px', paddingBottom: '5px' }}>Premire</label><input type="number" value={el.preTranche} onChange={(e) => this.updPreTranche(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '75px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><label style={{ textAlign: 'center', width: '70px', paddingBottom: '5px' }}>Deuxime</label><input type="number" value={el.deuxTranche} onChange={(e) => this.updDeuxTranche(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '75px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><label style={{ textAlign: 'center', width: '70px', paddingBottom: '5px' }}>Troisime</label><input type="number" value={el.troisTranche} onChange={(e) => this.updTroisTranche(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '75px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '35%', marginTop: '30px' }} className='buttonSupprimer' onClick={() => this.toggleLignFormation(el, index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLignFormation} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteLignFormation} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteLignFormation}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>tes-Vous sr de vouloir supprimer cette formation ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteLignFormation()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteLignFormation()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifNiveau);
