import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable, { createTheme } from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Collapsible from 'react-collapsible';
import Select from 'react-select';

const groupStyles = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
};
const groupBadgeStyles = {
    backgroundColor: '#EBECF0',
    borderRadius: '2em',
    color: '#172B4D',
    display: 'inline-block',
    fontSize: 12,
    fontWeight: 'normal',
    lineHeight: '1',
    minWidth: 1,
    padding: '0.16666666666667em 0.5em',
    textAlign: 'center',
};

const formatGroupLabel = data => (
    <div style={groupStyles}>
        <span>{data.label}</span>
        <span style={groupBadgeStyles}>{data.options.length}</span>
    </div>
);

class ReglApprenantCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Num inscrip',
                    selector: 'numInscrip',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Carte AD',
                    selector: 'carte',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Nom et prénom',
                    selector: 'nomComplet',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Connaissance',
                    selector: 'connaissance',
                    sortable: false,
                    center: true,
                    style: { width: '20%' }
                },
                {
                    name: 'Frais cycle',
                    cell: row => <table style={{ width: '166px' }} className="table table-bordered">
                        <thead>
                            <tr>
                                <th style={{ textAlign: 'center', width: '90px' }} scope="col">Frais</th>
                                <th style={{ textAlign: 'center', width: '65px' }} scope="col">Montant</th>
                            </tr>
                        </thead>
                        {row.reglFrais.map((el, index) =>
                            <tbody>
                                <tr>
                                    <td style={{ textAlign: 'center' }}><p>{el.frais}</p></td>
                                    <td style={{ textAlign: 'center' }}><p>{el.mnt}</p></td>
                                </tr>
                            </tbody>
                        )}
                    </table>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '25%'
                },
                {
                    name: 'Reglement inscription',
                    cell: row => <div onClick={() => this.reglApprenant(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px' }}><p>Reglement</p></div></div>
                    ,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '15%'
                },
                {
                    name: 'Recu',
                    cell: row => row.recu == 1 ? (<i style={{ color: '#2192FF', fontSize: '20px' }} onClick={() => this.attPay(row)} className="fas fa-file-invoice"></i>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            pending: true,
            regCycle: [],
            // search
            prenom: '',
            nom: '',
            cin: '',
            lieuResid: [],
            seletedLieuResid: '',
            listNivBase: [],
            selectedNivBase: '',
            listEleve: [],
            selectedIdEleve: '',
            typeFormation: [{ value: 1, label: 'Individuel' }, { value: 2, label: 'cycle' }],
            selectedTypeFormat: '',
            listCycle: [],
            selectedCycle: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppCycle.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectResid.php').then(res => {
                this.setState({
                    lieuResid: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
                console.log(res)
                this.setState({
                    listNivBase: res.data
                })
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectDetailCycle.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        listCycle: code_html
                    })
                }
            });
        }
    }

    reglApprenant = (row) => {
        this.setState({
            regCycle: row.reglInscrip
        }, () => {
            console.log(this.state.regCycle.detailRegl)
            this.setState(prevState => ({
                modal: !prevState.modal,
            }));
        });
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal,
        }));
    }

    attPay = (row) => {
        var idEnteteRegl = row.idEnteteRegl
        var idNiveau = row.idNiveau
        var typePaiement = row.typePaiement
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Php_client_jasper/Report_jasper_recu_paiement.php',
            type: 'POST',
            data: {
                idEnteteRegl: idEnteteRegl,
                idNiveau: idNiveau,
                typePaiement: typePaiement
            },
            dataType: 'json',
            success: function (code_html, statut) {
                var server_path = code_html.server_path;
                var file_path = code_html.file_path
                var path = server_path + file_path;
                window.open(path, "_blank")
            }
        });
    }

    recuRegl = (ele) => {
        var idDetailScol = ele.idDetailScol
        var totalPay = ele.totalPay
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Php_client_jasper/Report_jasper_recu_regl.php',
            type: 'POST',
            data: {
                idDetailScol: idDetailScol,
                totalPay: totalPay
            },
            dataType: 'json',
            success: function (code_html, statut) {
                var server_path = code_html.server_path;
                var file_path = code_html.file_path
                var path = server_path + file_path;
                window.open(path, "_blank")
            }
        });
    }

    newElement = (page, totalRows) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedIdEleve != '') {
            var idEleve = this.state.selectedIdEleve.value
        }
        else {
            var idEleve = ''
        }
        if (this.state.selectedEtat != '') {
            var idEtat = this.state.selectedEtat.value
        }
        else {
            var idEtat = ''
        }
        if (this.state.selectedIdModPay != '') {
            var idModPay = this.state.selectedIdModPay.value
        }
        else {
            var idModPay = ''
        }
        if (this.state.selectedIdGroup != '') {
            var idGroup = this.state.selectedIdGroup.value
        }
        else {
            var idGroup = ''
        }
        if ((idEleve == '') && (idEtat == "") && (idModPay == '') && (idGroup == '')) {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppCycle.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppCycleSearch.php',
                        type: 'POST',
                        data: {
                            idEleve: idEleve,
                            idEtat: idEtat,
                            idModPay: idModPay,
                            idGroup: idGroup,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    perPage = (currentRowsPerPage, currentPage) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedIdEleve != '') {
            var idEleve = this.state.selectedIdEleve.value
        }
        else {
            var idEleve = ''
        }
        if (this.state.selectedEtat != '') {
            var idEtat = this.state.selectedEtat.value
        }
        else {
            var idEtat = ''
        }
        if (this.state.selectedIdModPay != '') {
            var idModPay = this.state.selectedIdModPay.value
        }
        else {
            var idModPay = ''
        }
        if (this.state.selectedIdGroup != '') {
            var idGroup = this.state.selectedIdGroup.value
        }
        else {
            var idGroup = ''
        }
        if ((idEleve == '') && (idEtat == '') && (idModPay == '') && (idGroup == '')) {
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppCycle.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: currentRowsPerPage,
                            page: currentPage,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppCycleSearch.php',
                        type: 'POST',
                        data: {
                            idEleve: idEleve,
                            idEtat: idEtat,
                            idModPay: idModPay,
                            idGroup: idGroup,
                            limit: currentRowsPerPage,
                            page: currentPage,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }


    filterSearch = (event) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppCycle.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    searchPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    searchNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    searchCin = (event) => {
        this.setState({
            cin: event.target.value
        });
    }

    searchLieuResid = (seletedLieuResid) => {
        this.setState({ seletedLieuResid: seletedLieuResid });
    }

    searchNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
    };

    searchTypeFormat = selectedTypeFormat => {
        this.setState({ selectedTypeFormat });
    };

    searchCycle = selectedCycle => {
        this.setState({ selectedCycle });
    };

    recherche = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppCycleSearch.php',
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    afficherTout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppCycle.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html.data,
                    pending: false
                });
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Reglement cycle</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <h4 style={{ paddingLeft: '10px' }}>Critères de recherche</h4>
                                    <Fragment>
                                        <div class="col-md-6">
                                            <label>Prénom</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.searchPrenom(e)} placeholder={'Prénom'} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <label>Nom</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.searchNom(e)} placeholder={'Nom'} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <label>CIN</label>
                                            <div class="form-group">
                                                <input type="text" onChange={(e) => this.searchCin(e)} placeholder={'CIN'} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                    </Fragment>
                                    <Fragment>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Lieu résidence</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchLieuResid}
                                                    options={this.state.lieuResid}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Niveau de base</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchNivBase}
                                                    options={this.state.listNivBase}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Type formation</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchTypeFormat}
                                                    options={this.state.typeFormation}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Cycles</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    options={this.state.listCycle}
                                                    formatGroupLabel={formatGroupLabel}
                                                />
                                            </div>
                                        </div>
                                    </Fragment>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Reglement eleve</p></div></div></ModalHeader>
                    <ModalBody>
                        <div>
                            <table className="table">
                                <thead >
                                    <tr>
                                        <th style={{ textAlign: 'center' }} scope="col">Tranche</th>
                                        <th style={{ textAlign: 'center' }} scope="col">Etat</th>
                                    </tr>
                                </thead>
                                {this.state.regCycle.map((el, index) =>
                                    <tbody>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}><p>{index + 1 + ' tranche'}</p></td>
                                            {el.etat !== '' ?
                                                (<td style={{ textAlign: 'center' }}><div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}><p>{el.etat}</p><p style={{ paddingLeft: '5px' }}>{el.mntNonRegle}</p></div>
                                                    <Collapsible trigger={<button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }}><i style={{ color: 'white' }} className="fas fa-info"></i></button>}>
                                                        <Fragment>
                                                            <table className="table table-bordered">
                                                                <thead >
                                                                    <tr>
                                                                        <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Date</th>
                                                                        <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Montant</th>
                                                                        <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Reglement</th>
                                                                        <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Piéce</th>
                                                                    </tr>
                                                                </thead>
                                                                {el.detailRegl.map(ele =>
                                                                    <tbody>
                                                                        <tr>
                                                                            <td style={{ textAlign: 'center', fontSize: '10px' }}><p>{ele.date}</p></td>
                                                                            <td style={{ textAlign: 'center', fontSize: '10px' }}><p>{ele.totalPay}</p></td>
                                                                            {ele.modePaiement == "Chéque" ?
                                                                                (<td style={{ textAlign: 'center', fontSize: '10px' }}><p>{ele.modePaiement}</p><p style={{ fontSize: '9px' }}>{'Echéance : ' + ele.echeance}</p></td>) : (<td style={{ textAlign: 'center' }}><p>{ele.modePaiement}</p></td>)}
                                                                            <td><i onClick={() => this.recuRegl(ele)} className="fas fa-print"></i></td>
                                                                        </tr>
                                                                    </tbody>
                                                                )}
                                                            </table>
                                                        </Fragment>
                                                    </Collapsible>
                                                </td>) : (<td></td>)}
                                        </tr>
                                    </tbody>
                                )}
                            </table>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white', width: '40px ' }}><i style={{ color: 'white' }} className="fas fa-sign-out-alt"></i></button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}

export default ReglApprenantCycle;
