import React, { Component } from 'react';
import axios from 'axios'
import GoogleMapReact from 'google-map-react';

const AnyReactComponent = ({ text }) => <div>{text}</div>;
class Maps extends Component {
    static defaultProps = {
        center: {
            lat: 34.747847,
            lng: 10.766163
        },
        zoom: 11
    };
    constructor(props) {
        super(props);
        this.state = {
            latitude: '',
            longitude: ''
        }
    }

    componentDidMount() {
        var idClient = this.props.location.state.idClient;
        axios.get(process.env.REACT_APP_API_URL + 'Back_hooda/GetAdresseByClientId.php?id=' + idClient).then(res => {
            this.setState({
                latitude: res.data.latitude,
                longitude: res.data.longitude
            })
        })
    }

    render() {
        return (
            <div style={{ height: '100vh', width: '100%' }}>
                <GoogleMapReact
                    bootstrapURLKeys={{ key: 'AIzaSyAhL4D_X4dhl-AW1E9HvG2k657SdqOEwEA' }}
                    defaultCenter={this.props.center}
                    defaultZoom={this.props.zoom}
                >
                    <AnyReactComponent
                        lat={this.state.latitude}
                        lng={this.state.longitude}
                        text="Adresse"
                    />
                </GoogleMapReact>
            </div>
        );
    }
}

export default Maps;
