import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class ModifReglApp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listEleve: [],
            selectedEleve: '',
            arrayRegl: [],
            infoFormatEleve: [],
            fraisReglEleve: [],
            reglEleve: [],
            mntFraisTotalPayer: 0,
            mntIncripTotalPayer: 0,
            tabModPay: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            listModPay: []
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listEleve: code_html
                });
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectModePaiement.php').then(res => {
            console.log(res)
            this.setState({
                listModPay: res.data
            })
        })
    }

    selectEleve = (selectedEleve) => {
        if (selectedEleve != null) {
            this.setState({ selectedEleve: selectedEleve }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetReglByEleve.php',
                    type: 'POST',
                    data: {
                        eleveId: this.state.selectedEleve.value
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            arrayRegl: code_html,
                            infoFormatEleve: code_html[0],
                            fraisReglEleve: code_html[1],
                            reglEleve: code_html[2]
                        }, () => {
                            // Somme frais reglement
                            var sommeMntFraisPayer = 0;
                            for (var i = 0; i < this.state.fraisReglEleve.length; i++) {
                                var mntRestAPayer = parseInt(this.state.fraisReglEleve[i].mntRest)
                                if (mntRestAPayer > 0) {
                                    sommeMntFraisPayer += parseInt(this.state.fraisReglEleve[i].mntPay)
                                }
                            }
                            this.setState({
                                mntFraisTotalPayer: Number.parseFloat(sommeMntFraisPayer).toFixed(3)
                            });
                            // Somme reglement
                            let mntIncripTotalPayer = 0
                            for (var i = 0; i < this.state.reglEleve.length; i++) {
                                var mntRestAPayer = parseInt(this.state.reglEleve[i].mntRest)
                                if (mntRestAPayer > 0) {
                                    mntIncripTotalPayer += parseInt(this.state.reglEleve[i].mntPayer)
                                }
                            }
                            this.setState({
                                mntIncripTotalPayer: Number.parseFloat(mntIncripTotalPayer).toFixed(3)
                            });
                        });
                    }
                });
            });
        }
        else {
            this.setState({
                arrayRegl: [],
                infoFormatEleve: [],
                fraisReglEleve: [],
                reglEleve: []
            });
        }
    }

    updMntFraisPayer = (event, indexMntFraisPayer) => {
        let mntPay = event.target.value
        this.setState({
            fraisReglEleve: this.state.fraisReglEleve.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntPay: Number.parseFloat(mntPay).toFixed(3) }) : el))
        }, () => {
            let mntTotal = parseInt(this.state.fraisReglEleve[indexMntFraisPayer].mntTotal)
            var mntFraisPay = parseInt(this.state.fraisReglEleve[indexMntFraisPayer].mntPay)
            var mntFraisRest = parseInt(this.state.fraisReglEleve[indexMntFraisPayer].mntRest)
            if (mntTotal >= mntFraisPay) {
                if (mntFraisRest > 0) {
                    let mntRest = mntTotal - mntFraisPay
                    this.setState({
                        fraisReglEleve: this.state.fraisReglEleve.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntRest: Number.parseFloat(mntRest).toFixed(3) }) : el))
                    });
                }
                let sommeMntFraisPayer = 0;
                for (var i = 0; i < this.state.fraisReglEleve.length; i++) {
                    if (this.state.fraisReglEleve[i].mntPay != "") {
                        sommeMntFraisPayer += parseInt(this.state.fraisReglEleve[i].mntPay)
                    }
                    else {
                        sommeMntFraisPayer += 0
                    }
                }
                this.setState({
                    mntFraisTotalPayer: Number.parseFloat(sommeMntFraisPayer).toFixed(3)
                });
            }
        });
    }

    updMntInscripPayer = (event, indexMntInscripPayer) => {
        let mntPayer = event.target.value
        this.setState({
            reglEleve: this.state.reglEleve.map((el, id) => (id === indexMntInscripPayer ? Object.assign(el, { mntPayer: Number.parseFloat(mntPayer).toFixed(3) }) : el))
        }, () => {
            let mntAPayer = this.state.reglEleve[indexMntInscripPayer].mntAPayer
            let mntRestant = this.state.reglEleve[indexMntInscripPayer].mntRestant
            if (parseInt(mntAPayer) >= parseInt(mntPayer)) {
                if (parseInt(mntRestant) > 0) {
                    let mntInscripRest = mntAPayer - mntPayer
                    this.setState({
                        reglEleve: this.state.reglEleve.map((el, id) => (id === indexMntInscripPayer ? Object.assign(el, { mntRestant: Number.parseFloat(mntInscripRest).toFixed(3) }) : el))
                    });
                }
                let mntIncripTotalPayer = 0
                for (var i = 0; i < this.state.reglEleve.length; i++) {
                    mntIncripTotalPayer += parseInt(this.state.reglEleve[i].mntPayer)
                }
                this.setState({
                    mntIncripTotalPayer: Number.parseFloat(mntIncripTotalPayer).toFixed(3)
                });
            }
        });
    }

    additionLign = () => {
        let element = { modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }
        this.setState({
            tabModPay: [...this.state.tabModPay, element]
        })
    }

    addModPay = (selectedModPay, indexModPay) => {
        if (selectedModPay != null) {
            let modPay = selectedModPay;
            this.setState({
                tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { modPay: modPay }) : el))
            });
        }
    }

    addMntModPay = (event, indexModPay) => {
        let mntModPay = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { mntModPay: Number.parseFloat(mntModPay).toFixed(3) }) : el))
        });
    }

    addNumChec = (event, indexModPay) => {
        let numCheque = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { numCheque: numCheque }) : el))
        });
    }

    addEcheanceChec = (d, indexModPay) => {
        let dateChec = d;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { echeanceChec: dateChec }) : el))
        });
    }

    addDateEncaiss = (d, indexModPay) => {
        let dateEncaiss = d;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { dateEncaiss: dateEncaiss }) : el))
        });
    }

    addTitulaireChec = (event, indexModPay) => {
        let titulaireChec = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { titulaireChec: titulaireChec }) : el))
        });
    }

    addBanque = (event, indexModPay) => {
        let banque = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { banque: banque }) : el))
        });
    }

    toggleLign = (index) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignASupprimer: index
        }));
    }

    deleteLign = () => {
        this.state.tabModPay.splice(this.state.indexLignASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLign = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajoutRegInscrip = () => {
        var sommePaiement = 0
        sommePaiement = parseInt(this.state.mntFraisTotalPayer) + parseInt(this.state.mntIncripTotalPayer)
        var sommeReg = 0
        for (var i = 0; i < this.state.tabModPay.length; i++) {
            sommeReg += parseInt(this.state.tabModPay[i].mntModPay)
        }
        if (sommePaiement !== sommeReg) {
            toast.error('⛔ Veuillez vérifier les sommes saisie !', { containerId: 'A' });
        }
        else {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            var userId = userPermissions[0].idUser
            var arrayModPay = []
            for (var i = 0; i < this.state.tabModPay.length; i++) {
                arrayModPay.push(this.state.tabModPay[i].modPay.value)
            }
            // jQuery.ajax({
            //     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdReglEleve.php',
            //     type: 'POST',
            //     data: {
            //         eleveId: this.state.infoFormatEleve[0].eleveId,
            //         idInscrip: this.state.infoFormatEleve[0].idIns,
            //         regFrais: this.state.fraisReglEleve,
            //         regInscrip: this.state.reglEleve,
            //         modPay: this.state.tabModPay,
            //         arrayIdModPay: arrayModPay,
            //         anneeScolaire: anneeScolaire,
            //         userId: userId
            //     },
            //     dataType: 'json',
            //     success: (code_html, statut) => {
            //         if (code_html.Result == "OK") {
            //             this.props.history.push('/ReglApprenant');
            //         }
            //         else {
            //             alert("prbl lors de l'insertion")
            //         }
            //     }
            // });
        }
    }

    render() {
        if (this.state.arrayRegl.length > 0) {
            var x = this.state.reglEleve[0].typePay
            if (x == "Annuel") {
                var paiementInscrip = (
                    <Fragment>
                        <h3 style={{ paddingLeft: '15px' }}>Paiement reglement :</h3>
                        <table style={{ marginTop: '10px' }} class="table table-bordered">
                            <thead>
                                <tr>
                                    <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                </tr>
                            </thead>
                            {this.state.reglEleve.map((el, index) =>
                                <tbody>
                                    <tr>
                                        {parseInt(el.mntRestant) == 0 ?
                                            (<Fragment>
                                                <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{el.typePay}</p></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntAPayer).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" value={Number.parseFloat(el.mntPayer).toFixed(3)} onChange={(e) => this.updMntInscripPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntRestant).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                            </Fragment>) : (null)}
                                    </tr>
                                </tbody>
                            )}
                        </table>
                    </Fragment>)
            }
            else if (x == "Annuel avec réduction") {
                var paiementInscrip = (
                    <Fragment>
                        <h3 style={{ paddingLeft: '15px' }}>Paiement reglement :</h3>
                        <table style={{ marginTop: '10px' }} class="table table-bordered">
                            <thead>
                                <tr>
                                    <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                </tr>
                            </thead>
                            {this.state.reglEleve.map((el, index) =>
                                <tbody>
                                    <tr>
                                        {parseInt(el.mntRestant) == 0 ?
                                            (<Fragment>
                                                <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{el.typePay}</p></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntAPayer).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" value={Number.parseFloat(el.mntPayer).toFixed(3)} onChange={(e) => this.updMntInscripPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntRestant).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                            </Fragment>) : (null)}
                                    </tr>
                                </tbody>
                            )}
                        </table>
                    </Fragment>)
            }
            else if (x == "Mensuelle") {
                var paiementInscrip = (
                    <Fragment>
                        <h3 style={{ paddingLeft: '15px' }}>Paiement reglement :</h3>
                        <table style={{ marginTop: '10px' }} class="table table-bordered">
                            <thead>
                                <tr>
                                    <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                </tr>
                            </thead>
                            {this.state.reglEleve.map((el, index) =>
                                <tbody>
                                    <tr>
                                        {parseInt(el.mntRestant) == 0 ?
                                            (<Fragment>
                                                <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{index + 1 + " tranche"}</p></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntAPayer).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" value={Number.parseFloat(el.mntPayer).toFixed(3)} onChange={(e) => this.updMntInscripPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntRestant).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                            </Fragment>) : (null)}
                                    </tr>
                                </tbody>
                            )}
                        </table>
                    </Fragment>)
            }
            else if (x === "2 Tranche") {
                var paiementInscrip = (
                    <Fragment>
                        <h3 style={{ paddingLeft: '15px' }}>Paiement reglement :</h3>
                        <table style={{ marginTop: '10px' }} class="table table-bordered">
                            <thead>
                                <tr>
                                    <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                </tr>
                            </thead>
                            {this.state.reglEleve.map((el, index) =>
                                <tbody>
                                    <tr>
                                        {parseInt(el.mntRestant) == 0 ?
                                            (<Fragment>
                                                <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{index + 1 + " tranche"}</p></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntAPayer).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" value={Number.parseFloat(el.mntPayer).toFixed(3)} onChange={(e) => this.updMntInscripPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntRestant).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                            </Fragment>) : (null)}
                                    </tr>
                                </tbody>
                            )}
                        </table>
                    </Fragment>)
            }
            else {
                var paiementInscrip = (
                    <Fragment>
                        <h3 style={{ paddingLeft: '15px' }}>Paiement reglement :</h3>
                        <table style={{ marginTop: '10px' }} class="table table-bordered">
                            <thead>
                                <tr>
                                    <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                </tr>
                            </thead>
                            {this.state.reglEleve.map((el, index) =>
                                <tbody>
                                    <tr>
                                        {parseInt(el.mntRestant) == 0 ?
                                            (<Fragment>
                                                <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{index + 1 + " tranche"}</p></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntAPayer).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" value={Number.parseFloat(el.mntPayer).toFixed(3)} onChange={(e) => this.updMntInscripPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntRestant).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                            </Fragment>) : (null)}
                                    </tr>
                                </tbody>
                            )}
                        </table>
                    </Fragment>)
            }
        }
        else {
            var x = 0
        }
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Mise à jour reglement apprenant</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label>Eléves</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.selectEleve(e)}
                                                options={this.state.listEleve}
                                            />
                                        </div>
                                    </div>
                                    {this.state.arrayRegl.length > 0 ?
                                        (<Fragment>
                                            <div className="col-md-2">
                                                <div className="form-group">
                                                    <label>Groupe</label>
                                                    <input type="text" disabled value={this.state.infoFormatEleve[0].groupe} className="form-control" style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                                </div>
                                            </div>
                                            <div className="col-md-2">
                                                <div className="form-group">
                                                    <label>Niveau</label>
                                                    <input type="text" disabled value={this.state.infoFormatEleve[0].niveau} className="form-control" style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                                </div>
                                            </div>
                                            <div className="col-md-2">
                                                <div className="form-group">
                                                    <label>formation</label>
                                                    <input type="text" disabled value={this.state.infoFormatEleve[0].formation} className="form-control" style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                                </div>
                                            </div>
                                            <div className="col-md-2">
                                                <div className="form-group">
                                                    <label>Specialite</label>
                                                    <input type="text" disabled value={this.state.infoFormatEleve[0].nomSpecialite} className="form-control" style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                                </div>
                                            </div>
                                        </Fragment>) : (null)
                                    }
                                </div>
                                {this.state.arrayRegl.length > 0 ?
                                    (<Fragment>
                                        <h3>Paiement Frais Inscription :</h3>
                                        <div className="row">
                                            <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                                    </tr>
                                                </thead>
                                                {this.state.fraisReglEleve.map((el, index) =>
                                                    <tbody>
                                                        <tr>
                                                            {parseInt(el.mntRest) > 0 ?
                                                                (<Fragment>
                                                                    <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{el.nomFrais}</p></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntTotal).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" value={el.mntPay} onChange={(e) => this.updMntFraisPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" disabled value={el.mntRest} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                </Fragment>) : (null)}
                                                        </tr>
                                                    </tbody>
                                                )}
                                            </table>
                                            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '15px' }}>
                                                <div style={{ marginRight: '10px' }}>
                                                    <p>Montant total frais payer</p>
                                                </div>
                                                <div style={{ marginRight: '-40px' }}>
                                                    <input disabled value={this.state.mntFraisTotalPayer} style={{ border: 'solid', borderColor: '#DCDCDC', textAlign: 'center', width: '70%', borderRadius: '5px' }} type="text" />
                                                </div>
                                            </div>
                                            {paiementInscrip}
                                            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '15px' }}>
                                                <div style={{ marginRight: '10px' }}>
                                                    <p>Montant total inscription payer</p>
                                                </div>
                                                <div style={{ marginRight: '-40px' }}>
                                                    <input disabled value={this.state.mntIncripTotalPayer} style={{ border: 'solid', borderColor: '#DCDCDC', textAlign: 'center', width: '70%', borderRadius: '5px' }} type="text" />
                                                </div>
                                            </div>
                                        </div>
                                        <div className="row">
                                            <h3 style={{ paddingLeft: '15px' }}>Mode de paiement :</h3>
                                            <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                                <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                            </div>
                                            <table style={{ marginTop: '10px' }} class="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th style={{ textAlign: 'center' }} scope="col">Mode paiement</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Montant Paiement</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Numéro cheque</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Echéance cheque</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Date encaissement</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Titulaire cheque</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Banque</th>
                                                        <th style={{ textAlign: 'center', width: '15%' }} scope="col">Suppression</th>
                                                    </tr>
                                                </thead>
                                                {this.state.tabModPay.map((el, index) =>
                                                    <tbody>
                                                        <tr>
                                                            <td style={{ width: '90%' }}><Select
                                                                value={el.modPay}
                                                                isClearable={true}
                                                                isSearchable={true}
                                                                onChange={(e) => this.addModPay(e, index)}
                                                                options={this.state.listModPay}
                                                            />  </td>
                                                            <td style={{ textAlign: 'center' }}><input type="number" value={el.mntModPay} onChange={(e) => this.addMntModPay(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                            {this.state.tabModPay[index].modPay.value == 2 ?
                                                                (<Fragment>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" value={el.numCheque} onChange={(e) => this.addNumChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.echeanceChec} onChange={(e) => this.addEcheanceChec(e, index)} /></td>
                                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.dateEncaiss} onChange={(e) => this.addDateEncaiss(e, index)} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.titulaireChec} onChange={(e) => this.addTitulaireChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.banque} onChange={(e) => this.addBanque(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '35%' }} className='buttonSupprimer' onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                                </Fragment>) :
                                                                (<Fragment>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" disabled style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '35%' }} className='buttonSupprimer' onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                                </Fragment>)}
                                                        </tr>
                                                    </tbody>
                                                )}
                                            </table>
                                        </div>
                                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                            <button style={{ width: '15%', borderRadius: '15px' }} type="button" className="btn btn-success" onClick={this.ajoutRegInscrip}>Confirmer</button>
                                        </div>
                                    </Fragment>) : (null)}
                            </div>
                            <Modal isOpen={this.state.modal} toggle={this.nonDeleteLign} className={this.props.className} fade={false}>
                                <ModalHeader toggle={this.nonDeleteLign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                <ModalBody>
                                    <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                                            <p>Êtes-Vous sûr de vouloir supprimer ce mode de paiement ?</p>
                                        </div>
                                        <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                            <div><button onClick={() => this.deleteLign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                            <div><button onClick={() => this.nonDeleteLign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                        </div>
                                    </div>
                                </ModalBody>
                            </Modal>
                            <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifReglApp);
