import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class ModifMatiere extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idFormation: '',
            formations: [],
            ListFilieres: [],
            selectedFiliere: '',
            ListSpecialites: [],
            selectedSpecialite: '',
            nomFormation: '',
            ListNivBase: [],
            selectedNivBase: '',
            indexLignFormationASupprimer: '',
            idNivFomationASupprimer: '',
            tabIdNivFormationDelete: [],
            modal: false
        }
    }

    componentDidMount() {
        let idFormation = this.props.location.state.idFormation;
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetFormationById.php?id=' + idFormation).then(res => {
            console.log(res)
            this.setState({
                idFormation: res.data[0].idFormation,
                selectedFiliere: res.data[0].filiere,
                selectedSpecialite: res.data[0].specialite,
                nomFormation: res.data[0].nomFormation,
                selectedNivBase: res.data[0].nivBase,
                formations: res.data[1]
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php').then(res => {
            console.log(res)
            this.setState({
                ListFilieres: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                ListNivBase: res.data
            })
        })
    }

    additionLignFormation = () => {
        let element = { niveau: '', duree: '' }
        this.setState({
            formations: [...this.state.formations, element]
        })
    }

    updFiliere = selectedFiliere => {
        this.setState({ selectedFiliere }, () => {
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectSpecialites.php?id=' + selectedFiliere.value).then(res => {
                console.log(res)
                this.setState({
                    ListSpecialites: res.data
                })
            })
        });
    };

    updSpecialite = selectedSpecialite => {
        this.setState({ selectedSpecialite });
        console.log(`Specialite selected:`, selectedSpecialite);
    };

    updNomFormation = (event) => {
        this.setState({
            nomFormation: event.target.value
        });
    }

    updNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
        console.log(`NivBase selected:`, selectedNivBase);
    };

    updNiveau = (event, indexNiveau) => {
        let niveau = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNiveau ? Object.assign(el, { niveau: niveau }) : el))
        });
    }

    updDuree = (event, indexNiveau) => {
        let duree = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNiveau ? Object.assign(el, { duree: duree }) : el))
        });
    }

    toggleLignFormation = (idNiveau, indexLignFormation) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idNivFomationASupprimer: idNiveau,
            indexLignFormationASupprimer: indexLignFormation
        }));
    }

    deleteLignFormation = () => {
        let idNivFomationASupprimer = this.state.idNivFomationASupprimer
        this.setState({
            tabIdNivFormationDelete: [...this.state.tabIdNivFormationDelete, idNivFomationASupprimer]
        });
        this.state.formations.splice(this.state.indexLignFormationASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
