import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class ModifMagasin extends Component {
    constructor(props) {
        super(props);
        this.state = {
            magasinId: '',
            enseignes: [],
            selectedEnseigne: '',
            magasin: '',
            code: '',
            tel: '',
            adresse: '',
            articles: [],
            selectedArticle: '',
            // adresse
            address: '',
            errorMessage: '',
            latitude: null,
            longitude: null,
            isGeocoding: false,
            gmapsLoaded: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var idMagasin = this.props.location.state.idMagasin;
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetMagasinById.php?id=' + idMagasin).then(res => {
                this.setState({
                    magasinId: idMagasin,
                    selectedEnseigne: res.data.enseigne,
                    magasin: res.data.magasin,
                    code: res.data.code,
                    tel: res.data.tel,
                    selectedArticle: res.data.articles,
                    address: res.data.adresse,
                    latitude: res.data.latitude,
                    longitude: res.data.longitude,
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectEnseignes.php').then(res => {
                console.log(res)
                this.setState({
                    enseignes: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectArticles.php').then(res => {
                console.log(res)
                this.setState({
                    articles: res.data
                })
            })
            window.initMap = this.initMap
            const gmapScriptEl = document.createElement(`script`)
            gmapScriptEl.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyAhL4D_X4dhl-AW1E9HvG2k657SdqOEwEA&libraries=places&callback=initMap`
            document.querySelector(`body`).insertAdjacentElement(`beforeend`, gmapScriptEl)
        }
        else {
            document.location = '/'
        }
    }

    updEnseigne = selectedEnseigne => {
        if (selectedEnseigne != null) {
            this.setState({ selectedEnseigne: selectedEnseigne });
        }
        else {
            this.setState({ selectedEnseigne: '' });
        }
    }

    updMagasin = (event) => {
        this.setState({
            magasin: event.target.value
        });
    }

    updCode = (event) => {
        this.setState({
            code: event.target.value
        });
    }

    updTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    updArticle = selectedArticle => {
        if (selectedArticle != null) {
            this.setState({ selectedArticle: selectedArticle });
        }
        else {
            this.setState({ selectedArticle: '' });
        }
    };

    // adresse function

    handleChange = address => {
        this.setState({
            address,
            latitude: null,
            longitude: null,
            errorMessage: '',
        });
    };

    handleSelect = selected => {
        this.setState({ isGeocoding: true, address: selected });
        geocodeByAddress(selected)
            .then(res => getLatLng(res[0]))
            .then(({ lat, lng }) => {
                this.setState({
                    latitude: lat,
                    longitude: lng,
                    isGeocoding: false,
                });
            })
            .catch(error => {
                this.setState({ isGeocoding: false });
                console.log('error', error); // eslint-disable-line no-console
            });
    };

    handleCloseClick = () => {
        this.setState({
            address: '',
            latitude: null,
            longitude: null,
        });
    };

    handleError = (status, clearSuggestions) => {
        console.log('Error from Google Maps API', status); // eslint-disable-line no-console
        this.setState({ errorMessage: status }, () => {
            clearSuggestions();
        });
    };

    initMap = () => {
        this.setState({
            gmapsLoaded: true,
        }, () => {
            console.log('yes')
        })
    }

    modif = () => {
        if (this.state.selectedEnseigne != '' && this.state.magasin != '' && this.state.selectedArticle != '') {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/UpdMagasin.php',
                type: 'POST',
                data: {
                    magasinId: this.state.magasinId,
                    enseigneId: this.state.selectedEnseigne.value,
                    magasin: this.state.magasin,
                    code: this.state.code,
                    tel: this.state.tel,
                    adresse: this.state.address,
                    latitude: this.state.latitude,
                    longitude: this.state.longitude,
                    arrayArticles: this.state.selectedArticle
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.props.history.push('/Magasins');
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
        else {
            toast.error('⛔ Veuillez verifier les champs enseigne,magasin et articles !!', { containerId: 'A' });
        }
    }

    render() {
        const {
            address,
            errorMessage,
            latitude,
            longitude,
            isGeocoding,
            loading
        } = this.state;
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification magasin</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Magasins" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Enseigne</label>
                                            <Select
                                                value={this.state.selectedEnseigne}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updEnseigne}
                                                options={this.state.enseignes}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Magasin</label>
                                            <input type="text" className="form-control" value={this.state.magasin} onChange={this.updMagasin} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Code</label>
                                            <input type="text" className="form-control" value={this.state.code} onChange={this.updCode} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Tel</label>
                                            <input type="text" className="form-control" value={this.state.tel} onChange={this.updTel} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Articles</label>
                                            <Select
                                                value={this.state.selectedArticle}
                                                isMulti={true}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updArticle}
                                                options={this.state.articles}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        {this.state.gmapsLoaded && (
                                            <div>
                                                <PlacesAutocomplete
                                                    onChange={this.handleChange}
                                                    value={address}
                                                    onSelect={this.handleSelect}
                                                    onError={this.handleError}
                                                    shouldFetchSuggestions={address.length > 2}
                                                >
                                                    {({ getInputProps, suggestions, getSuggestionItemProps }) => {
                                                        return (
                                                            <div>
                                                                <label>Adresse</label>
                                                                <div style={{ display: 'flex' }}>
                                                                    {this.state.address.length > 0 ? (
                                                                        <input style={{ width: '95%', borderRadius: '5px', border: 'solid 1px #B3B3B3', height: '38px' }}
                                                                            {...getInputProps({
                                                                                placeholder: 'Recherche Places...',
                                                                                className: 'Demo__search-input',
                                                                            })}
                                                                        />
                                                                    ) : (<input style={{ width: '100%', borderRadius: '5px', border: 'solid 1px #B3B3B3', height: '38px' }}
                                                                        {...getInputProps({
                                                                            placeholder: 'Recherche Places...',
                                                                            className: 'Demo__search-input',
                                                                        })}
                                                                    />)}
                                                                    {this.state.address.length > 0 && (
                                                                        <button
                                                                            className="Demo__clear-button"
                                                                            onClick={this.handleCloseClick}
                                                                        >
                                                                            <i className="fas fa-remove-format"></i>
                                                                        </button>
                                                                    )}
                                                                </div>
                                                                <div className="autocomplete-dropdown-container">
                                                                    {loading && <div>Loading...</div>}
                                                                    {suggestions.map(suggestion => {
                                                                        // inline style for demonstration purpose
                                                                        const style = suggestion.active
                                                                            ? { backgroundColor: '#42a5f5', cursor: 'pointer' }
                                                                            : { backgroundColor: '#ffffff', cursor: 'pointer' };
                                                                        return (
                                                                            <div className="input-suggestion"
                                                                                {...getSuggestionItemProps(suggestion, {

                                                                                    style,
                                                                                })}
                                                                            >
                                                                                <i className="material-icons"></i> <span>{suggestion.description}</span>
                                                                            </div>
                                                                        );
                                                                    })}
                                                                </div>
                                                            </div>
                                                        );
                                                    }}
                                                </PlacesAutocomplete>
                                            </div>
                                        )}
                                        {errorMessage.length > 0 && (
                                            <div className="Demo__error-message">{this.state.errorMessage}</div>
                                        )}
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(ModifMagasin);
