import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'

class ModifNivBase extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idDiplome: '',
            diplome: ''
        }
    }

    componentDidMount() {
        let idNivBase = this.props.location.state.idNivBase;
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetDiplomeById.php?id=' + idNivBase).then(res => {
            console.log(res)
            this.setState({
                idDiplome: res.data.id,
                diplome: res.data.diplome
            })
        })
    }

    updDiplome = (event) => {
        this.setState({
            diplome: event.target.value
        });
    }

    Modif = () => {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdDiplome.php',
            type: 'POST',
            data: {
                diplome: this.state.diplome,
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/NivBase');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification niveau</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Diplome</label>
                                            <input type="text" class="form-control" value={this.state.diplome} onChange={this.updDiplome} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.Modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifNivBase);
