import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button } from 'reactstrap';

class AjoutArticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            compose: '',
            checkboxOui: '',
            checkboxNon: '',
            nom: '',
            unite: '',
            poids: '',
            prixUnitaire: '',
            prixKg: '',
            sousArticle: '',
            familleArticles: [],
            selectedFamilleArticles: '',
            tousSousArticles: [{ sousArticle: '' }],
            sousArticleAjouter: <input type="text" style={{ textAlign: 'center' }} />,
            combine: [{ article: '', quantite: '', unite: '', prix: '' }],
            articleAjouter: <input type="text" style={{ textAlign: 'center' }} />,
            quantiteAjouter: <input type="text" style={{ textAlign: 'center' }} />,
            uniteAjouter: <input type="text" style={{ textAlign: 'center' }} />,
            prixAjouter: <input type="text" style={{ textAlign: 'center' }} />
        }
        this.addNom = this.addNom.bind(this);
        this.addUnite = this.addUnite.bind(this);
        this.addPoids = this.addPoids.bind(this);
        this.addPrixUnitaire = this.addPrixUnitaire.bind(this);
        this.addPrixKg = this.addPrixKg.bind(this);
        this.addSousArticle = this.addSousArticle.bind(this);
        this.ajout = this.ajout.bind(this)
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/FamilleArticlesSelect.php').then(res => {
            console.log(res)
            this.setState({
                familleArticles: res.data
            })
        })
    }

    addNom(event) {
        this.setState({
            nom: event.target.value
        });
    }

    addUnite(event) {
        this.setState({
            unite: event.target.value
        });
    }

    addPoids(event) {
        this.setState({
            poids: event.target.value
        });
    }

    addPrixUnitaire(event) {
        this.setState({
            prixUnitaire: event.target.value
        });
    }

    addPrixKg(event) {
        this.setState({
            prixKg: event.target.value
        });
    }

    addSousArticle(event) {
        this.setState({
            sousArticle: event.target.value
        });
    }

    addFamilleArticles = selectedFamilleArticles => {
        this.setState({ selectedFamilleArticles });
        console.log(`Famille article selected:`, selectedFamilleArticles);
    };

    ajoutSousArticle = (id) => {
        let element = { sousArticleAjouter: this.state.sousArticleAjouter, idSousArticle: id }
        this.setState({
            tousSousArticles: [...this.state.tousSousArticles, element]
        })
    }

    ajoutCombine = (id) => {
        let element = { article: this.state.rate, quantite: this.state.name, unite: this.state.img, prix: this.state.date, idArticle: id }
        this.setState({
            combine: [...this.state.combine, element]
        })
    }

    ajout() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/AddArticle.php',
            type: 'POST',
            data: {
