import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

// const customStyles = {
//     rows: {
//       style: {
//         minHeight: '72px', 
//       }
//     },
//     headCells: {
//       style: {
//         paddingLeft: '8px', 
//         paddingRight: '8px',
//       },
//     },
//     cells: {
//       style: {
//         paddingLeft: '8px',
//         paddingRight: '8px',
//       },
//     },
//   };

class ReglApprenant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Num inscrip',
                    selector: 'numInscrip',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Apprenant',
                    selector: 'apprenant',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Type paiement',
                    selector: 'typePaiement',
                    sortable: false,
                    center: true
                },
                // {
                //     name: 'Frais réglé',
                //     cell: row => <table class="table table-bordered">
                //         <thead>
                //             <tr>
                //                 <th style={{ textAlign: 'center' }} scope="col">Frais</th>
                //                 <th style={{ textAlign: 'center' }} scope="col">Montant</th>
                //             </tr>
                //         </thead>
                //         {row.reglFrais.map((el, index) =>
                //             <tbody>
                //                 <tr>
                //                     <td style={{ textAlign: 'center' }}><p>{el.frais}</p></td>
                //                     <td style={{ textAlign: 'center' }}><p>{el.mntPayer}</p></td>
                //                 </tr>
                //             </tbody>
                //         )}
                //     </table>,
                //     ignoreRowClick: true,
                //     allowOverflow: true,
                //     button: true,
                // },
                {
                    name: 'Reglement inscription',
                    cell: row => <table style={{ width: '450px' }} class="table table-bordered">
                        <thead>
                            <tr>
                                <th style={{ textAlign: 'center' }} scope="col">Date</th>
                                <th style={{ textAlign: 'center' }} scope="col">Tranche</th>
                                <th style={{ textAlign: 'center' }} scope="col">Etat</th>
                            </tr>
                        </thead>
                        {row.reglInscrip.map((el, index) =>
                            <tbody>
                                <tr>
                                    <td style={{ textAlign: 'center' }}><p>{el.date}</p></td>
                                    <td style={{ textAlign: 'center' }}><p>{index + 1 + ' tranche'}</p></td>
                                    <td style={{ textAlign: 'center' }}><p>{el.etat}</p><p>{el.mntNonRegle}</p></td>
                                    {el.etat !== '' ?
                                        (<td style={{ textAlign: 'center' }}><div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}><i class="fas fa-info"></i><p onClick={() => this.attPay(el, index)}>Attestation de paiement</p></div></td>) :
                                        (null)}
                                </tr>
                            </tbody>
                        )}
                    </table>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
                // {
                //     name: 'Modifier',
                //     cell: row => <Link to={{
                //         pathname: '/ModifReglApprenant',
                //         state: { idRegl: row.idRegl }
                //     }}><button className='buttonModifier'><i style={{ color: 'white' }} class="fas fa-pencil-alt"></i></button></Link>,
                //     ignoreRowClick: true,
                //     allowOverflow: true,
                //     button: true,
                // }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idRegl: '',
            pending: true,
            // search: [{ nom: '', prenom: '', cin: '' }]
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        }
    }

    attPay = (el, index) => {
        var idEnteteRegl = el[index].idEnteteRegl
        var numTranchePay = el[index].numTrRegle
        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Php_client_jasper/Report_jasper_recu_regl_inscrip.php',
        //     type: 'POST',
        //     data: {
        //         idEnteteRegl: idEnteteRegl,
        //         numTranchePay:
        //     },
        //     dataType: 'json',
        //     success: function (code_html, statut) {
        //         var server_path = code_html.server_path;
        //         var file_path = code_html.file_path
        //         var path = server_path + file_path;
        //         window.open(path, "_blank")
        //     }
        // });
    }

    newElement(page, totalRows) {
        this.setState({
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: page
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }
        )
    }

    perPage(currentRowsPerPage, currentPage) {
        this.setState(
            {
                nbrEltperPage: currentRowsPerPage,
                pending: true
            }
            , () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: currentRowsPerPage,
                        page: currentPage
                    },
