import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import ReactApexChart from 'react-apexcharts'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import './paginate.css'

class StatDemandesTravailleurs extends Component {
    constructor(props) {
        super(props);
        this.state = {
            series: [{
                name: 'Nombre de demande valider',
                data: []
            },
            {
                name: 'Nombre de demande cloturer',
                data: []
            }],
            options: {
                chart: {
                    type: 'bar',
                    height: 350,
                    stacked: true,
                    toolbar: {
                        show: true
                    },
                    zoom: {
                        enabled: true
                    }
                },
                responsive: [{
                    breakpoint: 480,
                    options: {
                        legend: {
                            position: 'bottom',
                            offsetX: -10,
                            offsetY: 0
                        }
                    }
                }],
                plotOptions: {
                    bar: {
                        horizontal: false,
                    },
                },
                xaxis: {
                    type: 'text',
                    categories: []
                },
                legend: {
                    position: 'right',
                    offsetY: 40
                },
                fill: {
                    opacity: 1
                }
            },
            selectedService: null, // Le service selectionné
            services: null, // Didmount : tous les services
            startDate: new Date(),
            endDate: new Date()
        };
        this.ajout = this.ajout.bind(this);
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/TousServices.php').then(res => {
            console.log(res)
            this.setState({
                services: res.data
            })
        })
    }

    handleStart = d => {
        this.setState({
            startDate: d
        });
    };

    handleEnd = d => {
        this.setState({
            endDate: d
        });
    };

    choixService = selectedService => {
        this.setState({ selectedService });
        console.log(`service selected:`, selectedService);
    };

    ajout() {
        let month = '' + (this.state.startDate.getMonth() + 1);
        let day = '' + this.state.startDate.getDate();
        let year = this.state.startDate.getFullYear();
        if (month.length < 2)
            month = '0' + month;
        if (day.length < 2)
            day = '0' + day;
        let startDate = [year, month, day].join('-');
        console.log(startDate)
        /////////////////////
        let mois = '' + (this.state.endDate.getMonth() + 1);
        let jour = '' + this.state.endDate.getDate();
        let annee = this.state.endDate.getFullYear();
        if (mois.length < 2)
            mois = '0' + mois;
        if (jour.length < 2)
            jour = '0' + jour;
        let endDate = [annee, mois, jour].join('-');
        console.log(endDate)
        /////////////////////
        console.log(this.state.selectedService.value)
        /////////////////////
        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Back_snaii/StatTravailleursDemandes.php',
        //     type: 'POST',
        //     data: {
        //         startDate: startDate,
        //         endDate: endDate,
        //         idService: this.state.selectedService.value
        //     },
        //     dataType: 'json',
        //     success: function (code_html, statut) {
        //         console.log(code_html)
        //         this.setState({
        //             options: {
        //                 ...this.state.options,
        //                 xaxis: {
        //                     ...this.state.options.xaxis,
        //                     categories: code_html[0]
        //                 }
        //             }
        //         })
        //         var newSerieTravailleursParDemandeValider = []
        //         let nombreDeTravailleursParDemandeValider = code_html[1];
        //         newSerieTravailleursParDemandeValider.push({ data: nombreDeTravailleursParDemandeValider, name: 'Demandes Validées' })
        //         var newSerieTravailleursParDemandeCloturer = []
        //         let nombreDeTravailleursParDemandeCloturer = code_html[2];
        //         newSerieTravailleursParDemandeCloturer.push({ data: nombreDeTravailleursParDemandeCloturer, name: 'Demandes Clôturées' })
        //         let tous = []
        //         let demandes = tous.concat(newSerieTravailleursParDemandeValider, newSerieTravailleursParDemandeCloturer)
        //         console.log(demandes)
        //         this.setState({
        //             series: demandes
        //         })
        //     }.bind(this)
        // });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Statistique travailleurs par demande</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div style={{ display: 'flex', justifyContent: 'center' }} class="row">
                                    <div class="col-md-3">
                                        <div class="form-group">
                                            <label style={{ marginRight: '5px' }}>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.startDate}
                                                onChange={this.handleStart}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-3">
                                        <div class="form-group">
                                            <label style={{ marginRight: '5px' }}>Date fin</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.endDate}
                                                onChange={this.handleEnd}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center' }} class="row">
                                    <div class="col-md-3">
                                        <div class="form-group">
                                            <div style={{ display: 'flex', alignItems: 'center' }}>
                                                <label style={{ marginRight: '5px' }}>Services</label>
                                                <Select
                                                    className={'reactSelect'}
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.choixService}
                                                    options={this.state.services}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '10px', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout.bind(this)}>Confirmer</button>
                                </div>
                                {this.state.series[0].data != '' ?
                                    (<div class="row">
                                        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                                            <div id="chart">
                                                <ReactApexChart options={this.state.options} series={this.state.series} type="bar" width={700} height={450} />
                                            </div>
                                        </div>
                                    </div>) : (null)}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default StatDemandesTravailleurs;
