import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'

class ModifApprenant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            apprenantId: '',
            prenom: '',
            nom: '',
            selectedType: 'CIN',
            numSelonType: '',
            nationalite: '',
            dateDelivSelonType: new Date(),
            villeDelivreSelonType: '',
            dateNaissance: new Date(),
            lieuNaissance: '',
            email: '',
            selectedGender: 'Masculin',
            mobile: '',
            tel: '',
            adresseOfficiel: '',
            adresseTun: '',
            listNivBase: [],
            selectedNivBase: '',
            imageVerso: null
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            let idApprenant = this.props.location.state.idApprenant;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetApprenantById.php?id=' + idApprenant).then(res => {
                console.log(res)
                this.setState({
                    apprenantId: res.data.id,
                    prenom: res.data.prenom,
                    nom: res.data.nom,
                    selectedType: res.data.type,
                    numSelonType: res.data.numero,
                    nationalite: res.data.nationalite,
                    dateDelivSelonType: new Date(res.data.dateNum),
                    villeDelivreSelonType: res.data.villeNum,
                    dateNaissance: new Date(res.data.naissance),
                    lieuNaissance: res.data.villeNaissance,
                    adresseOfficiel: res.data.adrOfficiel,
                    adresseTun: res.data.adresseTun,
                    email: res.data.email,
                    selectedGender: res.data.sex,
                    mobile: res.data.mobile,
                    selectedNivBase: res.data.nivBase,
                    tel: res.data.tel
                })
            })
        }
    }

    updPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    updNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    updType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    updNumSelonType = (event) => {
        this.setState({
            numSelonType: event.target.value
        });
    }

    updNationalite = (event) => {
        this.setState({
            nationalite: event.target.value
        });
    }

    updDateLivraison = d => {
        this.setState({
            dateDelivSelonType: d
        });
    };

    updVilleDelivreSelonType = (event) => {
        this.setState({
            villeDelivreSelonType: event.target.value
        });
    }

    updDateNaissance = d => {
        this.setState({
            dateNaissance: d
        });
    };

    updLieuNaissance = (event) => {
        this.setState({
            lieuNaissance: event.target.value
        });
    }

    updAdresseOfficiel = (event) => {
        this.setState({
            adresseOfficiel: event.target.value
        });
    }

    updAdresseTun = (event) => {
        this.setState({
            adresseTun: event.target.value
        });
    }

    updEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    updGender = (selectedGender) => {
        this.setState({ selectedGender: selectedGender });
    }

    updMobile = (event) => {
        this.setState({
            mobile: event.target.value
        });
    }

    updTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    UpdNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
        console.log(`nivBase selected:`, selectedNivBase);
    };

    updImageVerso(e) {
        this.setState({ imageVerso: e.target.files })
    }

    modif = () => {
        console.log(this.state.prenom)
        console.log(this.state.nom)
        console.log(this.state.selectedType)
        console.log(this.state.numSelonType)
        console.log(this.state.nationalite)
        let dateDelivSelonType = this.state.dateDelivSelonType
        let month_one = '' + (dateDelivSelonType.getMonth() + 1);
        let day_one = '' + dateDelivSelonType.getDate();
        let year_one = dateDelivSelonType.getFullYear();
        if (month_one.length < 2)
            month_one = '0' + month_one;
        if (day_one.length < 2)
            day_one = '0' + day_one;
        let convertDateDelivSelonType = [year_one, month_one, day_one].join('-');
        console.log(convertDateDelivSelonType)
        console.log(this.state.villeDelivreSelonType)
        let dateNaissance = this.state.dateNaissance
        let month_two = '' + (dateNaissance.getMonth() + 1);
        let day_two = '' + dateNaissance.getDate();
        let year_two = dateNaissance.getFullYear();
        if (month_two.length < 2)
            month_two = '0' + month_two;
        if (day_two.length < 2)
            day_two = '0' + day_two;
        let convertDateNaissance = [year_two, month_two, day_two].join('-');
        console.log(convertDateNaissance)
        console.log(this.state.lieuNaissance)
        console.log(this.state.adresseOfficiel)
        console.log(this.state.adresseTun)
        console.log(this.state.email)
        console.log(this.state.selectedGender)
        console.log(this.state.mobile)
        console.log(this.state.tel)
        console.log(this.state.selectedNivBase.value)
        console.log(this.state.imageVerso)
        // // Test si photo vide
        // var pathImageVerso = '';
        // console.log(this.state.imageVerso)
        // if (this.state.imageVerso != null) {
        //     pathImageVerso = "images/Photo_Enseignants/" + this.state.imageVerso[0].name
        // }
        // var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        // var userId = userPermissions[0].idUser
        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdEnseignant.php?id=' + this.state.apprenantId,
        //     type: 'POST',
        //     data: {
        //         prenom: this.state.prenom,
        //         nom: this.state.nom,
        //         type: this.state.selectedType,
        //         numero: this.state.numSelonType,
        //         nationalite: this.state.nationalite,
        //         dateType: convertDateDelivSelonType,
        //         villeType: this.state.villeDelivreSelonType,
        //         dateNaissance: convertDateNaissance,
        //         lieuNaissance: this.state.lieuNaissance,
        //         adresseOfficiel: this.state.adresseOfficiel,
        //         adresseTun: this.state.adresseTun,
        //         email: this.state.email,
        //         sexe: this.state.selectedGender,
        //         mobile: this.state.mobile,
        //         tel: this.state.tel,
        //         photo: pathImageVerso,
        //         qualification: this.state.qualification,
        //         userId: userId
        //     },
        //     dataType: 'json',
        //     success: (code_html, statut) => {
        //         if (code_html.Result == "OK") {
        //             this.props.history.push('/Enseignants');
        //         }
        //         else if (code_html.Result == "KO") {
        //             alert("erreur,l'un de vos champs est vide!!");
        //         }
        //     }
        // });
        // // Test si photo vide
        // if (this.state.imageVerso != null) {
        //     let files = this.state.imageVerso;
        //     console.warn('data files', files)
        //     let reader = new FileReader();
        //     reader.readAsDataURL(files[0])
        //     reader.onload = (e) => {
        //         console.log(e)
        //         jQuery.ajax({
        //             url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Uploadphoto.php',
        //             type: 'POST',
        //             data: {
        //                 file: e.target.result,
        //                 nomfile: this.state.imageVerso[0].name
        //             },
        //             dataType: 'json',
        //             success: function (code_html, statut) {
        //             }
        //         }
        //         )
        //     }
        // }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification enseignant</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Prnom</label>
                                            <input type="text" class="form-control" value={this.state.prenom} onChange={this.updPrenom} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom</label>
                                            <input type="text" class="form-control" value={this.state.nom} onChange={this.updNom} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Sexe</label>
                                        <RadioGroup
                                            name="sexe"
                                            selectedValue={this.state.selectedGender}
                                            onChange={this.updGender}>
                                            <label>
                                                <Radio value="Masculin" />Masculin
                                                </label>
                                            <label>
                                                <Radio value="Feminin" />Feminin
                                                </label>
                                        </RadioGroup>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Type</label>
                                        <RadioGroup
                                            name="type"
                                            selectedValue={this.state.selectedType}
                                            onChange={this.updType}>
                                            <label>
                                                <Radio value="CIN" />CIN
                                                </label>
                                            <label>
                                                <Radio value="Passeport" />Passeport
                                                </label>
                                        </RadioGroup>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Numero</label>
                                            <input type="text" class="form-control" value={this.state.numSelonType} onChange={this.updNumSelonType} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nationalit</label>
                                            <input type="text" class="form-control" value={this.state.nationalite} onChange={this.updNationalite} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Delivr le</label>
                                        <div class="form-group">
                                            <DatePicker class="form-control ddate" value={this.state.dateDelivSelonType} style={{ border: 'none' }}
                                                selected={this.state.dateDelivSelonType}
                                                onChange={this.updDateLivraison}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Delivr ville de</label>
                                            <input type="text" class="form-control" value={this.state.villeDelivreSelonType} onChange={this.updVilleDelivreSelonType} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Date naissance</label>
                                        <div class="form-group">
                                            <DatePicker class="form-control ddate" value={this.state.dateNaissance} style={{ border: 'none' }}
                                                selected={this.state.dateNaissance}
                                                onChange={this.updDateNaissance}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Lieu de naissance</label>
                                            <input type="text" class="form-control" value={this.state.lieuNaissance} onChange={this.updLieuNaissance} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Adresse officiel</label>
                                            <input type="text" class="form-control" value={this.state.adresseOfficiel} onChange={this.updAdresseOfficiel} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Adresse en tunisie</label>
                                            <input type="text" class="form-control" value={this.state.adresseTun} onChange={this.updAdresseTun} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Email</label>
                                            <input type="text" class="form-control" value={this.state.email} onChange={this.updEmail} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Mobile</label>
                                            <input type="text" class="form-control" value={this.state.mobile} onChange={this.updMobile} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Tel</label>
                                            <input type="text" class="form-control" value={this.state.tel} onChange={this.updTel} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Niveau de base</label>
                                            <Select
                                                value={this.state.selectedNivBase}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addNivBase}
                                                options={this.state.listNivBase}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Photo verso </label>
                                            <input type="file" class="form-control" onChange={(e) => this.updImageVerso(e)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifApprenant);
