import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button } from 'reactstrap';

class AjoutArticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            compose: '',
            checkboxOui: '',
            checkboxNon: '',
            nom: '',
            selectedUnite: '',
            poids: '',
            prixUnitaire: '',
            prixKg: '',
            sousArticle: '',
            unites: [],
            familleArticles: [],
            selectedFamilleArticles: '',
            tousSousArticles: [{ sousArticle: '' }],
            sousArticleAjouter: '',
            combine: [{ article: '', quantite: '', unite: '', prix: '' }],
            articleAjouter: '' ,
            quantiteAjouter: '' ,
            uniteAjouter: '' ,
            prixAjouter: '' 
        }
        this.addNom = this.addNom.bind(this);
        this.addPoids = this.addPoids.bind(this);
        this.addPrixUnitaire = this.addPrixUnitaire.bind(this);
        this.addPrixKg = this.addPrixKg.bind(this);
        this.additionSousArticle = this.additionSousArticle.bind(this);
        this.additionCombine = this.additionCombine.bind(this);
        this.ajout = this.ajout.bind(this)
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/FamilleArticlesSelect.php').then(res => {
            console.log(res)
            this.setState({
                familleArticles: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/UnitesSelect.php').then(res => {
            console.log(res)
            this.setState({
                unites: res.data
            })
        })
    }

    addNom(event) {
        this.setState({
            nom: event.target.value
        });
    }

    addUnite = selectedUnite => {
        this.setState({ selectedUnite });
        console.log(`Unite selected:`, selectedUnite);
    };

    addPoids(event) {
        this.setState({
            poids: event.target.value
        });
    }

    addPrixUnitaire(event) {
        this.setState({
            prixUnitaire: event.target.value
        });
    }

    addPrixKg(event) {
        this.setState({
            prixKg: event.target.value
        });
    }

    addSousArticle = (event,idSousArticle) => {

        let nomSousArt = event.target.value;
         this.setState({
             tousSousArticles: this.state.tousSousArticles.map((el,id) => (id === idSousArticle ? Object.assign( {}, { sousArticle:nomSousArt  }) : el))
         });
    }

    addFamilleArticles = selectedFamilleArticles => {
        this.setState({ selectedFamilleArticles });
        console.log(`Famille article selected:`, selectedFamilleArticles);
    };

    additionSousArticle() {
        let element = { sousArticle: this.state.sousArticleAjouter }
        this.setState({
            tousSousArticles: [...this.state.tousSousArticles, element]
        })
    }

    additionCombine() {
        let element = { article: this.state.rate, quantite: this.state.name, unite: this.state.img, prix: this.state.date }
        this.setState({
            combine: [...this.state.combine, element]
        })
    }

    // addArticle = (event, id) => {
    //     this.setState({
    //         // articleAjouter: event.target.value
    //     });
    // }

    // addQuantite = (event, id) => {
    //     this.setState({
    //         // quantiteAjouter: event.target.value
    //     });
    // }

    // addUniteDeux = (event, id) => {
    //     this.setState({
    //         // uniteAjouter: event.target.value
    //     });
    // }

    // addPrix = (event, id) => {
    //     this.setState({
    //         // prixAjouter: event.target.value
    //     });
    // }

    ajout() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/AddArticle.php',
            type: 'POST',
            data: {
                nomUnite: this.state.nomUnite,
                poids: this.state.poids,
                prixUnitaire: this.state.prixUnitaire,
                prixKg: this.state.prixKg,
                sousArticle: this.state.sousArticle,
                idFamilleArticles: this.state.selectedFamilleArticles.value
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Articles');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    chouf = () => {
        console.log(this.state.tousSousArticles)
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout article</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div style={{ display: 'flex' }} class="col-md-6">
                                        <label>Composé</label>
                                        <div style={{ display: 'flex', justifyContent: 'space-around', width: '100%' }}>
                                            <div style={{ display: 'flex' }} class="form-group">
                                                <label style={{ marginRight: '5px' }}>Oui</label>
                                                <input type="checkbox" />
                                            </div>
                                            <div style={{ display: 'flex' }} class="form-group">
                                                <label style={{ marginRight: '5px' }}>Non</label>
                                                <input type="checkbox" />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div class="row">
                                    <table class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Unité</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Poids</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix unitaire piece</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix unitaire/kg</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Famille</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <tr>
                                                <td style={{ width: '10px' }}><input type="text" placeholder="Nom" onChange={this.addNom.bind(this)} style={{ textAlign: 'center' }} /></td>
                                                <td style={{ width: '115px' }}><Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addunite}
                                                    options={this.state.unites}
                                                />  </td>
                                                <td style={{ width: '10px' }}><input type="number" placeholder="Poids" onChange={this.addPoids.bind(this)} style={{ textAlign: 'center' }} /></td>
                                                <td style={{ width: '10px' }}><input type="number" placeholder="Prix unitaire" onChange={this.addPrixUnitaire.bind(this)} style={{ textAlign: 'center' }} /></td>
                                                <td style={{ width: '10px' }}><input type="number" placeholder="Prix par kg" onChange={this.addPrixKg.bind(this)} style={{ textAlign: 'center' }} /></td>
                                                <td style={{ width: '115px' }}><Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addFamilleArticles}
                                                    options={this.state.familleArticles}
                                                />  </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </div>
                                <div class="row">
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionSousArticle.bind(this)} color="secondary">Ajouter ligne</Button>
                                    </div>
                                    <table style={{ width: '10%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Sous article</th>
                                            </tr>
                                        </thead>
                                        {this.state.tousSousArticles.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td><input type="text" placeholder="Sous article" onChange={(e)=>this.addSousArticle(e,index)} style={{ textAlign: 'center' }} />{el.sousArticle}</td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                <button onClick={this.chouf}>clic me</button>
                                {/* <div class="row">
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionCombine.bind(this)} color="secondary">Ajouter ligne</Button>
                                    </div>
                                    <table style={{ width: '65%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Article</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Quantité</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Unité</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix</th>
                                            </tr>
                                        </thead>
                                        {this.state.combine.map((el, id) =>
                                            <tbody>
                                                <tr>
                                                    <td><input type="text" placeholder='Article' onChange={this.addArticle(id)} style={{ textAlign: 'center' }} />{el.article}</td>
                                                    <td><input type="number" placeholder='Quantité' onChange={this.addQuantite(id)} style={{ textAlign: 'center' }} />{el.quantite}</td> */}
                                {/* <td><Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addunite(id)}
                                                        options={this.state.unites}
                                                    />  </td> */}
                                {/* <td><input type="text" placeholder='Prix' onChange={this.addPrix(id)} style={{ textAlign: 'center' }} />{el.prix}</td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div> */}
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutArticle);
