import React, { Component } from 'react'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'

class AjoutSeanceAbsent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listFormation: [],
            selectedFormation: '',
            listMatiere: [],
            selectedMatiere: '',
            listEnseignant: [],
            selectedEnseignant: '',
            listGroupe: [],
            selectedGroupe: '',
            dateSeance: new Date(),
            heureDebut: new Date(),
            heureFin: new Date(),
            listEleve: []
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectMatFor.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listMatiere: code_html
                });
            }
        });
    }

    choixMatiere = selectedMatiere => {
        if (selectedMatiere != null) {
            this.setState({ selectedMatiere }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEnsMatFor.php',
                    type: 'POST',
                    data: {
                        matiereId: selectedMatiere.value
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            listEnseignant: code_html
                        });
                    }
                });
            })
        }
    }

    choixEnseignant = selectedEnseignant => {
        if (selectedEnseignant != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            this.setState({ selectedEnseignant }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupParEnseign.php',
                    type: 'POST',
                    data: {
                        ensId: selectedEnseignant.value,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            listGroupe: code_html
                        });
                    }
                });
            })
        }
    };

    choixGroupe = selectedGroupe => {
        if (selectedGroupe != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            this.setState({ selectedGroupe }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetEleveByGroup.php',
                    type: 'POST',
                    data: {
                        groupId: selectedGroupe.value,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            listEleve: code_html
                        });
                    }
                });
            })
        }
    };

    addDateSeance = d => {
        this.setState({
            dateSeance: d
        });
    }

    addEtat = (event, index) => {
        let etat = event;
        this.setState({
            listEleve: this.state.listEleve.map((el, id) => (id === index ? Object.assign(el, { etat: etat }) : el))
        });
    }

    ajout = () => {
        // var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        // var userId = userPermissions[0].idUser
        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddRole.php',
        //     type: 'POST',
        //     data: {
        //         role: this.state.role,
        //         userId: userId
        //     },
        //     dataType: 'json',
        //     success: (code_html, statut) => {
        //         if (code_html.Result == "OK") {
        //             this.props.history.push('/Roles');
        //         }
        //         else if (code_html.Result == "KO") {
        //             alert("erreur,l'un de vos champs est vide!!");
        //         }
        //     }
        // });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout séance</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Matiére</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.choixMatiere}
                                                options={this.state.listMatiere}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Enseignant</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.choixEnseignant}
                                                options={this.state.listEnseignant}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Groupe</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.choixGroupe}
                                                options={this.state.listGroupe}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date séance</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateSeance}
                                                onChange={this.addDateSeance}
                                            />
                                        </div>
                                    </div>
                                </div>
                                {this.state.listEleve.length > 0 ?
                                    (<div className="row">
                                        <table style={{ marginTop: '10px' }} className="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Prenom</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Assiduité</th>
                                                </tr>
                                            </thead>
                                            {this.state.listEleve.map((el, index) =>
                                                <tbody>
                                                    <tr>
                                                        <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{el.prenom}</p></td>
                                                        <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{el.nom}</p></td>
                                                        <td style={{ textAlign: 'center' }}><RadioGroup
                                                            name="asseduite"
                                                            selectedValue={this.state.selectedEtat}
                                                            onChange={this.addEtat}>
                                                            <div>
                                                                <label style={{ paddingRight: '10px' }}>
                                                                    <Radio value={1} /> Absent
                                                    </label>
                                                            </div>
                                                        </RadioGroup></td>
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                    </div>) : (null)}
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutSeanceAbsent);
