import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class AjoutEtudPfi extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filiere: [],
            selectedFiliere: '',
            niveau: [],
            selectedNiveau: '',
            groupe: [],
            selectedGroupe: '',
            eleve: [],
            selectedElv: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        filiere: code_html
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    addFiliere = selectedFiliere => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedFiliere: selectedFiliere }, () => {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivByfiliere.php',
                type: 'POST',
                data: {
                    filiereId: selectedFiliere.value,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        niveau: code_html
                    });
                }
            });
        });
    };

    addNiveau = selectedNiveau => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedNiveau: selectedNiveau }, () => {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupByNiveau.php',
                type: 'POST',
                data: {
                    niveauId: selectedNiveau.value,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        groupe: code_html
                    });
                }
            })
        });
    };

    addGroupe = selectedGroupe => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedGroupe: selectedGroupe }, () => {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEtudByNiv.php',
                type: 'POST',
                data: {
                    groupeId: selectedGroupe.value,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        eleve: code_html
                    });
                }
            })
        });
    };

    addElv = selectedElv => {
        this.setState({ selectedElv: selectedElv });
    };

    // ajout = () => {
    //     if (this.state.selectedElv != '') {
    //         var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
    //         var userId = userPermissions[0].idUser
    //         var anneeScolaire = userPermissions[0].anneeScolaire
    //         jQuery.ajax({
    //             url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddEtudPfi.php',
    //             type: 'POST',
    //             data: {
    //                 arrayElvId: this.state.selectedElv,
    //                 userId: userId,
    //                 anneeScolaire: anneeScolaire
    //             },
    //             dataType: 'json',
    //             success: (code_html, statut) => {
    //                 if (code_html.Result == "OK") {
    //                     this.props.history.push('/EtudiantsDiplome');
    //                 }
    //                 else if (code_html.Result == "KO") {
    //                     alert("erreur,l'un de vos champs est vide!!");
    //                 }
    //             }
    //         });
    //     }
    //     else {
    //         toast.error('⛔ veuillez verifier le champ eleve', { containerId: 'A' });
    //     }
    // }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout etudiant (diplome)</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/EtudiantsDiplome"> <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Formation</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.addFiliere(e)}
                                                options={this.state.filiere}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Niveaux</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.addNiveau(e)}
                                                options={this.state.niveau}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Groupes</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.addGroupe(e)}
                                                options={this.state.groupe}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Eléves</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                isMulti={true}
                                                onChange={(e) => this.addElv(e)}
                                                options={this.state.eleve}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutEtudPfi);
