import React, { Component, Fragment } from 'react'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';

class ModifReglApp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listEleve: [],
            selectedEleve: '',
            reglEleve: ''
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listEleve: code_html
                });
            }
        });
    }

    selectEleve = (selectedEleve) => {
        if (selectedEleve != null) {
            this.setState({ selectedEleve: selectedEleve }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetReglByEleve.php',
                    type: 'POST',
                    data: {
                        eleveId: this.state.selectedEleve.value
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            reglEleve: code_html
                        });
                    }
                });
            });
        }
        else {
            this.setState({
                reglEleve: ''
            });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Mise à jour reglement apprenant</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-3">
                                        <div class="form-group">
                                            <label>Eléves</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.selectEleve(e)}
                                                options={this.state.listEleve}
                                            />
                                        </div>
                                    </div>
                                    {this.state.reglEleve.length != '' ?
                                        (<Fragment>
                                            <div class="col-md-3">
                                                <div class="form-group">
                                                    <label>Groupe</label>
                                                    <input type="text" disabled value={this.state.reglEleve.groupe} class="form-control" style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-3">
                                                <div class="form-group">
                                                    <label>Niveau</label>
                                                    <input type="text" disabled value={this.state.reglEleve.niveau} class="form-control" style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-3">
                                                <div class="form-group">
                                                    <label>formation</label>
                                                    <input type="text" disabled value={this.state.reglEleve.formation} class="form-control" style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-3">
                                                <div class="form-group">
                                                    <label>Specialite</label>
                                                    <input type="text" disabled value={this.state.reglEleve.nomSpecialite} class="form-control" style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                                </div>
                                            </div>
                                        </Fragment>) : (null)
                                    }
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifReglApp);
