import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable, { createTheme } from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Collapsible from 'react-collapsible';
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class ReglApprenantFrais extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Num inscrip',
                    selector: 'idIns',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Apprenant',
                    selector: 'nomComplet',
                    sortable: false,
                    center: true,
                    style: { width: '20%' }
                },
                {
                    name: 'Frais réglé',
                    selector: 'nomFrais',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Montant',
                    selector: 'montant',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Date réglement',
                    selector: 'date',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Modif',
                    cell: row => <Link to={{
                        pathname: '/MiseAJourReglFor',
                        state: { idRegFor: row.idRegFor }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    cell: row => <Link to={{
                        pathname: '/MiseAJourReglFor',
                        state: { idRegFor: row.idRegFor }
                    }}><button className='buttonSupprimer'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Detail',
                    cell: row => <div onClick={() => this.reglFrais(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px' }}><p>Detail</p></div></div>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                // {
                //     name: 'Imp',
                //     cell: row => row.reglInscrip.length > 0 ? (<div onClick={() => this.reglApprenant(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px' }}><p>Reglement</p></div></div>) : (null),
                //     ignoreRowClick: true,
                //     allowOverflow: true,
                //     button: true,
                //     width: '10%'
                // }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idRegl: '',
            pending: true,
            reglInscrip: [],
            modalFrais: false,
            reglFraisFormat: [],
            // search
            listEleve: [],
            selectedIdEleve: '',
            dateDebut: '',
            dateFin: '',
            // Condition session
            idRoleUser: ''
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            var idRoleUser = userPermissions[0].idRoleUser
            this.setState({
                idRoleUser: idRoleUser
            });
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppFrais.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        listEleve: code_html
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    reglFrais = (row) => {
        this.setState({
            reglFraisFormat: row.reglFrais
        }, () => {
            this.setState(prevState => ({
                modalFrais: !prevState.modalFrais
            }));
        });
    }

    toggleFrais = () => {
        this.setState(prevState => ({
            modalFrais: !prevState.modalFrais
        }));
    }

    recuFraisFor = (el) => {
        var idRegFor = el.idRegFor
        var incluEsp = false
        var incluChec = false
        // selon le type de paiement
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ApprenantsTypePayFor.php',
            type: 'POST',
            data: {
                idRegFor: idRegFor
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    arrayModePaiement: code_html
                }, () => {
                    if (this.state.arrayModePaiement.includes('1') || this.state.arrayModePaiement.includes('3')) {
                        incluEsp = true
                    }
                    if (this.state.arrayModePaiement.includes('2')) {
                        incluChec = true
                    }
                    if (incluChec == true) {
                        // Recu tranche
                        jQuery.ajax({
                            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/RecuRegForFraisChec.php',
                            type: 'POST',
                            data: {
                                idRegFor: idRegFor
                            },
                            dataType: 'json',
                            success: (code_html, statut) => {
                                var server_path = code_html.server_path;
                                var file_path = code_html.file_path
                                var path = server_path + file_path;
                                window.open(path, "_blank")

                            }
                        });
                    }
                    else if (incluEsp == true) {
                        // Recu tranche
                        jQuery.ajax({
                            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/RecuRegForFraisEsp.php',
                            type: 'POST',
                            data: {
                                idRegFor: idRegFor
                            },
                            dataType: 'json',
                            success: (code_html, statut) => {
                                var server_path = code_html.server_path;
                                var file_path = code_html.file_path
                                var path = server_path + file_path;
                                window.open(path, "_blank")
                            }
                        });
                    }
                })
            }
        })
    }

    newElement(page, totalRows) {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedIdEleve != '') {
            var idEleve = this.state.selectedIdEleve.value
        }
        else {
            var idEleve = ''
        }
        // convertir date debut
        if (this.state.dateDebut != '') {
            var dateDebut = this.state.dateDebut
            let monthOne = '' + (dateDebut.getMonth() + 1);
            let dayOne = '' + dateDebut.getDate();
            let yearOne = dateDebut.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            var convertDateDebut = [yearOne, monthOne, dayOne].join('-');
        }
        else {
            var convertDateDebut = '';
        }
        // convertir date fin
        if (this.state.dateFin != '') {
            var dateFin = this.state.dateFin
            let monthTwo = '' + (dateFin.getMonth() + 1);
            let dayTwo = '' + dateFin.getDate();
            let yearTwo = dateFin.getFullYear();
            if (monthTwo.length < 2)
                monthTwo = '0' + monthTwo;
            if (dayTwo.length < 2)
                dayTwo = '0' + dayTwo;
            var convertDateFin = [yearTwo, monthTwo, dayTwo].join('-');
        }
        else {
            var convertDateFin = '';
        }
        if ((idEleve == '') && (convertDateDebut == "") && (convertDateFin == "")) {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppFrais.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppFraisSearch.php',
                        type: 'POST',
                        data: {
                            idEleve: idEleve,
                            dateDebut: convertDateDebut,
                            dateFin: convertDateFin,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    perPage(currentRowsPerPage, currentPage) {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedIdEleve != '') {
            var idEleve = this.state.selectedIdEleve.value
        }
        else {
            var idEleve = ''
        }
        // convertir date debut
        if (this.state.dateDebut != '') {
            var dateDebut = this.state.dateDebut
            let monthOne = '' + (dateDebut.getMonth() + 1);
            let dayOne = '' + dateDebut.getDate();
            let yearOne = dateDebut.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            var convertDateDebut = [yearOne, monthOne, dayOne].join('-');
        }
        else {
            var convertDateDebut = '';
        }
        // convertir date fin
        if (this.state.dateFin != '') {
            var dateFin = this.state.dateFin
            let monthTwo = '' + (dateFin.getMonth() + 1);
            let dayTwo = '' + dateFin.getDate();
            let yearTwo = dateFin.getFullYear();
            if (monthTwo.length < 2)
                monthTwo = '0' + monthTwo;
            if (dayTwo.length < 2)
                dayTwo = '0' + dayTwo;
            var convertDateFin = [yearTwo, monthTwo, dayTwo].join('-');
        }
        else {
            var convertDateFin = '';
        }
        if ((idEleve == '') && (convertDateDebut == '') && (convertDateFin == '')) {
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppFrais.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: currentRowsPerPage,
                            page: currentPage,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppFraisSearch.php',
                        type: 'POST',
                        data: {
                            idEleve: idEleve,
                            dateDebut: convertDateDebut,
                            dateFin: convertDateFin,
                            limit: currentRowsPerPage,
                            page: currentPage,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    filterSearch(event) {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppFrais.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    searchEleve = selectedIdEleve => {
        if (selectedIdEleve !== null) {
            this.setState({ selectedIdEleve });
        }
        else {
            this.setState({
                selectedIdEleve: ''
            });
        }
    };

    addDateDebut = d => {
        this.setState({
            dateDebut: d
        });
    };

    addDateFin = d => {
        this.setState({
            dateFin: d
        });
    };

    recherche = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedIdEleve != '') {
            var idEleve = this.state.selectedIdEleve.value
        }
        else {
            var idEleve = ''
        }
        // convertir date debut
        if (this.state.dateDebut != '') {
            var dateDebut = this.state.dateDebut
            let monthOne = '' + (dateDebut.getMonth() + 1);
            let dayOne = '' + dateDebut.getDate();
            let yearOne = dateDebut.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            var convertDateDebut = [yearOne, monthOne, dayOne].join('-');
        }
        else {
            var convertDateDebut = '';
        }
        // convertir date fin
        if (this.state.dateFin != '') {
            var dateFin = this.state.dateFin
            let monthTwo = '' + (dateFin.getMonth() + 1);
            let dayTwo = '' + dateFin.getDate();
            let yearTwo = dateFin.getFullYear();
            if (monthTwo.length < 2)
                monthTwo = '0' + monthTwo;
            if (dayTwo.length < 2)
                dayTwo = '0' + dayTwo;
            var convertDateFin = [yearTwo, monthTwo, dayTwo].join('-');
        }
        else {
            var convertDateFin = '';
        }
        this.setState({
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppFraisSearch.php',
                    type: 'POST',
                    data: {
                        idEleve: idEleve,
                        dateDebut: convertDateDebut,
                        dateFin: convertDateFin,
                        anneeScolaire: anneeScolaire,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    afficherTout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html.data,
                    pending: false
                });
            }
        });
    }

    impr = () => {
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Reglement formation intiale</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <h4 style={{ paddingLeft: '10px' }}>Critères de recherche</h4>
                                    <Fragment>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Apprenant</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchEleve}
                                                    options={this.state.listEleve}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Date debut</label>
                                                <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                    selected={this.state.dateDebut}
                                                    onChange={this.addDateDebut}
                                                    dateFormat="dd/MM/yyy"
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Date fin</label>
                                                <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                    selected={this.state.dateFin}
                                                    onChange={this.addDateFin}
                                                    dateFormat="dd/MM/yyy"
                                                />
                                            </div>
                                        </div>
                                    </Fragment>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.impr}>Afficher tout</button>
                                    </div>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modalFrais} toggle={this.toggleFrais} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggleFrais}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Reglement frais</p></div></div></ModalHeader>
                    <ModalBody>
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th style={{ textAlign: 'center' }} scope="col">Frais</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant payer</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Mode</th>
                                    {this.state.idRoleUser == 1 ? (
                                        <Fragment>
                                            <th style={{ textAlign: 'center' }} scope="col">Modif</th>
                                            <th style={{ textAlign: 'center' }} scope="col">Supp</th>
                                        </Fragment>
                                    ) : (null)}
                                </tr>
                            </thead>
                            {this.state.reglFraisFormat.map((el, index) =>
                                <tbody>
                                    <tr>
                                        <td style={{ textAlign: 'center' }}><p>{el.frais}</p></td>
                                        <td style={{ textAlign: 'center' }}><p>{el.mntPayer}</p></td>
                                        <td style={{ textAlign: 'center' }}><p></p></td>
                                        {this.state.idRoleUser == 1 ? (
                                            <Fragment>
                                                <td style={{ textAlign: 'center' }}><Link to={{
                                                    pathname: '/MiseAJourReglFor',
                                                    state: { idRegFor: el.idRegFor }
                                                }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link></td>
                                                <td style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleSupFrais(el.idLigneRegFor)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                            </Fragment>) : (null)}
                                    </tr>
                                </tbody>
                            )}
                        </table>
                        <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                            <div><button onClick={() => this.toggleFrais()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white', width: '40px ' }}><i style={{ color: 'white' }} className="fas fa-sign-out-alt"></i></button></div>
                        </div>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}
export default ReglApprenantFrais;
