import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button } from 'reactstrap';

class ModifMatiere extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idMatiere: '',
            matiere: [],
            ListTypeEns: [],
            selectedTypeEns: ''
        }
    }

    componentDidMount() {
        let idMatiere = this.props.location.state.idMatiere;
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetMatiereById.php?id=' + idMatiere).then(res => {
            this.setState({
                idMatiere: res.data[0].idMatiere,
                matiere: res.data[0]
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectTypeEns.php').then(res => {
            console.log(res)
            this.setState({
                ListTypeEns: res.data
            })
        })
    }

    updCode = (event, indexCode) => {
        let code = event.target.value;
        this.setState({
            matiere: this.state.matiere.map((el, id) => (id === indexCode ? Object.assign(el, { code: code }) : el))
        });
    }

    updNomMatiere = (event, indexNomMatiere) => {
        let nomMatiere = event.target.value;
        this.setState({
            matiere: this.state.matiere.map((el, id) => (id === indexNomMatiere ? Object.assign(el, { nomMatiere: nomMatiere }) : el))
        });
    }

    updAbrev = (event, indexAbrev) => {
        let abrev = event.target.value;
        this.setState({
            matiere: this.state.matiere.map((el, id) => (id === indexAbrev ? Object.assign(el, { abrev: abrev }) : el))
        });
    }

    updTypeEns = (selectedTypeEns, indexTypeEns) => {
        let typeEns = selectedTypeEns.value;
        this.setState({
            matiere: this.state.matiere.map((el, id) => (id === indexTypeEns ? Object.assign({}, { typeEns: typeEns }) : el))
        });
    }

    modif = () => {
        let year = new Date().getFullYear();
        console.log(this.state.matieres)
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdMatiere.php?id=' + this.state.idMatiere,
            type: 'POST',
            data: {
                matiere: this.state.matiere,
                year: year
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Matieres');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification matiére</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Code</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Abrev</th>
                                                <th style={{ textAlign: 'center' }} scope="col">TypeEns</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.matiere.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td><input type="text" value={el.code} onChange={(e) => this.updCode(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td><input type="text" value={el.nomMatiere} onChange={(e) => this.updNomMatiere(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td><input type="text" value={el.abrev} onChange={(e) => this.updAbrev(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td style={{ width: '20%' }}><Select
                                                        value={el.typeEns}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.updTypeEns(e, index)}
                                                        options={this.state.ListTypeEns}
                                                    />  </td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '33%' }} className='buttonSupprimer' onClick={() => this.toggleLignMatiere(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLignMatiere} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifMatiere);
