import React, { Component } from 'react';
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import jQuery from 'jquery'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user_name: '',
            user_passe: '',
        }
        this.changeusername = this.changeusername.bind(this)
        this.changeuserpasse = this.changeuserpasse.bind(this)
        this.connect = this.connect.bind(this)
    }

    changeusername(event) {
        this.setState({
            user_name: event.target.value
        });
    }

    changeuserpasse(event) {
        this.setState({
            user_passe: event.target.value
        });
    }

    connect() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/ConnectUtilisateur.php',
            type: 'POST',
            data: {
                user_nom: this.state.user_name,
                mot_de_passe: this.state.user_passe
            },
            dataType: 'json',
            success: function (code_html, statut) {
                if (code_html.connection === "OK") {
                    toast.success('✅ Bienvenue dans AGORA !', { containerId: 'A' });
                    document.location = "/FamilleArticles"
                }
                else if (code_html.connection === "KO") {
                    toast.error('⛔ Veuillez vérifier votre login et mot de passe !', { containerId: 'A' });
                }
            }
        });
    }

    render() {
        return (
            <section id="wrapper" className="login-register">
                <div style={{ background: '#fff', position: 'absolute', width: '420px', marginLeft: '35%', marginTop: '5%', height: '53px' }}>
                    <div className="white-box">
                        <form className="form-horizontal m-t-20" action="index.html">
                            <div className="form-group ">
                                <div style={{ display: 'flex', justifyContent: 'center' }} className="col-xs-12">
                                    <img className="img-circle" width='60px' height='60px' src="images/lagora.jpg" alt="slim" />
                                </div>
                            </div>
                            <div className="form-group ">
                                <div className="col-xs-12">
                                    <input style={{ borderRadius: '5px', borderColor: '#C10706' }} className="form-control" type="text" required placeholder="Login" onChange={this.changeusername.bind(this)} />
                                </div>
                            </div>
                            <div className="form-group">
                                <div className="col-xs-12">
                                    <input style={{ borderRadius: '5px', borderColor: '#C10706' }} className="form-control" type="password" required placeholder="Password" onChange={this.changeuserpasse.bind(this)} />
                                </div>
                            </div>
                            <div className="form-group text-center m-t-40">
                                <div className="col-xs-12">
                                    <button onClick={this.connect.bind(this)} style={{ width: '40%', backgroundColor: '#C10706', borderColor: '#C10706', fontSize: '16px', borderRadius: '5px' }} className="btn btn-success btn-lg btn-block text-uppercase waves-effect waves-light" type="button">Connexion</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
            </section>
        );
    }
}

export default Login;
