import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Switch from "react-switch";

class ModifTypePayEleve extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idTypePay: '',
            typePay: '',
            checked: false,
            sousTypePay: [],
            choix: [],
            choixChecked: [],
            modal: false,
            indexLignASupprimer: '',
            idSousTypePayASupprimer: '',
            tabIdSousTypePayDelete: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            let idTypePay = this.props.location.state.idTypePay;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetTypePayEleveById.php?id=' + idTypePay).then(res => {
                console.log(res)
                this.setState({
                    idTypePay: res.data[0].id,
                    typePay: res.data[0].type,
                    sousTypePay: res.data[1],
                    choixChecked: res.data[2]
                }, () => {
                    if (this.state.sousTypePay.length > 0) {
                        this.setState({
                            checked: true
                        });
                    }
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetChoixPay.php').then(res => {
                console.log(res)
                this.setState({
                    choix: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    updTypePay = (event) => {
        this.setState({
            typePay: event.target.value
        });
    }

    handleChange = (checked) => {
        this.setState({ checked });
        console.log(this.state.checked)
    }

    additionLign = () => {
        let element = { idSousTypePay: -1, sousType: '' }
        this.setState({
            sousTypePay: [...this.state.sousTypePay, element]
        })
    }

    updSousTypePay = (event, indexSousTypePay) => {
        let sousType = event.target.value;
        this.setState({
            sousTypePay: this.state.sousTypePay.map((el, id) => (id === indexSousTypePay ? Object.assign(el, { sousType: sousType }) : el))
        });
    }

    toggleLign = (idSousTypePay, indexLign) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idSousTypePayASupprimer: idSousTypePay,
            indexLignASupprimer: indexLign
        }));
    }

    deleteLign = () => {
        let idSousTypePayASupprimer = this.state.idSousTypePayASupprimer
        this.setState({
            tabIdSousTypePayDelete: [...this.state.tabIdSousTypePayDelete, idSousTypePayASupprimer]
        });
        this.state.sousTypePay.splice(this.state.indexLignASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLign = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    updChoix = (e, el, index) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.id
            this.setState({
                choixChecked: [...this.state.choixChecked, element]
            });
        }
        else {
            let element = el.id
            let indice = this.state.choixChecked.indexOf(element)
            this.state.choixChecked.splice(indice, 1)
        }
    }

    modif = () => {
        console.log(this.state.choixChecked)
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdTypePayEleve.php?id=' + this.state.idTypePay,
            type: 'POST',
            data: {
                typePay: this.state.typePay,
                sousTypePay: this.state.sousTypePay,
                tabIdSousTypePay: this.state.tabIdSousTypePayDelete,
                choixChecked: this.state.choixChecked,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/TypesPaiementEleve');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification type paiement</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/TypesPaiementEleve" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Type paiement</label>
                                            <input type="text" className="form-control" value={this.state.typePay} onChange={this.updTypePay} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div style={{ marginTop: '30px', display: 'flex', alignItems: 'center' }}>
                                        <span style={{ marginLeft: '15px', marginRight: '10px' }}>Sous type paiement</span>
                                        <Switch offColor='#FF0000' onChange={this.handleChange} checked={this.state.checked} />
                                    </div>
                                    {this.state.checked == true ?
                                        (<Fragment>
                                            <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th style={{ textAlign: 'center' }} scope="col">Sous type paiement</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                                    </tr>
                                                </thead>
                                                {this.state.sousTypePay.map((el, index) =>
                                                    <tbody>
                                                        <tr>
                                                            <td className="col-md-10" style={{ textAlign: 'center' }}><input type="text" value={el.sousType} onChange={(e) => this.updSousTypePay(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleLign(el.idSousTypePay, index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                        </tr>
                                                    </tbody>
                                                )}
                                            </table>
                                            <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px', marginBottom: '10px' }}>
                                                <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i className="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                            </div>
                                        </Fragment>) : (null)}
                                </div>
                                <div className="row">
                                    {this.state.checked == false ?
                                        (<div className="col-md-7">
                                            <label>Choix</label>
                                            <div className="form-group">
                                                {this.state.choix.map((el, index) =>
                                                    <label>
                                                        <Checkbox
                                                            style={{ paddingRight: '5px' }}
                                                            onChange={(e) => this.updChoix(e, el, index)}
                                                            disabled={this.state.disabled}
                                                        />
                                                        {el.choixPay}
                                                    </label>
                                                )}
                                            </div>
                                        </div>) : (null)}
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteLign} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteLign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer cette ligne ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteLign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteLign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifTypePayEleve);
