import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import axios from 'axios'

class Clients extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomClient: '',
            adresse: '',
            numeroTelephone: '',
            email: ''
        }
    }

    componentDidMount() {
        let id_client = this.props.location.state.id_client;
        axios.get(process.env.REACT_APP_API_URL + 'Back_vds/GetClientById.php?id=' + id_client).then(res => {
            console.log(res)
            this.setState({ nomClient: res.data.nomClient })
            this.setState({ adresseClient: res.data.adresse })
            this.setState({ numeroTelephone: res.data.numeroTelephone })
            this.setState({ email: res.data.email })
        })
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Informations Client</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Nom client</label>
                                <input type="text" disabled Value={this.state.nomClient} class="form-control" />
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Adresse</label>
                                <input type="text" disabled Value={this.state.adresse} class="form-control" />
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Numéro de Telephone</label>
                                <input type="text" disabled Value={this.state.numeroTelephone} class="form-control" />
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Email</label>
                                <input type="text" disabled Value={this.state.email} class="form-control" />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Clients;
