import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';

class PaiementEtudGroup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            formation: [],
            selectedFormation: '',
            groupe: [],
            selectedGroupe: '',
            typeImp: [{ label: 'Global', value: '1' }, { label: "Aujourdhui", value: '2' }],
            selectedTypeImpr: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
        }
        else {
            document.location = '/'
        }
    }

    addImp = selectedTypeImpr => {
        if (selectedTypeImpr != null) {
            this.setState({
                selectedTypeImpr: selectedTypeImpr
            }, () => {
                if (this.state.selectedTypeImpr.value == 1) {
                    var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
                    var anneeScolaire = userPermissions[0].anneeScolaire
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFormations.php',
                        type: 'POST',
                        data: {
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            this.setState({
                                formation: code_html
                            });
                        }
                    });
                }
                else if (this.state.selectedTypeImpr.value == 2) {
                    var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
                    var anneeScolaire = userPermissions[0].anneeScolaire
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupePayToday.php',
                        type: 'POST',
                        data: {
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            this.setState({
                                groupe: code_html
                            });
                        }
                    });
                }
            });
        }
        else {
            this.setState({
                selectedTypeImpr: ''
            });
        }
    }

    addFormation = selectedFormation => {
        if (selectedFormation != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            this.setState({ selectedFormation: selectedFormation }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupesSelonForm.php',
                    type: 'POST',
                    data: {
                        formationId: selectedFormation.value,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            groupe: code_html
                        });
                    }
                });
            });
        }
        else {
            this.setState({ selectedFormation: '' })
        }
    };

    addGroupe = selectedGroupe => {
        if (selectedGroupe != null) {
            this.setState({ selectedGroupe: selectedGroupe });
        }
        else {
            this.setState({ selectedGroupe: '' });
        }
    };

    imprToday = () => {
        if (this.state.selectedGroupe != '') {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            // Date aujourdhui
            let dateToday = new Date()
            let month = '' + (dateToday.getMonth() + 1);
            let day = '' + dateToday.getDate();
            let year = dateToday.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var today = [year, month, day].join('-');
            //
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ImprPayGroupe.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire,
                    today: today,
                    groupeId: this.state.selectedGroupe.value
                },
                dataType: 'json',
                success: function (code_html, statut) {
                    var server_path = code_html.server_path;
                    var file_path = code_html.file_path
                    var path = server_path + file_path;
                    window.open(path, "_blank")
                }
            });
        }
        else {
            toast.error('⛔ Veuillez selectionner le groupe correspondant !!', { containerId: 'A' });
        }
    }

    impr = () => {
        if (this.state.selectedGroupe != '') {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ImprPayGroupe.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire,
                    today: '',
                    groupeId: this.state.selectedGroupe.value
                },
                dataType: 'json',
                success: function (code_html, statut) {
                    var server_path = code_html.server_path;
                    var file_path = code_html.file_path
                    var path = server_path + file_path;
                    window.open(path, "_blank")
                }
            });
        }
        else {
            toast.error('⛔ Veuillez selectionner le groupe correspondant !!', { containerId: 'A' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Paiments groupes</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Type impression</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.addImp(e)}
                                                options={this.state.typeImp}
                                            />
                                        </div>
                                    </div>
                                    {this.state.selectedTypeImpr.value == 1 ? (
                                        <Fragment>
                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label>Formations</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addFormation(e)}
                                                        options={this.state.formation}
                                                    />
                                                </div>
                                            </div>
                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label>Groupes</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addGroupe(e)}
                                                        options={this.state.groupe}
                                                    />
                                                </div>
                                            </div>
                                        </Fragment>
                                    ) : (null)}
                                    {this.state.selectedTypeImpr.value == 2 ? (
                                        <Fragment>
                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label>Groupes</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addGroupe(e)}
                                                        options={this.state.groupe}
                                                    />
                                                </div>
                                            </div>
                                        </Fragment>
                                    ) : (null)}
                                </div>
                                <div className="row">
                                    {this.state.selectedTypeImpr.value == 1 ? (
                                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                                            <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.impr}><i style={{ color: 'white', paddingRight: '5px' }} class="fas fa-print"></i>Impression global</button>
                                        </div>
                                    ) : (null)}
                                    {this.state.selectedTypeImpr.value == 2 ? (
                                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                                            <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.impr}><i style={{ color: 'white', paddingRight: '5px' }} class="fas fa-print"></i>Impression aujourdhui</button>
                                        </div>
                                    ) : (null)}
                                </div>
                            </div>
                            <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default withRouter(PaiementEtudGroup);
